css:
@-moz-document url-prefix(https://thehackernews.com) {

	.main.clear {
		background: #333 !important;
	}

	.google.cf {
		background: #333 !important;
	}

	.menu.cf {
		background: #2b2b2b !important;
	}

	.m-menu-ul a, .menu-ul a {
		color: #aaa !important;
	}

	.home-title {
		color: #b4b4b4 !important;
	}

	.home-desc {
		color: #aaa !important;
	}

	.widget.PopularPosts {
		display: none !important;
	}

	.below-post, .deals {
		background: #2f2f2f !important;
	}

	.footer.cf {
		background : #222 !important;
	}

	.sponsor {
		display: none !important;
	}

	.articlebody {
		color: #ccc !important;
	}

	.static_title a, .story-title a {
		color: #aaa !important;
	}
}
