css:
@-moz-document url-prefix("http://localhost:9000") {
body,
body .modal-content,
body .panel-body {
    background: #14161a;
    color: #DDD;
}

body .panel-body {
    background: #171a1f !important;
}

body a, 
body a:hover,
body .blocklist-item--selected,
body .blocklist-item:hover {
    color: #DDD;
    text-decoration: none;
}

body .blocklist-item .fa.blue-icon,
body .blocklist-item .fab.blue-icon {
    color: #CD3131;
}

body #sidebar-wrapper,
body .sidebar-header,
body .row.header,
body .widget,
body .datatable .toolBar {
    background: #171a1f;
    color: #DDD;
}

body .widget,
body .datatable .searchBar,
body .table>thead>tr>th,
body .table>tfoot>tr>td,
body .table>tfoot>tr>th,
body .table>thead>tr>td,
body .table>thead>tr>th {
    border: none;
}

body .table>tbody>tr>td,
body .table>tbody>tr>th,
body .datatable .footer {
    border-top: 1px solid #14161a;
}

body ul.sidebar .sidebar-list a.active,
body ul.sidebar .sidebar-list a:hover,
body ul.sidebar .sidebar-list .sidebar-sublist a.active {
    background: #CD3131;
    color: #DDD;
    border-left: 3px solid #DDD;
}

body .widget .widget-body table thead,
body .datatable .footer,
body .widget .widget-body table thead a,
body .table-hover>tbody>tr:hover,
body .table-hover>tbody>tr:hover a,
body .table>tbody>tr.active>td,
body .table>tbody>tr.active>th,
body .table>tbody>tr>td.active,
body .table>tbody>tr>th.active,
body .table>tfoot>tr.active>td,
body .table>tfoot>tr.active>th,
body .table>tfoot>tr>td.active,
body .table>tfoot>tr>th.active,
body .table>thead>tr.active>td,
body .table>thead>tr.active>th,
body .table>thead>tr>td.active,
body .table>thead>tr>th.active,
body .table-hover>tbody>tr.active:hover>td,
body .table-hover>tbody>tr.active:hover>th,
body .table-hover>tbody>tr:hover>.active,
body .table-hover>tbody>tr>td.active:hover,
body .table-hover>tbody>tr>th.active:hover,
body .datatable .searchBar {
    background: #14161a;
}

body .nav-tabs>li.active>a,
body .nav-tabs>li.active>a:focus,
body .nav-tabs>li.active>a:hover,
body .widget .widget-header,
body .table .table,
body .dropdown-menu,
body .tableMenu {
    background: #14161a;
    color: #DDD;
    border: none;
}

body .dropdown-menu,
body .tableMenu {
    background: #171a1f;
    color: #DDD;
    border: none;
    border-radius: 0;
}

body .nav-tabs,
body .form-section-title {
    border-bottom: 1px solid #14161a;
}

body .form-control,
input:-webkit-autofill,
input:-webkit-autofill:hover, 
input:-webkit-autofill:focus,
textarea:-webkit-autofill,
textarea:-webkit-autofill:hover,
textarea:-webkit-autofill:focus,
select:-webkit-autofill,
select:-webkit-autofill:hover,
select:-webkit-autofill:focus {
    border-radius: 0;
    background-color: #14161a;
    border:none;
    color: #DDD;
    box-shadow: inset 1px 1px 3px #222;
    -webkit-text-fill-color: #DDD !important;
    -webkit-box-shadow: 0 0 0px 1000px #14161a inset !important;
}

body .btn-group-sm>.btn,
body .btn-sm,
body .btn {
    border-radius: 0;
    border: none;
}

body .btn-default {
    background: #14161a;
    color: #DDD;
    border: none;
    border-radius: 0;
}

body .btn-group .btn+.btn,
body .btn-group .btn+.btn-group,
body .btn-group .btn-group+.btn,
body .btn-group .btn-group+.btn-group {
    margin-left: 0;
}

body .form-control:focus {
    border: none;
    box-shadow: 0 0 3px #CD3131;
}

body code,
body kbd {
    background: #14161a;
    color: #CD3131;
}

body .table-bordered,
body .table-bordered>tbody>tr>td,
body .table-bordered>tbody>tr>th,
body .table-bordered>tfoot>tr>td,
body .table-bordered>tfoot>tr>th,
body .table-bordered>thead>tr>td,
body .table-bordered>thead>tr>th {
    border: 1px solid #171a1f;
}

body .pagination>.disabled>a,
body .pagination>.disabled>a:focus,
body .pagination>.disabled>a:hover,
body .pagination>.disabled>span,
body .pagination>.disabled>span:focus,
body .pagination>.disabled>span:hover,
body .pagination>li>a,
body .pagination>li>span {
    border-radius: 0;
    background: #14161a;
    border:none;
    color: #DDD;
    padding:0.6em 1em;
}

body .pagination>li>a:focus,
body .pagination>li>a:hover,
body .pagination>li>span:focus,
body .pagination>li>span:hover,
body .md-checkbox input[type=checkbox]:checked+label:before {
    background: #CD3131;
    color: #DDD;
}

body .blocklist-item {
    border: 1px solid #14161a;
    box-shadow: none;
    background: #14161a;
}
body .blocklist-item:hover {
    border: 1px solid #CD3131;
    box-shadow: 0 3px 10px -2px rgba(205,49,49,0.3);
    background: #14161a;
}

body .searchBar {
    border-top: 1px solid #14161a !important;
}

body .bootbox-form .checkbox :checked~i,
body .switch :checked+i {
    color: #CD3131;
}

body .bootbox-form .checkbox :checked~i,
body .switch :checked+i {
    box-shadow: inset 0 0 1px rgba(205,49,49,.5), inset 0 0 40px #CD3131;
}

body .md-checkbox label:before {
    background: #14161a;
    border:1px solid #474747;
    box-shadow: inset 1px 1px 3px #222;
}
body .md-checkbox input[type=checkbox]:disabled+label:before {
    background: rgba(55,55,55,0.1);
    border:1px solid rgba(71,71,71,0.1);
    box-shadow: inset 1px 1px 3px rgba(20,20,20,0.2);
}

body ul.sidebar .sidebar-list a,
body ul.sidebar .sidebar-list .sidebar-sublist a {
    color: #DDD;
}

body ul.sidebar .sidebar-title.endpoint-name {
    color: #CD3131;
    border-bottom: 1px solid #CD3131;
    text-align: left;
    padding-left:1.6em;
}

body ul.sidebar .sidebar-title.endpoint-name,
body ul.sidebar .sidebar-title {
    color: #CD3131;
    border-bottom: 1px solid #CD3131;
}

body .sidebar-footer-content {
    opacity: 0.2;
}

body .panel-body,
body .panel {
    background: #171a1f;
    border: none;    
}

body .input-group-addon {
    background: #CD3131;
    color: #DDD;
    border: none;
    border-radius: 0;
}

body .modal-content {
    border-radius: 0;
}

body .modal-content .modal-header {
    background-color: #CD3131;
    color: #DDD;
    border-bottom: 1px solid #CD3131;
    padding: 10px;
}

body .modal-content .modal-footer {
    color: #CD3131;
    border-top: 1px solid #171a1f;
    padding: 10px;
}

body .modal-header .close {
    margin-top: 4px;
    margin-right:5px;
}

body .boxselector_wrapper input[type=radio]:checked+label {
    background: #CD3131;
    border-color: #CD3131;
    box-shadow: 0 3px 10px -2px rgba(205,49,49,0.3);
}

body .boxselector_wrapper input[type=radio]:checked+label:after {
    color: #CD3131;
    border-color: #CD3131;
}

body .boxselector_wrapper label {
    height:70px;
    padding-top: 1.7em;
    background-color: #14161a;
    box-shadow: 0 3px 10px -2px rgba(205,49,49,0.3);
}

body .widget .widget-icon,
body .widget .widget-icon.blue {
    background-color: #CD3131 !important;
}

body json-tree {
    color: #ddd;
}

body json-tree .key {
    color: #cd3131;
}

::-webkit-scrollbar {
    background-color: #171a1f !important;
}
::-webkit-scrollbar-thumb {
    background-color: #CD3131 !important;
}
}
