css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {
body {
	height: 98%!important;
	width: 100%!important;
	position: fixed!important;
	top: 0px!important;
	left: 0px!important;
	margin: 0!important;
	padding: 0!important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 8pt!important;
	overflow: hidden!important;
		background: #76827B !important;
}
#deviantART, .fullscreen, .hi-res, .ready, #body, html {
	height: 101%!important;
	width: 100%!important;
	position: fixed!important;
	top: 0px!important;
	left: 0px!important;
	background: #76827B !important;
	z-index: 2!important;
}
#head {
	position: absolute!important;
			top:-71px!important;
	left: 54px!important;
	height: 85px!important;
	background: url(http://s.deviantart.com/styles/minimal/minish/bg-tttop-all.gif) top left repeat-x!important;
	background-position: bottom left!important;
	width: 100%!important;
	z-index: 2!important;
}
#head h1 {
display:block!important;
	position:absolute!important;
    left:-60px!important;
    top:0!important;
	font-size:2pt!important;
	text-indent:-500px!important;
	overflow:hidden!important;
	height:100px!important;
  z-index:36!important;
    width:100%!important;
    overflow:hidden!important;
	background: url(http://s.deviantart.com/styles/minimal/minish/bg-tttop-all.gif) no-repeat!important;
	
}
#head h1 a {

height:30px;
width:200px;
    background-image:none !important;
}
#deviantART-loves-you {
display:block!important;
		position:absolute!important;
		text-indent:-99999px;
		top:-71px!important;
		right:0px!important;
    height:100px!important;
    width:60px!important;
    z-index:66!important;
	background:#76827B url('http://s.deviantart.com/styles/minimal/minish/bg-tttop-all.gif') no-repeat !important;
	background-position:right -110px!important;
}
#head table {display:none!important; position:absolute; top:0px; left:-9999px;}
#bar-deviant {
	position:absolute!important;
  top:0!important;
  left: -30px!important;
  height:100px!important;
  width:100%!important;
	z-index:100!important; /* might as well */
	background: /*url(http://s.deviantart.com/styles/minimal/minish/bg-tttop-all.gif) no-repeat right -110px*/ none!important;
	border: none!important;
	color: #ffffff!important;
	text-indent:-300px!important;
	padding-top:9px!important;
	/*margin-right:10px!important;*/
	margin:0!important;
}
#bar-deviant-ctrl1, #bar-deviant-ctrl2, #bar-deviant-ctrl3, #bar-main-ctrl1, #bar-main-ctrl2, #bar-main-ctrl3 {background:none!important;}
#bar-deviant span {
	color: #374341!important;
}
.ext {
	z-index:102!important; /* might as well */
	position: absolute!important;
	top:62px!important;
	left: 0px!important;
	width:36px!important;
	height:29px!important;
	margin:0px!important;
	display:block!important;
	font-size: 0!important;
	border: 0px!important;
	-moz-outline:none !important;
  background: url('http://s.deviantart.com/styles/minimal/minish/icons-tttop.gif') no-repeat!important;
  background-position:0 -64px!important;
  cursor:default!important;
}


.ext:hover {
	z-index: 19!important;
	width:36px!important;
	height:29px!important;
	margin: 0!important;
  background:url('http://s.deviantart.com/styles/minimal/minish/icons-tttop.gif') no-repeat!important;
    background-position:0 -122px!important;
}
.ext:hover a {
	visibility:visible!important;
	z-index:25!important;
	padding:2px 4px!important;
	overflow:hidden!important;
	height: 20px!important;
width:240px!important;
	text-align:left!important;
text-indent:10px!important;
	display: block!important;
	font-size: 8.25pt!important;
		vertical-align: middle!important;
}
.ext:hover a:first-child {
	margin-top:27px!important;
	z-index: 120!important;
	background-color:#212A2A!important;
	border-top: 3px solid #AFC81C!important;
}
.ext:hover a:last-child {
	-moz-border-radius: 0px 0px 5px 5px!important;
}
.ext:hover a, .ext:hover a:visited{
    background-color:#212A2A!important;
    color:#CAC9C9!important;
}
.ext:hover a:hover {
    background-color:#AFC81C!important;
    color:#586608!important;
    text-decoration:none!important;
}
.ext:hover a:active {
    background:#AFC81C!important;
    color:#586608!important;
}
/* <iconify> */
.ext:hover a[style="cursor: pointer;"],
.ext:hover a#chatroomsettingslink,
.ext:hover a[href="http://chat.deviantart.com"],
.ext:hover a[href="http://comments.deviantart.com/emoticons"],
.ext:hover a[href="http://help.deviantart.com/chat/generalchatquestions/"] {
  background-image: url(http://img166.imageshack.us/img166/7725/deviatetx0.gif) !important;
background-repeat:no-repeat!important;
padding-left:20px!important;
}
.ext a[style="cursor: pointer;"] {background-position:2px 0px;}
.ext a#chatroomsettingslink {background-position:2px -25px;}
.ext a[href="http://chat.deviantart.com"] {background-position:2px -50px;}
.ext a[href="http://comments.deviantart.com/emoticons"] {background-position:2px -75px;}
.ext a[href="http://help.deviantart.com/chat/generalchatquestions/"] {background-position:2px -100px; border-bottom:10px solid #212A2A!important;}

/* </iconify>  (blame dio =P, but it does look good) */

.main {
	position: relative !important;
	top: 0px!important;
	background: none !important;
	border-color: #D3E0DC !important;
}
/* Reposition the main chat area */
#window{
	position: fixed !important;
	top: 53px!important;
	height: 84%!important;
	width: 93.5%!important;	margin-left: 19px!important;
margin-right: 19px!important;
	border: none !important;
	background: #EAF2EE !important;
	padding: 5px!important;
	-moz-border-radius: 5px!important;
}
/* <tabbar retheme> */
.tabbar {
	position: absolute !important;
	top: -63px!important;
	left: 40px!important;
	height: 25px!important;
	z-index: 12!important;
}
.tabbar strong {
	position: relative !important;
	top: -3px!important;
	height: 14px!important;
	border: none !important;
	color: #222!important;
	font-size: 8.25pt!important;
	vertical-align: middle!important;
	background: #afc81c !important;
border-top:1px solid #222!important;
	z-index: 5!important;
	margin:0px 3px!important;
	-moz-border-radius: 0px 0px 5px 5px!important;
}
.tabbar a {
	position: relative !important;
	top: -3px!important;
	height: 14px!important;
	border: none !important;
	background: #2C3737 !important;
border-top:1px solid #000!important;
	color: #C8D8C8;
	font-weight: normal;
	z-index: 5!important;
	margin:0px 3px!important;
	-moz-border-radius: 0px 0px 5px 5px!important;
}
.tabbar a, .tabbar strong {
	padding-left: 4px!important;
	padding-right: 4px!important;
}
.tabbar a:focus, .tabbar a:active {
	color: #586608 !important;
	background: #485856 !important;
}
.tabbar a:hover {
	border-bottom: 3px solid #AFC81C!important;
	color: #eff4f0 !important;
	background: #2c3737 !important;
}
/* </tabbar retheme> */
.content {
position:relative!important;
top:-10px!important;
left:5px!important;
margin:0px -20px!important;
	-moz-border-radius: 0px!important;
	padding: 5px!important;
	height: 96%!important;
	background: #EAF2EE!important;
}
/* <color fixing> */
.damnc-serverlog, .damncr-topbar, .damncr-close, .damncr-title, .damncrc-chat-outer, .damncrc-chat-window {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.damn-lo-row-top, .damn-lo-row-flex, .alt1-border {
	border-color: #EAF2EE!important;
	background: #EAF2EE!important;
}
.alt0 {
	background-color: #EAF2EE!important;
}
.alt0-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-top-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-bottom-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt0-bottom-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-left-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
div.damncrc-chat-window alt2 {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
* html .collapsed .exc, * html .expanded .exc {
	border-color: #EAF2EE!important;
}
#content, #content-core, #content-ctrl1, #content-ctrl2, #content-ctrl3, #content-ctrl4, #content-ctrl5, #content-ctrl6, #content-ctrl7, #content-ctrl8, .output-primary, #dAmnPluginArea, .damnc-serverlog-outer, .dAmn-Chatbox, .damn, .trailing, .section-block, #window {
	border-color: #EAF2EE!important;
	background: #EAF2EE!important;
}
.damncr-members, .sidebar, .damncr-leftbar, .damncr-iconbar, .damncrc-input, .damncr-topbar, .damncr, .dAmn-Chatspace, .section, .damncrc-iconbar{
	background: #EAF2EE!important;
	border-color: #EAF2EE !important;
}

/* </color fixing> */
/* This Removes the images in the title. */
.damncr-title img {
	display: none !important;
	text-indent: -999px!important;
}
/* Change the topic */
.damncrc-topic {
	position: relative!important;
	top: 0px!important;
	padding: 2px 4px!important;
	-moz-border-radius: 10px 10px 0px 0px!important;
	background: #D3E0DC!important;
	border: 1px solid #D3E0DC!important;
}
/* The Mmeber List (to the right) */
.damncr-member ul {
	list-style: none !important;
}
.damncr-member {
	min-width: 100px!important;
}
.damncr-members .simple b {
	border-bottom: 1px solid #95a59b !important;
}
.damncr-members dt {
	text-align: center!important;
	padding: 2px 4px!important;
}
/* This Removes the Close button, and the text saying which room your in. */
.damncr-close, .damncrc-icon-roomname {
	display: none !important;
}
.userinfo-outer .damncr-close {
	display: block!important;
}
/* The log area */
.damncrc-chat-inner {
	background: #fff!important;
	border: 1px solid #000!important;
	height: auto!important;
}
/* <messages> */
div.msg part {
	color: #485856 !important;
text-align:left!important;
}
.damncr .self-hl  .from SPAN {
	background: #f9f9f9!important;
font-style:italic!important;
text-align:left!important;
}
.damncr .other-hl .text SPAN {
background:none!important;
font-weight:normal!important;
text-align:left!important;
}
.damncr .other-hl .from SPAN {
	background: #D3E0DC!important;
-moz-border-radius: 5px!important;
padding:2px!important;
text-align:left!important;
}
.damncr .join, .damncr .part {
	color: #76827b!important;
text-align:left!important;
}
span .text {text-align:left!important;}
/* </messages> */
form {
	position: relative!important;
	top: 2px!important;
	background: #EAF2EE !important;
	border-color: #EAF2EE !important;
	margin: 0px 75px -1px 5px!important;
}
textarea, textarea:focus {
	height: 40px!important;
	-moz-border-radius: 5px!important;
	background: #fff!important;
	border-color: #838582!important;
}
input {
	-moz-border-radius: 5px!important;
	padding: 2px 4px!important;
	height: 24px!important;
	margin-bottom: -1px!important;
vertical-align:middle!important;
}
input.button {
	position: relative!important;
	top: 20px!important;
	margin-right: 5px!important;
	width: 65px!important;
	background-color: #374341 !important;
	border: none !important;
	font-weight: bold !important;
	color: #8a9b91 !important;
}
/* End The log area */
/* <userinfo hoverbox> */
.damncri-member .b {
	background: #D3E0DC!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px 0px 5px 0px!important;
}
.damncri-member .bodyarea UL {
	list-style-type: none!important;
	margin: 0!important;
	padding-left: 1.2em!important;
	border-color: #EAF2EE!important;
}
div.aside-left {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}
div.damncr-infotr img {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
div.damncr-infotr, div.damncr-infotr alt0 {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}
div.bodyarea {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}
/* </userinfo hoverbox> */
/* Something Random*/
.damncr .userinfo-outer, .damncr .userinfo-inner, .damncr .userinfo, .collapsed *, .expanded *, .collapsed * {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
* html .collapsed .exc, * html .expanded .exc {
	border-color: #76827b!important;
}
.damncrc-alertbox {padding:2px 4px!important;}
.damncrc-alertbox, .damncrc-error {background:#D3E0DC!important; border-color: #EAF2EE!important;}
.damncrc-chat *, .damncrc-chat-inner *, .damncrc-chat-window{ overflow:auto!important;text-align:left!important;}
div .damncrc-chat-window alt2,
span .damncrc-chat-outer alt2,
.damncrc-chat-inner {width:99%; overflow-x:hidden!important;}
damn-lo-row-flex, alt2, alt0-bottom-border, alt1-top-border {background:#EAF2EE!important;}

div .dAmn-Chatbox section * ,
div #window .damn trailing section-block,
div #damn-chatspace .damn-chatspace,
div .damn-lo-row-flex alt1 alt1-border,
div .damncr,
div .damn-lo-row-flex alt0 alt0-border,
div .damn-lo-col-flex damncr-main alt0 alt0-border {background:#EAF2EE!important;}
}
