css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* Search bars */
#searchForm {
background:transparent url('http://img26.imageshack.us/img26/4629/searchblue.gif') repeat-x scroll 0 -30px !important ;
}

#searchForm div.form {
background:transparent url('http://img26.imageshack.us/img26/4629/searchblue.gif') no-repeat scroll left top !important ;
}

#searchForm input.itext, #searchForm input.empty:focus {
background:transparent url('http://img26.imageshack.us/img26/4629/searchblue.gif') repeat-x scroll 0 -30px !important ;
left: 24px !important ;
}

#searchForm input.empty {
background:transparent none repeat scroll 0 0 !important ;
}

#searchForm input.ibutton a {
background:transparent url('http://img26.imageshack.us/img26/4629/searchblue.gif') repeat-x scroll 100% -60px !important ;
}

#searchForm ul.select li a {
background:transparent url('http://img26.imageshack.us/img26/4629/searchblue.gif') repeat-x scroll 0 -60px !important ;
}

#searchForm ul.select li a:hover {
background-position: 0 -90px !important ;
}

#searchForm ul.select li a.active, #searchForm ul.select li a:active, #searchForm ul.active li.selected a {
background-position: 0 -120px !important ;
}

div.popup2-search a:hover, div popup2-search a:focus {
background:transparent url('http://e.deviantart.com/emoticons/b/bulletwhite.gif') no-repeat 3px 4px !important ;
background-color: #2c6f91 !important ;
color: #ffffff !important ;
padding-left: 23px !important ;
}

div.popup2-search a:active {
background: #2c6f91 none repeat scroll 0 0 !important ;
color: #ffffff !important ;
}

div.popup2-search a {
background:transparent url('http://e.deviantart.com/emoticons/b/bulletblue.gif') no-repeat 3px 4px !important ;
background-color: #084b6d !important ;
color: #ffffff !important ;
padding-left: 23px !important ;
}

/* Pageview graph */
div.funsizegraph span.bar {
float:right;
/*margin-right:2px;*/
width:5px;
height:44px;
border:0 solid;
border-right-width:2px;
_width:7;
overflow:hidden;
position:relative;
top:44px;
background:#013b59 url(http://img25.imageshack.us/img25/5156/graphblue.gif) repeat-x!important;
}

/* Userpage & message center poll */
div.ctube div.cfill {
    background:url(http://img27.imageshack.us/img27/7311/pollblue.gif)!important;
}
div.ctube div.cfill span.sub10 {
color:#FFFFFF!important;
}

/* Footer poll */
.pollpole,
.poll-bar {
	background-color:#dde6ea!important;
	border-color:#2a6d8f!important;
	color:#01324b!important;
}
.pollpole {
	border-top:0px!important;	
	border-bottom:0px!important;	
	border-right:0px!important;
}
.poll_form .poll-bar i.tr,
.poll_form .poll-bar i.tl,
.poll_form .poll-bar i.br,
.poll_form .poll-bar i.bl {
	border-color:#d6ded4!important;
}

}
