css:
@-moz-document domain(testng.org) {
* {
    font-family: sans-serif;
    box-sizing: border-box;
}

body, html {
    margin: 0;
    padding: 0;
}

body {
    padding: 0 15%;
}

#topmenu {
    margin-top: 20px;
}

#topmenu>table, #topmenu td {
    background-color: white;
    margin: 0;
    padding: 0;
}

#topmenu table td, #topmenu table th {
    border-bottom: unset;
}

#topmenu td.current {
    background: #22222222;
    border-radius: 6px;
}
#topmenu td {
    padding: 0.5rem 0.5rem;
}

body>h2 {
    text-align: left;
    font-size: 1.5rem;
}
body>h3 {
    background: unset;
    border-bottom: unset;
    font-size: 1.25rem;
}

#table-of-contents {
    font-size: 0.9rem;
}

.syntaxhighlighter {
    background-color: unset !important;
    font-family: monospace;
}

h3 {
    font-size: 1rem;
    letter-spacing: unset;
}

h4 {
    font-size: 0.95rem;
}
h5 {
    font-size: 0.9rem;
}

caption {
    text-align: left;
    margin-bottom: 5px;
}

table {
    margin-top: 10px;
    margin-bottom: 10px;
    border-collapse: collapse;
    width: 100%;
}
table td, table th {
    border-bottom: 1px solid #33333333;
    padding: 10px;
}
table th {
    height: 40px;
    background: unset;
    text-align: left;
}
table td {
    height: 40px;
    background: unset;
}
}
