css:
@-moz-document domain("mokum.place") {
@media(max-width: 767px) {
    .bem-post__controls {
        display: flex;
        align-items: stretch;
    }
    .bem-post__controls > .bem-post__button, .bem-post__controls > .dropdown {
        border: 1px solid silver;
        flex-grow: 1;
        height: 32px;
    }
    .bem-post__controls > .dropdown {
        display: flex;
        justify-content: center;
    }
    .bem-post__controls > .dropdown > button {
        align-self: center;
    }
    .bem-post__controls .dropdown:nth-child(5) .dropdown-menu,
    .bem-post__controls .dropdown:nth-child(4) .dropdown-menu {
        right: 0px;
        left: inherit;
    }
    .bem-post__controls .dropdown:nth-child(3) .dropdown-menu {
        right: inherit;
        left: inherit;
    }
}
}
