 * Usage          : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(rapidshare.com) {

/* Resets default Mozilla styling of elements */
input, button, select, textarea, option {
-moz-appearance:none !important;
}

* {
outline:none!important;
}

img,
hr,
img[src="img2/pfeil.jpg"],
div[style="position: fixed; left: 0px; bottom: 0px; height: 15px; width: 50%; background-color: rgb(142, 144, 143);"],
div[style="position: fixed; left: 50%; bottom: 0px; height: 15px; width: 50%; background-color: rgb(0, 39, 96);"],
div[style="margin-top: 20px; font-size: 8pt; color: rgb(0, 39, 96);"],
div[style="color: rgb(0, 39, 96); font-size: 8pt;"],
h2,
p#small,
div.untermenue,
div.klappbox p:not([class])+p,
div.klappbox p:not([class])+p+p,
div.klappbox p:not([class])+p+p+p,
center div[style="width: 750px; text-align: left;"],
div.klappbox br,
div[style="padding-top: 15px; width: 728px; height: 353px; background-image: url(/img2/premium_vorteile_bg.jpg); background-repeat: no-repeat;"] {
display:none!important;
}

body {
background:#333 !important;
color:#747b6e!important;
}

/*FONTS*/
.klapptitel div, div.klappbox table[width="100%"] tbody tr td,
input[name="newfolder"],
input[value="Send download link"],
input[value="Premium Zone Login"],
input[value="Extend Premium Account"],
input[value="Send"],
input[value="Login"],
input[value="Check URLs"] {
font-size:11px!important;
}

.downloadlink {
color:#747b6e!important;
}

h1 {
background:none !important;
border-bottom:1px solid #484d44!important;
color:#747b6e!important;
}

h3 {
color:#747b6e!important;
}

/*BUTTON IMAGE*/
input[type="submit"], input[value="Check URLs"] {
background:url(data:image/png;base64,) no-repeat !important;
border:none!important;
color:#747b6e!important;
cursor:pointer!important;
height:28px!important;
padding:0 0 2px!important;
text-transform:uppercase!important;
width:138px!important;
font-size:11px!important;
margin-bottom:300px!important;
}

/*BUTTON IMAGE HOVER*/
input[type="submit"]:hover, input[value="Check URLs"]:hover {
background:url(data:image/png;base64,) no-repeat !important;
}

div.hauptmenue ul li {
background:transparent !important;
border:none!important;
color:#747b6e!important;
}

div.hauptmenue ul li a {
color:#747b6e!important;
}

div.hauptmenue ul li a:hover {
color:#63695e!important;
}

a {
color:#999!important;
text-decoration:none!important;
}

a:hover {
color:#888!important;
}

/*DOWNLOAD BUTTON*/
center input[type*="image"][src*="download_file.jpg"], input[id="btnupload"] {
background:url(data:image/png;base64,) !important;
height:100px!important;
padding-left:100px!important;
width:0!important;
margin-bottom:200px!important;
}

/*DOWNLOAD BUTTON HOVER*/
center input[type*="image"][src*="download_file.jpg"]:hover, input[id="btnupload"]:hover {
background:url(data:image/png;base64,) no-repeat !important;
}

.klapptitel div {
border-bottom:none!important;
color:#747b6e!important;
font-weight:900!important;
text-transform:uppercase!important;
}

p[style="color: red;"] {
color:#999!important;
text-transform:uppercase!important;
}

td {
border:none!important;
}

div#files {
color:#999!important;
}

input[size="65"] {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
margin-top:40px!important;
}

div.klappbox table[width="100%"] tbody tr td {
color:#999!important;
text-transform:uppercase!important;
font-weight:bold!important;
}

td input {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
}

input[type="checkbox"], textarea[style="width: 300px;"] {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
margin-top:5px!important;
}

textarea[style="width: 300px;"] {
padding:3px!important;
width:294px!important;
}

input[value="Send download link"] {
padding-bottom:3px!important;
}

center p, div[style="color: rgb(0, 39, 96); font-size: 10pt; font-weight: bold;"] {
color:#747b6e!important;
}

div[style="width: 725px; height: 316px; background-image: url(img2/praemien_infobox.jpg); background-repeat: no-repeat; background-position: right center;"] {
background:#222!important;
border:1px solid #666!important;
}

div[style="color: black; float: right; width: 380px; margin-right: 25px;"] {
color:#747b6e!important;
}

div[style="color: rgb(0, 32, 78); font-size: 16pt; height: 40px; margin-top: 55px;"] {
color:#999!important;
}

td div[style*="border: 1px solid rgb(0, 39, 96);"] {
background:none!important;
border:1px solid #666!important;
color:#747b6e!important;
}

h3[style="color: red;"] {
color:#999!important;
text-transform:uppercase!important;
}

div#inhaltbox center table[cellpadding="4"] {
float:left!important;
}

input[value="Extend Premium Account"] {
width:160px!important;
background:#222!important;
border:1px solid #666!important;
}

input[value="Extend Premium Account"]:hover {
background:#222!important;
border:1px solid #4a5145!important;
}

input[name="email"] {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
}

input[value="Send"] {
padding-top:5px!important;
}

tr input[name="username"], tr input[name="password"], tr input[name="login"], tr input[name="adminpassword"] {
background:#222!important;
width:150px!important;
}

form[id="freefoldersform"] input[name="foldername"],
form[id="freefoldersform"] input[name="folderheadline"],
form[id="freefoldersform"] input[name="adminpassword"],
form[id="freefoldersform"] input[id="adminpassword2"],
form[id="freefoldersform"] input[name="folderpassword"],
form[id="freefoldersform"] input[name="email"] {
background:#222!important;
}

textarea[id="urls"] {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
}

input[value="Check URLs"] {
margin-bottom:0!important;
}

input[value="Create Collector's Account"] {
background:#222!important;
border:1px solid #666!important;
color:#747b6e!important;
font-size:10px!important;
width:170px!important;
height:20px!important;
}

input[value="Create Collector's Account"]:hover {
background:#222!important;
border:1px solid #4a5145!important;
}
}
