css:
@-moz-document domain("youtube.com") {
  
ytd-rich-item-renderer:not([is-post]) {
  flex-basis: 210px !important;
  margin-bottom: 24px !important;
}

ytd-rich-grid-video-renderer {
  width: 210px;
}

#avatar-link {
  display: none;
}

#meta h3 {
   margin: 8px 0 8px !important;
}

#video-title.ytd-rich-grid-video-renderer {
  display: -webkit-box !important; 
  max-height: 3.2rem !important; 
  -webkit-box-orient: vertical !important; 
  overflow: hidden !important; 
  text-overflow: ellipsis !important; 
  white-space: normal !important; 
  -webkit-line-clamp: 2 !important; 
  font-size: 1.4rem !important; 
  font-weight: 500 !important; 
  line-height: 1.6rem !important; 
}

#metadata #text.ytd-channel-name,
#metadata-line.ytd-video-meta-block {
  font-size: 1.3rem !important
}

#video-title-link #video-title {
     display: -webkit-box !important;
    -webkit-box-orient: vertical !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
    white-space: normal !important;
    -webkit-line-clamp: 2 !important;
    font-size: var(--yt-link-font-size, 1.4rem) !important;
    font-weight: 500 !important;
    line-height: var(--ytd-link-line-height, 1.6rem) !important;
    letter-spacing: var(--yt-link-letter-spacing, normal) !important;
}

}
