css:
@-moz-document domain("penzu.com") {
body._app.ui-theme--none.__free-user div.container-main {
    background-image: url(https://penzu.com/stylesheets/themes/page/grass/background.jpg) !important;
    background-size: auto auto !important;
    background-repeat: repeat;
}

.header {
    background-color: green;
    background-image: linear-gradient(to right, #006915, #006915);
}

.header .sub-header a.nav-link span.icon,
.header .sub-header a.nav-link span.fa {
    color: #fff;
}

a.btn.btn-light.js-header-pro {
    color: #006915
}

.header .go-pro {
    border-right: 1px solid #fff
}

.global-search__input input {
    background: #01430f none repeat scroll 0 0;
    border: 1px solid #006915;
}

.global-search__input input:focus {
    background: #158f2d none repeat scroll 0 0;
    border: 1px solid #0f6921;
}

.global-loader__wrap.ng-hide {
    background: #006915;
}

.global-loader__wrap {
    background: #006915;
}

.global-loader__wrap .global-loader {
    background: #006915;
}
}
