css:
@-moz-document domain("www.wprost.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #ed1c24 #1c1c1c;
}
textarea,input{
    background: #1c1c1c !important;
    color: #fff !important;
    border-color: #ED1C24 !important;
}
/*STRONA-G≈ÅOWNA*/
#content,.box-list li.box-list-item-type-image_text.box-list-item-color-white,.box-list li.box-list-item-type-image_text.box-list-item-color-red .news-titlelead-wrapper,.page-box.box-blog-list ul.box-blog-list li{
    background: #1c1c1c;
}
.page-box .page-box-header ul li a,#exchange-rates-slider.in-page-box b,.box-blog-list li .blog-title, .box-blog-list li .blog-title strong,ul.box-blog-list li .item-author, ul.box-blog-list li .item-author a{
    color: #ED1C24;
}
.box-list li.box-list-item-type-image_text .news-titlelead-wrapper .news-title,.box-blog-list li .blog-lead, .box-blog-list li .blog-lead i{
    color: #fff;
}
.page-box-primetime .page-box-header h2 a{
    color: #FFD147 !important;
}
.page-box-quizzes .page-box-header{
    border-top: 5px solid #505050;
}
.page-box-quizzes .page-box-header h2 a{
    color: #505050 !important;
}
.box-list li .news-details{
    text-shadow: none;
}
/*ARTYKUL*/
.page-header .page-header-latests,.box-list-item .sub-header-tabs .sub-header-tabs-buttons span{
    background: #1c1c1c;
}
.news-titlelead-wrapper a span{
    color: #ed1c24;
}
.art-containers,.box-list li.box-list-item-list-color-white .sub-header, .box-list li.box-list-item-list-color-white .sub-header-tabs .sub-header{
    background: #ed1c24;
}
.share-button.more-button{
    background-color: #ed1c24;
}
.art-containers .item-containers a{
    color: #fff;
}
.art-title{
    color: #ed1c24;
}
.art-lead,.art-text{
    color: #fff;
}
.art-content .share-buttons{
    background: transparent;
    box-shadow: none;
}
#box-list-tab-latest-1-11-117-1 li,#box-list-tab-latest-1-11-117-1,.sub-header-tabs{
    background: #1c1c1c;
}
.box-list li.box-list-item-list .sub-header-tabs-buttons::before,#share-buttons::after{
    background: none;
}
#right-list-fresh{
    border-bottom: none;
}
.art-text a{
    color: #ed1c24;
}
.box-list-item .sub-header-tabs .sub-header span.transition.pointer{
    color: #1c1c1c;
}
.news-lead i{
    color: #fff !important;
}
.art-bar{
    background: #1c1c1c;
}
.comments ul li .comment{
    background: transparent;
    border: 2px solid #ed1c24;
}
#articles-autoload .element-next-area .page-billboard{
    border-top: 20px solid #ed1c24;
}
#articles-autoload .element-next-area .art-text .art-text-inner::after,.box-list li.box-list-item-list.box-list-item-list-color-white .sub-header-tabs-buttons::before,.art-text-canonical-button::after{
    background: none;
}
.bar-button-icon i, .share-button-icon i{
    background-color: #1c1c1c;
}
/*WIADOMOSCI*/
.box-list li.box-list-item-list ul.box-list{
    background: #1c1c1c;
}
.box-list li.box-list-item-list.box-list-item-list-color-white::after{
    background: none;
}
.box-list li.box-list-item-type-image_text.box-list-item-color-white .news-titlelead-wrapper{
    background: transparent;
}
/*KOMENTARZE*/
.comments .comment-nouser .comment-nouser-buttons .comment-button{
    color: #ed1c24;
}
.comment-nouser-buttons{
    color: #fff;
}
.comments .comment-nouser .comment-nouser-buttons a{
    color: #fff;
}
.comments ul li .comment{
    color: #fff;
}
.comments ul li header .comment-user-thumb::after{
    filter: invert();
}
#footer .copyright:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #ed1c24;
}
}
