css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbpress.org") {
body#bbpress, body.wide {
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  min-height: 100%;
  background: #363 url(data:image/png;base64,) repeat-x fixed !important;
}
#rap {
  background: #fff !important;
  margin: 15px auto !important;
  min-width: 700px !important;
  max-width: 1200px !important;
  width: 75% !important;
  -moz-border-radius: 1em !important;
  padding: .5em !important;
  position: relative !important;
}
#catchphrase {
  left: auto !important;
  right: 15px !important;
}
#bbfooter {
  margin: 1em 0 0 !important;
  -moz-border-radius: 1em !important;
}
#bbfooter p {
  font: 3em 'Courier New',monospaced !important;
  font-weight: bold !important;
  margin: 0 !important;
  opacity: .5 !important;
}
#bbfooter:hover p {
  opacity: .7 !important;
}
#submenu ul {
  list-style: none !important;
  margin: 0 !important;
}
table.content-list th:first-child, #latest th:first-child, #forumlist th:first-child, #favorites th:first-child, table.content-list, #latest, #forumlist, #favorites {
  -moz-border-radius-topleft: .75em !important;
}
table.content-list th:last-child, #latest th:last-child, #forumlist th:last-child, #favorites th:last-child, table.content-list, #latest, #forumlist, #favorites {
  -moz-border-radius-topright: .75em !important;
}
table.content-list, #latest, #forumlist, #favorites {
  border: 1px solid #777 !important;
}
table.content-list th, #latest th, #forumlist th, #favorites th {
  font-size: 13px !important;
}
#thread {
  margin-left: 160px !important;
}
.threadauthor {
  margin-left: -170px !important;
  width: 150px !important;
}
.star-average, .star.star-rating {
  background-color: #360 !important;
}
ul#sections li {
  font-size: 1.2em !important;
}
ul#sections li.current {
  -moz-border-radius-topleft: 10px !important;
  -moz-border-radius-topright: 10px !important;
}
div.column-right h4 {
  -moz-border-radius-topleft: 10px !important;
  -moz-border-radius-topright: 10px !important;
  border-bottom: 1px solid #77a977 !important;
}
div.column-right {
  -moz-border-radius: 12px !important;
  border: 1px solid #006400 !important;
}
#thread li, #postform {
  -moz-border-radius: 1em !important;
}
#post_content {
  -moz-border-radius: .5em !important;
  width: 99% !important;
}
fieldset p {
  font-size: 1.2em !important;
}
fieldset {
  border: 0 !important;
  padding: 0 !important;
  margin: 0 !important;
}
ol.screenshots {
  list-style: none !important;
  margin:  1em 0 !important;
}
img.screenshot {
  width: auto !important;
  max-width: 100% !important;
  margin: 0 auto !important;
  display: block;
}
ul#sections {
  border: 1px solid #ccc !important;
  border-bottom: none !important;
  -moz-border-radius-topleft: 12px !important;
  -moz-border-radius-topright: 12px !important;
  padding: 0.85em 0 5px !important;
}
ul#sections li.section-stats {
  display: none !important;
}
tr.resolved {
  font-size: .7em !important;
}
}
