css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#searchForm {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/searchbar/rainbowsearch.gif') repeat-x scroll 0 -30px !important ;
}

#searchForm div.form {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/searchbar/rainbowsearch.gif') no-repeat scroll left top !important ;
}

#searchForm input.itext, #searchForm input.empty:focus {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/searchbar/rainbowsearch.gif') repeat-x scroll 0 -30px !important ;
left: 24px !important ;
}

#searchForm input.empty {
background:transparent none repeat scroll 0 0 !important ;
}

#searchForm input.ibutton a {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/searchbar/rainbowsearch.gif') repeat-x scroll 100% -60px !important ;
}

#searchForm ul.select li a {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/searchbar/rainbowsearch.gif') repeat-x scroll 0 -60px !important ;
}

#searchForm ul.select li a:hover {
background-position: 0 -90px !important ;
}

#searchForm ul.select li a.active, #searchForm ul.select li a:active, #searchForm ul.active li.selected a {
background-position: 0 -120px !important ;
}

}
