@-moz-document domain(ionisx.com) {
	
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider {
    transform: scale(1) translate3d(0,0,0);
    background: rgba(255,255,255,0.26);
    border: 0;
    border-radius: 0px;
    position: absolute;
    z-index: 1;
    bottom: 100%;
    left: 0;
    right: 0;
    height: 8px;
    margin-left: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    -webkit-transition: -webkit-transform 0.7s ease-in-out;
    -moz-transition: -moz-transform 0.7s ease-in-out;
    -ms-transition: -ms-transform 0.7s ease-in-out;
    transition: transform 0.2s ease-in-out;
    box-shadow: 0px 0px 0px 0px black;
}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider {
    box-shadow: 0px 4px 15px 0px black;

}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider div.ui-widget-header {
    background-color: rgba(255, 255, 255, 0.8);
    background-position-x: 0%;
    background-position-y: 0%;
    background-repeat: repeat;
    background-attachment: scroll;
    background-image: none;
    background-size: auto;
    background-origin: padding-box;
    background-clip: border-box;
    border-radius: 0px;
    
	box-shadow: none;
    background: -webkit-linear-gradient(to right, #0072ff, #00c6ff);
    background: linear-gradient(to right, #0072ff, #00c6ff);
    transition: width 1.2s ease-in-out;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls:hover div.slider div.ui-widget-header {
    transition: width 0s ease-in-out;
}




.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider a.ui-slider-handle {
    box-shadow: 0px 4px 15px 0px black;
    transform: scale(1,1) translate3d(-50%, -15%, 0);
    background: #00c6ff;
    background-size: auto;
	background-size: 50%;
	border: 0px solid;
	border-radius: 50%;
    height: 10px;
    width: 10px;
    margin-left: 0;
    top: 0;
    transition: background 0.2s ease-in-out, transform 0.2s ease-in-out, box-shadow 0.15s ease-in-out, left 1.2s ease-in-out;
    box-shadow: 0px 1px 5px 0px rgba(0,0,0,0);
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper:hover section.video-controls div.slider a.ui-slider-handle {
    transform: scale(1) translate3d(-50%, -15%, 0);
}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls:hover div.slider a.ui-slider-handle {
    transition: background 0.2s ease-in-out, transform 0.2s ease-in-out, box-shadow 0.15s ease-in-out, left 0s ease-in-out;
}



.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider a.ui-slider-handle:focus, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider a.ui-slider-handle:hover, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider div.info-wrapper section.handouts ol li div.hitarea:hover + h4.ui-slider-handle, div.info-wrapper section.handouts ol li .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider div.hitarea:hover + h4.ui-slider-handle, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider div.info-wrapper section.handouts ol li div.hitarea:focus + h4.ui-slider-handle, div.info-wrapper section.handouts ol li .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider div.hitarea:focus + h4.ui-slider-handle {
    background-color: #009dff;
    box-shadow: 0px 4px 10px 0px rgba(0,0,0,0.5);

}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.slider a.ui-slider-handle:hover {
    background: #00b0ff;
}




.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls {
	background: #222;
}








/* PLAY */


.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume > a, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.add-fullscreen, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.quality-control, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.lang > a.hide-subtitles {
    border: none;
    line-height: 32px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control {

    float: left;
    background-image: url("https://i.imgur.com/wMZyRwY.png");
    background-size: 28px 56px;
    background-repeat: no-repeat;
    border: 0px;
    padding: 0 1.06181em;
    width: 10px;
    transition: 0.15s ease-in-out;
    border-radius: 0px 0 0px 6px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control.play {

    background-position: 5px -25px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control.pause {

    background-position: 5px 2px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control:focus {

    position: relative;
    outline: none;
}





/* TIME */
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr div.vidtime {
    line-height: 30px;
    padding-left: 0px;
    color: #999;
    transition: 0.15s ease-in-out;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls:hover .vcr div.vidtime {
    color: #ddd;
}






/* SPEED */

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button {
    min-width: 0;
    width: 45px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button .value {
    padding: 0 0 0 0;
    line-height: 30px;
    width: 35px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu {
    transition: 0.3s ease-in-out;
	background-color: #444;
	border: none;
    bottom: 30px;
    opacity: 0;
    box-shadow: 0 4px 20px 0 rgba(0,0,0,0.5);
    border-radius: 10px 10px 0 0;
    overflow: hidden;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu li {
    box-shadow: none;
	border-bottom: none;
	color: #fff;
    transition: 0.3s ease-in-out;
    width: 60px;
}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu li a {
    transition: 0.15s ease-in-out;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu li a:hover,
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu li a:focus {
    background-color: #fff;
	color: #333;
    outline-offset: 0;
}
a:link:hover, div.info-wrapper section.handouts ol li div.hitarea:hover + h4:link, div.info-wrapper section.handouts ol li div.hitarea:focus + h4:link, a:link:focus, a:visited:hover, div.info-wrapper section.handouts ol li div.hitarea:hover + h4:visited, div.info-wrapper section.handouts ol li div.hitarea:focus + h4:visited, a:visited:focus {
    text-decoration: none;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls .menu-container .menu li.is-active a {
    font-weight: bold;
    background-color: #fff;
	color: #333;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .menu {
    width: 55px;

}







/* VOLUME */

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume > a {

    background-image: url("https://i.imgur.com/IDnxgHA.png") center no-repeat;
    background-size: 15px 15px;
    width: 20px;
    height: 30px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume.is-muted > a {

    background-image: url("https://i.imgur.com/pRSiZus.png") center no-repeat;
    background-size: 15px 15px;

}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume .volume-slider-container .volume-slider a.ui-slider-handle {
    -webkit-transition: height 2s ease-in-out 0s,width 2s ease-in-out 0s;
    -moz-transition: height 2s ease-in-out 0s,width 2s ease-in-out 0s;
    transition: height 2s ease-in-out 0s,width 2s ease-in-out 0s;
    background: #00c6ff;
    background-color: #00c6ff;
	border: none;
	border-radius: 15px;
    height: 15px;
    left: -5px;
    width: 15px;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume .volume-slider-container .volume-slider {
    height: 100px;
    border: none;
    width: 5px;
    margin: 14px auto;
    background: #666;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume .volume-slider-container {
    transition: 0.3s ease-in-out;
	background-color: #444;
    border: none;
    bottom: 45px;
    display: none;
    opacity: 0;
    position: absolute;
    width: 0px;
    height: 0px;
    margin-left: 0px;
    border-radius: 100px 100px 0 0;
    box-shadow: 0px 0px 0px 0px black;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume.is-opened .volume-slider-container {
    width: 30px;
    height: 125px;
    box-shadow: 0px 4px 20px 0px rgba(0,0,0,0.5);
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume .volume-slider-container .volume-slider a.ui-slider-handle {
    box-shadow: inset 0 0px 0;
}







/* HD */

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.quality-control.active {
    background-color: #FF416C;
    background-color: -webkit-linear-gradient(to right, #FF4B2B, #FF416C);
    background-color: linear-gradient(to right, #FF4B2B, #FF416C);
	color: #0ff;
    text-decoration: none;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.quality-control:focus {
    z-index: 0;
}





/* FULLSCREEN */

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.add-fullscreen {

    background: url("https://i.imgur.com/LTOxTH7.png") center no-repeat;
        background-color: rgba(0, 0, 0, 0);
    background-size: 25px 25px;
    border-left: none;
    float: left;
    padding: 0 11px;
    width: 10px;
    border-radius: 0px 0 6px 0px;

}
.content-wrapper .container .course-wrapper .course-content .xblock.xblock-student_view-video .video.video-fullscreen {
    background: #111;
}



.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button .value {

    padding: 0 0 0 10px;

}


.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button .label {
	display: none;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button {
    background-image: url("https://d1f7pu4s0iin02.cloudfront.net/static/images/closed-arrow.54c8afeba30a.png");
    background-position: 10px center;
    background-repeat: no-repeat;
    min-width: 50px;
    text-indent: 0;
}


.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume .volume-slider-container {
	transform: translateY(16px);
    width: 30px;
    
}


.xmodule_display.xmodule_VideoModule div.video.video-fullscreen div.tc-wrapper article.video-wrapper object, .xmodule_display.xmodule_VideoModule div.video.video-fullscreen div.tc-wrapper article.video-wrapper iframe, .xmodule_display.xmodule_VideoModule div.video.video-fullscreen div.tc-wrapper article.video-wrapper video {

    position: absolute;
    width: auto;
    height: auto;
    box-shadow: 0px 10px 50px 10px black;

}















/* OTHER CONTAINERS*/


.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls {
    border-radius: 0px 0 6px 6px;
    z-index: 10000000000000;
}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-player object, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-player iframe, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-player video{
    border-radius: 6px 6px 0 0;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-player {

    overflow: hidden;
    min-height: 100px;

}

.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls .vcr .video_control, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.speeds .speed-button, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.volume > a, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.add-fullscreen, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls a.quality-control, .xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls div.secondary-controls div.lang > a.hide-subtitles {
    box-shadow: none;
}
.xmodule_display.xmodule_VideoModule div.video article.video-wrapper section.video-controls {

    background: #333;
    border: 0;
}
