css:
@-moz-document domain("watchnebula.com") {
body, .Thumbnail, #NebulaApp .ChannelFeatured--Thumbnail .ChannelFeatured-Image, #NebulaApp .Channel-Header .Channel-Avatar, #NebulaApp .ChannelSocialLinks a, .ui.fluid.image.Hero-Image {
    background-color: #2C2F33;
}

#NebulaApp .NavBar, .ui.menu .dropdown.item .menu {
    background-color: #23272A
}

#NebulaApp .NavBar-Left .item.active {
    color: #6FBCE6;
}


.ui.form input:not([type]), .ui.form input[type="date"], .ui.form input[type="datetime-local"], .ui.form input[type="email"], .ui.form input[type="number"], .ui.form input[type="password"], .ui.form input[type="search"], .ui.form input[type="tel"], .ui.form input[type="time"], .ui.form input[type="text"], .ui.form input[type="file"], .ui.form input[type="url"] {
    background-color: #171a1c;
    border-color: #6FBCE6;
    color: #6FBCE6;
}

.ui.form input:not([type]):focus, .ui.form input[type="date"]:focus, .ui.form input[type="datetime-local"]:focus, .ui.form input[type="email"]:focus, .ui.form input[type="number"]:focus, .ui.form input[type="password"]:focus, .ui.form input[type="search"]:focus, .ui.form input[type="tel"]:focus, .ui.form input[type="time"]:focus, .ui.form input[type="text"]:focus, .ui.form input[type="file"]:focus, .ui.form input[type="url"]:focus {
    background-color: #141517;
    border-color: #83c6eb;
    color: #83c6eb;;
    transition: color 0.1s ease, border-color 0.1s ease, background-color 0.1s ease;
}

.search {
    color: #fff; 
}

#NebulaApp .ChannelFeatured-Header, .content, .ui.header, .VideoSquare-Header, .Channel-BaseInfo {
        color: #6FBCE6;
}

#NebulaApp .Video-Description {
    color: #ccc;
}

.FollowingButton--follow {
    color: #FFF;
	border: 1px solid #ccc;
}


.FollowingButton--unfollow {
    background-color: #23272A;
}

.corner {
    display: none;
} 

#NebulaApp .Channel .Channel-Tabs > .menu, .ui.tabular.menu .active.item {
    border-bottom: 3px solid #6FBCE6;
    
}

#NebulaApp .Channel .Channel-Tabs > .menu > a.active {
    background: none #6FBCE6;
    color: #fff;
    font-weight: bold;
}

.CategoryMenuItem img {
    filter: invert(1);
}
}
