@-moz-document domain("animemusicquiz.com")
{
/* ==UserStyle==
@name         AMQ Styles
@namespace    https://openusercss.org/theme/5a2f049bba666f0b00b9c82b
@version      1.0.0
@license      Other
@description  AMQ
@author       PastGhost
@downloadURL https://github.com/felipemcord/AMQScripts/raw/master/Stylus.css
@updateURL   https://github.com/felipemcord/AMQScripts/raw/master/Stylus.css
==/UserStyle== */
/* Thank you to: Klem, Juvian and LolCats for providing a bunch of the code*/
/* You can pretty much change any element here. Feel free to message me if you want to find a certain thing to change */
/*-MelodyElodie*/


  :root
  {
	/* Change the URL in quotes to whatever you want the bg to be. */
    --bg: url(https://i.imgur.com/YCcwvQT.jpg);
    /* Optional: if you want a different background for chat. Phone wallpapers tend to work best. */
    --chatbg: rgba(0,0,0,1);
  }

  #startPage
  {
    background-image: var(--bg);
    background-size: cover;
  }

  #gameContainer
  {
    background-image: var(--bg);
    background-size: cover;
  }

  #loadingScreen
  {
    background-image: var(--bg);
    background-size: cover;
  }

  #gameChatPage > .col-xs-9
  {
    background-image: var(--bg);
    background-size: cover;
  }

  #gcGameChatButton
  {
    color: #ffffff;
    box-shadow: none;
    text-shadow: 0 0 20px #96FF96, 0 0 30px #66FF66, 0 0 40px #66FF66, 0 0 50px #66FF66 !important;
  }

  #footerMenuBarBackground
  {
    background: rgba(193,188,188,1);
  }

  #awMainView
  {
    background-image: var(--bg);
    background-size: cover;
  }

  #gameChatContainer
  {
    background: var(--chatbg);
  }

  .leftShadowBorder, #currencyContainer, #menuBarOptionContainer, #awContentRow .rightShadowBorder
  {
    box-shadow: none;
  }

  #footerMenuBar .menuBar, #optionsContainer.popOut, #rightMenuBarPartContainer::before, .gcList > li:nth-child(2n)
  {
    background-color: #1b1b1b;
  }

  #socialTab:not(.open), #optionsContainer:not(.open)
  {
    display: none;
  }

  #mainMenuSocailButton, #avatarUserImgContainer
  {
    border: none !important;
  }

  #optionsContainer li
  {
    background-color: #424242 !important;
  }

  #rbMajorFilters
  {
    background-color: rgb(0, 0, 0);
    padding-left: 10px;
  }

  .leftShadowBorder
  {
    box-shadow: none;
    -webkit-box-shadow: none;
  }

  .gcList > li:nth-child(2n)
  {
    background: none;
  }

  .gcInputContainer
  {
    background: none;
  }

/* Legibility fix for guesses */
  .qpAvatarAnswerContainer.rightAnswer .qpAvatarAnswer
  {
    box-shadow: none;
    text-shadow: 0 0 20px #96FF96, 0 0 30px #66FF66, 0 0 40px #66FF66, 0 0 50px #66FF66 !important;
  }

  .qpAvatarAnswerContainer.wrongAnswer .qpAvatarAnswer
  {
    box-shadow: none;
    text-shadow: 0 0 20px #FF9996, 0 0 30px #FF4A44, 0 0 40px #FF4A44, 0 0 50px #FF4A44 !important;
  }

  .avatarIsHostContainer
  {
    box-shadow: none;
    -webkit-box-shadow: none;
  }

  .avatarIsHostContainerOuter
  {
    position: initial;
    box-shadow: none;
    -webkit-box-shadow: none;
  }

  .qpAvatarNameContainer .avatarIsHostContainer
  {
    background-color: unset;
    background-image: url("https://files.catbox.moe/jizubh.png");
    transform: none;
    left: 25px;
    width: 20px;
    height: 20px;
    margin-top: 4px;
  }

  #mainMenuSocailButton, .lobbyAvatarImgContainer, .menuBarOptionContainer
  {
    background-color: rgba(0,0,0,0.7);
  }

  #avatarUserImgContainer
  {
    opacity: 9;
  }

  #avatarUserImgContainer:hover
  {
    opacity: 1.0;
  }

  .lobbyAvatarTextContainer
  {
    background: rgba(0,0,0,0.7);
  }

  #gameContainer, #gameChatPage > .col-xs-9, #mainMenuSocailButton, #avatarUserImgContainer, .topMenuBar, #awContentRow, #awMainView, .gcInputContainer, .qpSideContainer, .qpAnimeNameContainer, #qpCounter, #qpWaitBuffering,
.chatBoxContainer
  {
    background-color: rgba(1,1,1,1);
    background-size: 100% auto !important;
    background-attachment: fixed !important;
    background-position: 0px !important;
  }

  #qpAnimeNameHider , #qpVideoHider, #qpInfoHider, .qpAvatarNameContainer, .qpAvatarAnswerContainer, .qpVideosUserHidden,.qpVideoOverlay, #qpVoteSkip, #qpVoteState, #qpWaitBuffering
  {
    background-color: rgb(0, 0, 0);
  }

  #gameContainer >.col-xs-9, #rbRoomContainer .rbrRoomImageContainer, .awOverviewAvatar, .menuBar .popOut, #socailTabFooter > .selected, #optionsContainer li , .qpSingleRateContainer
  {
    background-color: rgba(0, 0, 0, 0.3) !important;
  }

  #footerMenuBar .menuBar,#rightMenuBarPartContainer::before , #rbMajorFilters , #rbFilterContainer , #roomBrowserHostButton, .floatingContainer
  {
    background-color: rgba(0, 0, 0, 0.8);
  }

  #gcGameChatButton
  {
    text-shadow: none !important;
  }

  #gcChatMenu > div.selected , .tab-modal .tab.selected
  {
    background: none;
    background-color: rgba(40, 40, 40, .7) !important;
  }

  #elQuestionNoSongSelectedContainer, .modal-content, .tab-modal .tabContainer, .tab-modal .modal-header, .topMenuButton, .topMenuBar > .topMenuBack,  .topRightBackButton
  {
    background-color: rgba(0, 0, 0);
  }

  .qpAvatarImgContainer
  {
    box-shadow: 0 0 0px 0px rgb(0, 0, 0);
    display: inline-block;
    background-color: rgba(0,0,0,0.6);
    background-size: cover;
    position: relative;
    z-index: -1;
  }

  .qpAvatarLevelText
  {
    background-color: rgb(0, 0, 0);
    border-radius: 1px 1px 50px 50px;
  }

  .qpAvatarPointText
  {
    background-color: rgb(0, 0, 0);
    border-radius: 50px 50px 1px 1px;
  }

  .leftShadowBorder
  {
    box-shadow: none;
    -webkit-box-shadow: none;
  }

  .gcList > li:nth-child(2n)
  {
    background: none;
  }

  .gcInputContainer
  {
    background: none;
  }

  #xpBackground
  {
    background: #af00ff;
    border: #d53cf9;
  }

/* Legibility fix for guesses */
  .qpAvatarAnswerContainer.rightAnswer .qpAvatarAnswer
  {
    box-shadow: none;
    text-shadow: 0 0 20px #96FF96, 0 0 30px #66FF66, 0 0 40px #66FF66, 0 0 50px #66FF66 !important;
  }

  .qpAvatarAnswerContainer.wrongAnswer .qpAvatarAnswer
  {
    box-shadow: none;
    text-shadow: 0 0 20px #FF9996, 0 0 30px #FF4A44, 0 0 40px #FF4A44, 0 0 50px #FF4A44 !important;
  }

  #xpBarCoverInner
  {
    background-color: rgb(0,0,0);
  }

  #xpBarCoverOuter
  {
    border: solid 3px rgb(19, 24, 40);
  }

  #xpBarCoverInner
  {
    border-top: rgb(19, 24, 40) solid;
    border-left: rgb(19, 24, 40) solid;
    border-right: rgb(19, 24, 40) solid;
  }
}
