css:
@-moz-document domain("trello.com") {

/* GENERAL */

body, html, input[type="text"], select, textarea { font-size: 10px; }

/* BOARD */

.board-header { margin-bottom: 0px; }
.board-header-btn { 
  line-height: 32px;
  height: 32px;
}
.board-header-btn-icon {
  padding: 8px 5px;
}

.board-header-btn-divider {
  margin: 5px 8px 12px 4px;
}


/* LIST */

.list-wrapper { width: 180px; }

.list-header { padding-top: 6px; }

.list-header-name { 
  font-size: 11px; 
  line-height: 13px 
}

.list-header-num-cards { 
  font-size: 9px; 
  font-weight: 600; 
  line-height: 11px; 
  display: block;
  margin-top: 3px;
}


/* CARD */

.list-card {
  margin-bottom: 6px
}

.list-cards {
  margin: 0 3px;
  padding: 0 3px;
}

.list-card-details {
  padding-top: 0;
}

.list-card-title {
  font-size: 9px;
  line-height: 11px !important;
  margin-top: 4px;
  font-weight: 500;
  color: #616161;
}

.list-card .card-label,
.list-card .card-label .label-text {
  font-size: 8px;
  line-height: 12px !important;
}

.list-card-members .member,
.list-card-members .member .member-avatar {
  width: 16px;
  height: 16px;
  border-radius: 3px;
  margin: 0;
  padding: 0;
}

.list-card-members {
  margin-top: -2px;
}

.list-card-details {
  padding-bottom: 4px;
}

.list-card-details .badges {
  line-height: 0;
  margin-bottom: 0;
  display: flex;   
}

.list-card-details .js-plugin-badges,
.list-card-details .js-custom-field-badges,
.list-card-details .js-badges {
  display: inline-block;
  line-height: normal;
}

.list-card-details .badge {
  line-height: 11px;
  min-height: 0;
  margin: 0 2px;
  padding: 1px 2px;
}


.badge-icon.icon-sm {
  height: 11px;
  width: 11px;
  font-size: 11px;
  line-height: 11px;
  vertical-align: middle
}

.badges .badge-text {
  font-size: 9px;
  padding-right: 2px;
}

.card-short-id {
  display: inline;
  cursor: default;
  line-height: 12px;
  margin-right: 4px;
  text-decoration: none;
  font-size: 9px;
  border: 0;
  padding: 0;
  color: #000;
  font-weight: 600;
}

.list-card-details .badge .plugin-icon {
  background-size: 11px;
  background-position: 0;
}
    
/* CARD SIZE PLUGIN */    

.js-plugin-badges .badge.plugin-color-orange {
  vertical-align:bottom;
}
    
.js-plugin-badges .badge.plugin-color-orange .badge-icon {
  display: none;
}

/* CARD COMPOSER */
textarea.list-card-composer-textarea {
  font-size: 9px;
  line-height: 12px;
  margin-top: 5px;
}

.card-composer .js-add-card {
  font-size: 11px;
}

/* VERTICAL LABELS */

.list-card-details {
  padding-left: 20px;
}
    
.list-card-details .list-card-front-labels-container {
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;      
}

.list-card-details .list-card-front-labels-container .js-react-root,
.list-card-details .list-card-front-labels-container .js-react-root > div,    
.list-card-details .list-card-front-labels-container .js-react-root > div > div,
.list-card-details .list-card-front-labels-container .js-react-root > div > div > button {
  height: 100%;      
}
   
.list-card-details .list-card-front-labels-container .js-react-root > div {
  gap: 0;
}
    
.list-card-details .list-card-front-labels-container .js-react-root > div > div > button {
  min-width: 9px;
  max-width: 9px;
  border-radius: 0;
}
    
.list-card-details .list-card-front-labels-container .js-react-root > div > div:first-child > button {
  border-top-left-radius: 2px;
  border-bottom-left-radius: 2px;
}  


}
