css:
@-moz-document domain("kaban.is-very-cute.moe") {
/*====================*/
/* Basic Site Styling */
/*====================*/

html, body {
    background: #191919;
    border: none;
    color: #C8C8C8; }

body {
    max-width: 700px; }

#header {
    background: #232323;
    border-radius: 6px;
    margin: 1rem 0; }

h1, h2 {
    color: #FFFFFF; }

#sitename, ul li {
    line-height: 1.5rem; }

nav {
    float: right; }

nav a {
    color: #FF88FF; }

/*===================*/
/* Restyle All Links */
/*===================*/

a {
    color: #EAEAEA;
    text-decoration: none;
    transition: color 0.2s; }

a:link {
    font-weight: 700; }

a:visited {
    font-weight: 400; }

a:hover {
    text-decoration: underline;
    color: #FFFFFF; }

/*===============*/
/* Table Fuckery */
/*===============*/

/* Remove borders */

table, thead, td, th {
    border: none; }

th, td {
    padding: 0.5rem; }

/* Awful hacky way to round table corners */

table tr:first-child th:first-child { /* Top Left */
    border-top-left-radius: 6px; }

table tr:first-child th:last-child { /* Top Right */
    border-top-right-radius: 6px; }

table tr:last-child td:first-child { /* Bottom Left */
    border-bottom-left-radius: 6px !important; }

table tr:last-child td:last-child { /* Bottom Right */
    border-bottom-right-radius: 6px; }

/* Change every other table row's color */

thead tr th {
    background: #232323;
    color: #FFFFFF; }

table tr:nth-child(even) {
    background: #232323 !important; }

/*==========================*/
/* Style Upload Replay Form */
/*==========================*/

#replay_drop {
    background-color: #232323; 
    border-radius: 6px; 
    border: solid 1px #EAEAEA; }
}
