/* ------------------------------ Details ------------------------------ */
/*                  name: dAmn v7                                        */
/*                author: WasNeverAlive with help from electricjonny     */
/*               created: Thursday, May 07, 2009, 1:10 PM EST            */
/*         last modified: Thursday, May 07, 2009, 2:10 PM EST            */
/*           description: Clean dAmn CSS, uncluttered unlike most styles */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://chat.deviantart.com/chat/") {

                              /* Start of CSS */
body {
	height: 98%!important;
	width: 100%!important;
	position: fixed!important;
	top: 0px!important;
	left: 0px!important;
	margin: 0!important;
	padding: 0!important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 8pt!important;
	overflow: hidden!important;
	background: #76827B !important;
}
.main {
	position: relative !important;
	top: 0px!important;
	background: none !important;
	border-color: #D3E0DC !important;
}
                    /* Reposition the main chat area */
#window {
	position: fixed !important;
	top: 34px!important;
	height: 90.70%!important;
	width: 97.70%!important;	
	margin-left: 7px!important;
	margin-right: 19px!important;
	border: none !important;
	background: #EAF2EE !important;
	padding: 5px!important;
	-moz-border-radius: 5px!important;
}
                        /* Remove the More button */
.damnc-morelink {
	display:none!important;
}
                               /* Tab fix */
.damnc-tabbar strong i, 
.damnc-tabbar a i {
	display:none!important;
}
                                /* Tab bar */
.tabbar {
	position: absolute !important;
	top: -3px!important;
	left: 20px!important;
	height: 30px!important;
	z-index: 12!important;
}
.tabbar strong {
	position: relative !important;
	top: -3px!important;
	height: 20px!important;
	width: 140px!important;
	border: none !important;
	color: #AFC81C!important;
	font-size: 8.25pt!important;
	text-align: center!important;
	vertical-align: middle!important;
	background: #76827B !important;
	border-top:0px solid #222!important;
	z-index: 5!important;
	margin:0px 3px!important;
	-moz-border-radius: 0px 0px 5px 5px!important;
}
.tabbar a {
	position: relative !important;
	top: -3px!important;
	height: 20px!important;
	border: none !important;
	background: #76827B !important;
	border-top:0px solid #000!important;
	color: #AFC81C;
	font-weight: normal;
	z-index: 5!important;
	margin:0px 3px!important;
	-moz-border-radius: 0px 0px 5px 5px!important;
}
.tabbar a, 
.tabbar strong {
	padding-left: 4px!important;
	padding-right: 4px!important;
}
.tabbar a:focus, 
.tabbar a:active {
	color: #AFC81C !important;
	background: #EAF2EE !important;
}
.tabbar a:hover {
	border-bottom: 3px solid #AFC81C!important;
	color: #AFC81C !important;
	background: #EAF2EE !important;
}
.content {
	position:relative!important;
	top:-10px!important;
	left:5px!important;
	margin:0px -20px!important;
	-moz-border-radius: 0px!important;
	padding: 5px!important;
	height: 100%!important;
	background: #EAF2EE!important;
}
                               /* Color Fixes */
.damnc-serverlog, 
.damncr-topbar, 
.damncr-close, 
.damncr-title, 
.damncrc-chat-outer, 
.damncrc-chat-window {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.damn-lo-row-top, 
.damn-lo-row-flex, 
.alt1-border {
	border-color: #EAF2EE!important;
	background: #EAF2EE!important;
}
.alt0 {
	background-color: #EAF2EE!important;
}
.alt0-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-top-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-bottom-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt0-bottom-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
.alt1-left-border {
	background-color: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
div.damncrc-chat-window alt2 {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
* html .collapsed .exc, * html .expanded .exc {
	border-color: #EAF2EE!important;
}
#content, 
#content-core, 
#content-ctrl1, 
#content-ctrl2, 
#content-ctrl3, 
#content-ctrl4, 
#content-ctrl5, 
#content-ctrl6, 
#content-ctrl7, 
#content-ctrl8, 
.output-primary, 
#dAmnPluginArea, 
.damnc-serverlog-outer, 
.dAmn-Chatbox, 
.damn, 
.trailing, 
.section-block, 
#window {
	border-color: #EAF2EE!important;
	background: #EAF2EE!important;
}
.damncr-members, 
.sidebar, 
.damncr-leftbar, 
.damncr-iconbar, 
.damncrc-input, 
.damncr-topbar, 
.damncr, 
.dAmn-Chatspace, 
.section, 
.damncrc-iconbar {
	background: #EAF2EE!important;
	border-color: #EAF2EE !important;
}
          /* Images in the title (to block, change show to none) */
.damncr-title img {
	display: show !important;
	text-indent: -999px!important;
}
          /* Images in the topic (to block, change show to none) */
.damncr-topic img {
        display: show !important;
        text-indent: -999px!important;
}
                                   /* Topic */

.damncrc-topic {
	position: relative!important;
	top: 0px!important;
	padding: 2px 4px!important;
	-moz-border-radius: 10px 10px 0px 0px!important;
	background: #D3E0DC!important;
	border: 1px solid #D3E0DC!important;
}
                                /* Member-list */
.damncr-member ul {
	list-style: none !important;
}
.damncr-member {
	min-width: 100px!important;
}
.damncr-members .simple b {
	border-bottom: 1px solid #95a59b !important;
}
.damncr-members dt {
	text-align: center!important;
	padding: 2px 4px!important;
}
                 /* Removes name and room on the toolbar */
.damncr-close, 
.damncrc-icon-roomname {
	display:none!important;
}
.userinfo-outer .damncr-close {
	display:none!important;
}
                            /* Conversation area */
.damncrc-chat-inner {
	background: #fff!important;
	border: 1px solid #000!important;
	height: auto!important;
}
                                 /* Messages */
div.msg part {
	color: #485856 !important;
	text-align:left!important;
}
.damncr .self-hl  .from SPAN {
	background: #f9f9f9!important;
	font-style:bold!important;
	text-align:left!important;
}
.damncr .other-hl .text SPAN {
	background:none!important;
	font-weight:normal!important;
	text-align:left!important;
}
.damncr .other-hl .from SPAN {
	background: #D3E0DC!important;
	-moz-border-radius: 5px!important;
	padding:2px!important;
	text-align:left!important;
}
.damncr .join, 
.damncr .part {
	color: #76827b!important;
	text-align:left!important;
}
span .text {
	text-align:left!important;
}
form {
	position: relative!important;
	top: 2px!important;
	background: #EAF2EE !important;
	border-color: #EAF2EE !important;
	margin: 0px 75px -1px 5px!important;
}
textarea, 
textarea:focus {
	height: 40px!important;
	-moz-border-radius: 5px!important;
	background: #fff!important;
	border-color: #838582!important;
}
input {
	-moz-border-radius: 5px!important;
	padding: 2px 4px!important;
	height: 24px!important;
	margin-bottom: -1px!important;
	vertical-align:middle!important;
}
input.button {
	position: relative!important;
	top: 20px!important;
	margin-right: 5px!important;
	width: 65px!important;
	background-color: #374341 !important;
	border: none !important;
	font-weight: bold !important;
	color: #8A9B91 !important;
}
                     /* Hover over username on userlist */
.damncri-member .b {
	background: #D3E0DC!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px 0px 5px 0px!important;
}
.damncri-member .bodyarea UL {
	list-style-type: none!important;
	margin: 0!important;
	padding-left: 1.2em!important;
	border-color: #EAF2EE!important;
}
div.aside-left {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}
div.damncr-infotr img {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
div.damncr-infotr, 
div.damncr-infotr alt0 {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}
div.bodyarea {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
	-moz-border-radius: 5px!important;
}

.damncr .userinfo-outer, 
.damncr .userinfo-inner, 
.damncr .userinfo, 
.collapsed *, 
.expanded *, 
.collapsed * {
	background: #EAF2EE!important;
	border-color: #EAF2EE!important;
}
* html .collapsed .exc, * html .expanded .exc {
	border-color: #76827b!important;
}
.damncrc-alertbox {
	padding:2px 4px!important;
}
.damncrc-alertbox, 
.damncrc-error {
	background:#D3E0DC!important; 
	border-color: #EAF2EE!important;
}
.damncrc-chat *, 
.damncrc-chat-inner *, 
.damncrc-chat-window { 
	overflow:auto!important;
	text-align:left!important;
}
div .damncrc-chat-window alt2,
span .damncrc-chat-outer alt2,
.damncrc-chat-inner {
	width:99%; 
	overflow-x:hidden!important;
}
damn-lo-row-flex, alt2, alt0-bottom-border, alt1-top-border {
	background:#EAF2EE!important;
}

div .dAmn-Chatbox section * ,
div #window .damn trailing section-block,
div #damn-chatspace .damn-chatspace,
div .damn-lo-row-flex alt1 alt1-border,
div .damncr,
div .damn-lo-row-flex alt0 alt0-border,
div .damn-lo-col-flex damncr-main alt0 alt0-border {
	background:#EAF2EE!important;
}
}
