css:
@-moz-document domain("weather.com") {
body,
.region,
.styles__allergyOutlook__3e1L4,
.lifestyle-allergy-indoor > * > * > .results,
.styles__historicalPollenChart__uBs6o,
.styles__forecast_graph__54bbA,
.y-axis-wrapper,
.styles__nearbyResorts__3luQ5,
.styles__articleNavigationContainer__2i63K,
.styles__articleLink__3Szgr:hover{
    color:#fff !important;
    background-color: #000 !important;
}

.region-secondary,
.region-content-top,
.region-content-middle,
.region-content-bottom{
    background-color:#0000 !important;
}

.styles__root__1t6_I{
    background-color:#000 !important;
    box-shadow:0 0 3px 1px rgba(255,255,255,.05) !important;
}

.today,
.hero-flex,
.section-page-name{
    color:#fff !important;
    background-color:#222 !important;
}

.video-module,
.article-wrapper > *,
.styles__nextArticle__1dZMD:hover,
.region-rail,
.region-main,
.region-secondary-rail,
.card,
.cm-module,
.cm-four-stack-v2,
.wx-media-content,
.tabs,
.tabs__label,
.panel,
.wx-media-group,
.wx-media-object,
.locations-title,
.styles__menu__1Rxmv,
nav,
.styles__nav__17Fst,
.print-btn{
    color:#fff !important;
    background-color:#222 !important;
}

.styles__nav__17Fst>li>a:hover{
    background-color:#000 !important;
}

.twc-table th{
    text-shadow:1px 1px 1px #000 !important;
}

.region-main .locations-title{
    box-shadow:0 0 3px rgba(255,255,255,.3) !important;
}

.twc-sticky-col,
.twc-table > * > * > .description,
.twc-table > * > * > .temp,
.twc-table > * > * > .feels,
.twc-table > * > * > .precip,
.twc-table > * > * > .humidity,
.twc-table > * > * > .wind,
.lifestyle-precip-soil-cmpnt{
    color:#fff !important;
    background-color:#333 !important;
}

.flex-container,
.solar-status__col,
.narrative,
.flex-container > * > * > *,
.flex-container > * > * > * > *,
.wxcard-daypart > *,
.wxcard-title{
    color: #fff !important;
    background-color: #000 !important;
}

.forecast-monthly__days >*,
.forecast-monthly__day-info__content,
.icon-close,
.dayCell,
.dayCell > *,
.dayCell > * >*,
.selected-month > *{
    color: #fff !important;
    background-color: #222 !important;
}

.forecast-monthly__calendar .selected .date{
    background-color:#3c8ed2 !important;
}

.icon-na > * > *{
    fill:#fff !important;
}

.video-description > .description,
.col-high,
.col-low,
.col-precip{
    color:#fff !important;
}

.bold-record{
    background-color:#333 !important;
}

.legend,
.wxcard,
.hi-temp,
.lo-temp,
.weather-cell{
    color:#fff !important;
    background-color: #222 !important;
}

.legend{
    box-shadow:1px 1px 1px 0 rgba(255,255,255,.15) !important;
}

.this-weekend,
.next-weekend{
    color:#fff !important;
    background-color: #333 !important;
}

.styles__displayBtn__YPvCm{
    color:#fff !important;
    background:#3c8ed2 !important;
}

.delivery-units{
    border-left:1px solid #222 !important;
}


.SevereAlertBar .dropdown a,
.SevereAlertBar .dropdown a:hover,
.SevereAlertBar .bg-alert-4 .timestamp{
    color:#fff !important;
}

.SevereAlertBar .bg-alert-4,
.SevereAlertBar .bg-alert-4:hover,
.styles__alertsBadge__1mKcX.styles__alert-4__3LRax{
    background-color: #c4813e !important;
    
}

.gm-sun-moon .moonphase,
ul.list-group.sun > * > *,
ul.list-group.moon > * > *{
    background-color:#222 !important;
}

ul.list-group.sun > * > * > *{
    color:#d5b63d;
}

.moonphase,
ul.list-group.moon > * > * > *{
    color: #3c8ed2 !important;
}

.pagination__activePage__lZqVU,
.styles__slideCounter__2fS3C,
.styles__description__2zhsx,
.styles__allergyOutlookHeaderBtnGroup__ogSTd > *,
.button:not(:disabled) > .iconset-arrows,
.button > .iconset-arrows,
.theme__iconMenu__2aMxm > *,
.iconset-ui:before,
.iconset-weather-data:before{
    color:#fff !important;
}

.button:disabled{
    color:#999 !important;
}

.info-button.filled,
.styles__filled__3N6mF{
    background-color:#555 !important;
}

.pagination__paginationWrapper__YAYhw > button:not(:hover) > .iconset-arrows,
.topNavigation__topNavigation__31lUd > button:not(:hover) > .iconset-arrows,
.lifestyle-precip-text,
.styles__wxTitleWrapTimestamp__12-cd{
    color:#ccc !important;
}

.styles__control_button__3n6Sd{
    background-color:#555 !important;
    box-shadow:0 0 7px rgba(255,255,255,.15) !important;
}

#Group-3 > path,
#email > path,
#facebook > * > path,
.label._wind,
.icon-svg,
.icon-font{
    fill:#fff !important;
}

.styles__articleLink__3Szgr p:after{
    background:#0000 !important;
}

.wx-ellipsis-inner:after{
    background:linear-gradient(90deg,hsla(0,0%,100%,0),#222 50%,#222) !important;
}

strong,
h1,
h2,
h3,
h4{
    color:#fff !important;
}

a{
    color:#fff !important;
}

/* underline links on hover
a:hover{
    text-decoration: underline !important;
    text-decoration-color: #fff !important;
}
*/

.region-footer{
    background-color: #3c8ed2 !important;
}

.twc-footer__social span{
    color:#fff !important;
    border:2px solid #fff !important;
}
}
