css:
@-moz-document domain("geekweek.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #4fa4db #1c1c1c;
}
input{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #4fa4db !important;
}
*{
    box-shadow: none !important;
}
.page{
    background: #1c1c1c;
}
.menu{
    background: #1c1c1c;
}
.adocean-billboard{
    margin: 0;
}
.section--slider,.news__time{
    background: #4fa4db !important;
}
.section{
    background: #1c1c1c;
}
.news__title,.section__content a,.nav__link{
    color: #4fa4db;
}
.news-more a,.section__header h3,.section__header h1,.polling__title,.polling__label,.news--target .news__description,.breadcrumbs__item,.nav--header .nav__list li a,.news__link:hover .news__title,.news__preview,.section__content a:hover,.footer__copyrights{
    color: #fff;
}
.nav--sub .nav__link:hover{
    background: transparent;
    color: #fff;
}
.button::after{
    filter: invert(65%) sepia(57%) saturate(1325%) hue-rotate(175deg) brightness(90%) contrast(90%);
}
.tag{
    background: #1c1c1c;
    border: 3px solid #4fa4db;
    color: #4fa4db;
}
.tag:hover,.breadcrumbs__item:last-child {
    color: #1c1c1c;
}
.footer__col--left .footer__copyrights:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #4fa4db;
}
}
