css:
@-moz-document domain("2cvforum.de")
{

body {
    font-size: 1.35em;
    padding-top: 30px;
}

.postbody .content {
    font-size: 1.05em;
}

.section-page, /* passende Beiträge unten auf der Seite */
.post-id, /* ID des Beitrags recht neben der Überschrift */
div#hot_topics1.panel.panel-danger.list-announcement, /* Sidebar Hot - Was ist hier los*/
div#poll8.panel.panel-inverse, /* Sidebar - Umfrage */
div#birthday5.panel.panel-info,/* Sidebar - Geburtstag */
div#lastbadges5.panel.panel-success ,/* Sidebar - AUszeichnungen */
div#users6.panel.panel-info,  /* Sidebar - Neue Entenfahrer */
div#posters7.panel.panel-info,  /* Sidebar - Top Mitglieder */
div#donators7.panel.panel-info, /* Sidebar - Spenden */
div#jumpbox.dropdown, /* Dropdown Forum wählen */
.badges_postrow, /* Auszeichnung unter User */
.status-wrapper /* Kilometerstand unter User */
{
    display: none;
}


/* Header Bild */
.flat-header {
    height: 0px;
}


/* Navigation angepasste */
.flat-nav {
    background-color: #c7cbd6;
}

/* .nav > li > a {
    padding: 10px 10px;
    padding-top: 10px;
    padding-bottom: 10px;
} */

  /* Navigations hover  */
 .navbar-nav-fancy > li::before {
    background:  #E8E8E8;
}
  
 
.fa.fa-asterisk::before, /* Astersik erster Beitrag */
.fa-sign-out::before, /* Pfeil letzter Beitrag */
.fa-paperclip::before /* Klammer für Anhang */
  {
  padding: 0.5em;
  }  


}
