css:
@-moz-document url-prefix("http://wikipediocracy.com/forum/"), url-prefix("https://wikipediocracy.com/forum/"), url-prefix("https://www.wikipediasucks.co/forum/") {



/* Font size */
.postbody .content {
    font-size: 1rem;
}
body {
    font-size: 0.8em;
}

/* Font */
.content, .message-box textarea,
h2, a.topictitle, a.forumtitle, input,
a.button1, a.button2, input.button1, input.button2, input.button3,
* {
	font-family: 'Roboto Slab', 'Noto Serif', Poppins /*sans*/, "Trebuchet MS" /*sans*/, Verdana /*sans*/, Helvetica /*sans*/, Arial /*sans*/, serif;
}

/* @import url('https://fonts.googleapis.com/css?family=Roboto+Slab&display=swap'); */
