/* Logo positioning */
.site_logo {
	width: 115px;
	transform: rotate(-15deg);
	transition: transform 0.35s cubic-bezier(0.2, 0.8, 0.3, 2);
}
.site_logo:hover {
	transform: none;
}
.site-description {
	padding-left: 0;
}


/* Full-width */
.wrap {
	max-width: none;
}
.post > .inner {
	display: flex; /* instead of floats */
}
.postbody {
	float: none;
	flex-grow: 1; /* take up all free space */
}
.postprofile {
	max-width: 20em;
	float: none;
	order: 1; /* flex: profile on right side */
}
.back2top {
	/* bottom right corner of post */
	position: absolute;
	bottom: 0;
	right: 0;
}
.back2top .top {
	float: none;
	margin: 0;
}


/* Light theme */
body, .wrap {
	background-color: hsla(0, 0%, 98%, 1);
}
.panel, .bg1 {
	background-color: hsla(0, 0%, 90%, 1);
}
.headerbar, .navbar:not(.navbar-top):not(.navbar-bottom),
.bg2.bg2 {
	background-color: hsla(0, 0%, 93%, 1);
}


/* Invert: ad-hoc dark mode */
body.invert {
	filter: invert(82%);
}
body.invert img {
	filter: invert(1);
}



} /* @-moz-document url-prefix */
