css:
@-moz-document url-prefix("https://www.cert.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #00ff82 #1c1c1c !important;
}
nav.main .logo{
    background: #fff;
    border: 2px solid #00ff82;
}
nav.main a{
    color: #fff;
}
.content{
    color: #fff;
}
h2{
    color: #fff;
}
.before_head.border{
    color: #fff;
}
.tag-cloud-link{
    color: #fff;
}
aside .social .other{
    padding: 18px 105px 2px 0;
}
footer{
    background: #202020;
}
.row{
    color: #fff;
}
.alignnone.wp-image-15165{
    background: #fff;
}
.head h6,.head a,.head h2{
    color: #089252 !important;
}
.slider_blog_footer{
    background: transparent;
}
.slider_blog_footer .slick-prev,.slider_blog_footer .slick-next{
    filter: invert(100%);
}
.cert_report_baner .inner .bottom,.cert_header_bg h1{
    background: #00ff82;
}
.content h3,.content h2,.cert_right h4{
    color: #00ff82;
}
.widget_tag_cloud h3{
    color: #089252 !important;
    border-top: 1px solid #00ff82 !important;
    border-bottom: 1px solid #00ff82 !important;
}
.breadcrumbs a{
    color: #00ff82;
}
nav.main .mag{
    filter: invert(100%);
}
main.training .tab .header h3,main.job .header h2{
    background: #101C28;
}
main.training .tab .header::after{
    filter: invert(100%);
}
main.ouch figure.item .title{
    background: #00ff82;
}
.cert_tools_submit,div.cert_sort select{
    color: #00ff82;
    border: 1px solid #00ff82;
}
.dropdown .public option{
    background: #101C28;
}
main.front article,main.front article figure.content .head{
    border-bottom: 1px solid #00ff82;
}
.breadcrumbs hr{
    border-top: 1px solid #00ff82;
}
/*FOOTER*/
div.footer_copy .row{
    text-align: -moz-right;
}
div.footer_copy .row a{
    color: #fff;
}
div.footer_copy .row:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #00ff82;
    margin-right: 15px;
}
/*FOOTER*/

}

@-moz-document url-prefix("https://incydent.cert.pl/") {
body{
    background: #1c1c1c;
}
.form-block-dark{
    background: #1c1c1c;
    border-bottom: 1px solid #089252;
}
.form-block h2,.form-block h4,.report-form{
    color: #089252;
}
.form-block p{
    color: #fff;
}
.alert-info{
    background: transparent;
}
.text-muted:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #089252;
}
}
