@-moz-document domain("youtube.com") {
::-webkit-scrollbar,div::-webkit-scrollbar{width:10px}::-webkit-scrollbar-thumb,div::-webkit-scrollbar-thumb,yt-live-chat-header-renderer,yt-live-chat-message-input-renderer,ytd-author-comment-badge-renderer{background:#222}::-webkit-scrollbar-thumb:hover,div::-webkit-scrollbar-thumb:hover{background:#444}#guide-inner-content.ytd-app::-webkit-scrollbar-thumb{background:#1d1d1d}#container.ytd-searchbox,#masthead,#masthead-container{background:0 0}#container.ytd-searchbox{background:#000000a3}app-drawer:not([persistent]) #guide-content.ytd-app{backdrop-filter:blur(1px);background:#000000a3!important}#guide-content.ytd-app{background:0 0!important;box-shadow:2px 0 1px -1px #303030}#container.style-scope.ytd-masthead{backdrop-filter:blur(1px);background:#00000059;box-shadow:0 0 0 1px #303030}paper-listbox{background:#171717}#guide-inner-content.ytd-app::-webkit-scrollbar-track,yt-live-chat-renderer{background:#151515}ytd-mini-guide-entry-renderer,ytd-mini-guide-renderer{background:0 0;box-shadow:2px 0 0 -1px #303030}#channel-header.ytd-c4-tabbed-header-renderer,#tabs-inner-container.ytd-c4-tabbed-header-renderer,ytd-multi-page-menu-renderer[background-color-update],ytd-simple-menu-header-renderer{background:#141414}#tabs-inner-container.ytd-c4-tabbed-header-renderer{box-shadow:0 2px 0 -1px #303030}.sbdd_a{z-index:2020}.sbdd_b{backdrop-filter:blur(2px);background:#000000a3;border:0;box-shadow:0 0 1px 2px #0d0d0d;left:1px;outline:1px solid #2d2d2d;position:relative;top:-9px}.sbsb_a{background:0 0;border:0}.sbsb_b li{font-size:1.8rem;padding:0}.sbsb_b li .sbse{box-sizing:border-box;height:100%;padding:8px 26px 8px 10px;width:100%}.sbsb_a,.sbsb_c{padding:0}.sbsb_c.gsfs.sbsb_d{backdrop-filter:blur(1px);background:0 0;box-shadow:0 0 0 1px #3d3d3d}.sbqs_c{color:#ccc}ytd-searchbox[has-focus] #container.ytd-searchbox{border-color:#303030}ytd-section-list-renderer:not([page-subtype=history]):not([page-subtype=memberships-and-purchases]):not([page-subtype=ypc-offers]) #contents.ytd-section-list-renderer>.ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer):not([item-dismissed]).ytd-section-list-renderer{border-color:#151515}ytd-guide-collapsible-section-entry-renderer.ytd-guide-section-renderer:not(:first-child),ytd-guide-renderer[is-guide-refresh] #sections.ytd-guide-renderer>.ytd-guide-renderer:not(:last-child){border-color:#222}ytd-multi-page-menu-renderer[background-color-update]{border:1px solid var(--yt-spec-10-percent-layer)}#grid-title.ytd-rich-grid-renderer{margin-bottom:4px;margin-top:22px}#grid-title.ytd-rich-grid-renderer span,#title.ytd-shelf-renderer{font-size:26px;height:26px;max-height:unset}#meta.ytd-rich-grid-video-renderer{padding:6px 0 0}#avatar-link.ytd-rich-grid-video-renderer{margin:-40px 0 0 -10px;position:absolute}#avatar.ytd-rich-grid-video-renderer{border:1px solid #ffffff73;box-shadow:0 0 5px #0d0d0d;height:48px;width:48px}yt-img-shadow.ytd-guide-entry-renderer,yt-img-shadow.ytd-guide-entry-renderer img{height:32px;width:32px}img.yt-img-shadow,ytd-thumbnail #thumbnail.ytd-thumbnail{border-radius:4px}img.yt-img-shadow{max-width:calc(100% + 2px)}ytd-rich-item-renderer{margin-bottom:25px}#card.yt-live-chat-viewer-engagement-message-renderer,.style-scope.ytd-page-manager,::-webkit-scrollbar-track,div::-webkit-scrollbar-track,ytd-app,ytd-browse[background-color-update][page-subtype=channels],ytd-playlist-sidebar-renderer[background-color-update],ytd-thumbnail.ytd-rich-grid-video-renderer:before{background:#111}.sbfl_a,.sbsb_b li:last-child{display:none}
}
