css:
@-moz-document domain("rutracker.org") {
:root {
  --color1: #1d1d1d;
  --color2: #999;
  --color3: #ccc;
  --color4: #151515;
  --color5: #111;
  --color6: #888;
  --color7: #ccc;
  --color8: rgba(90, 90, 99, .25);
  --color9: rgba(27, 27, 27, 1);
  --color10: rgba(27, 27, 27, 1);
  --color11: rgba(27, 27, 27, 1);
}
::-webkit-scrollbar,
div::-webkit-scrollbar {
  width: 10px;
}
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track,
div::-webkit-scrollbar-track {
  background: #0d0d0d;
}
::-webkit-scrollbar-thumb,
div::-webkit-scrollbar-thumb {
  background: #222;
}
::-webkit-scrollbar-thumb:hover,
div::-webkit-scrollbar-thumb:hover {
  background: #444;
}
#body_container,
#page_container,
.menu-a,
.news_title,
.q,
.sp-body,
body,
input,
optgroup option,
select,
td.cat.pad_2,
textarea {
  background-color: var(--color1) !important;
  background-image: none;
}
.menu-a a:hover,
.news_date,
.row1,
.row1 td,
.row4,
.row4 td,
.row5,
.row5 td,
.sb2-block,
.sp-wrap,
optgroup,
table.forumline {
  background-color: var(--color4) !important;
  background-image: none;
}
#cse-search-btn-top,
.bordered th,
.cat,
.cat_title,
.forumline th,
.row3,
.row3 td,
.spaceRow,
.thHead,
div.t-tags span,
input[type=submit],
option:hover,
td.cat,
td.catBottom,
td.catHead,
td.catTitle,
tr.hl-tr:hover td {
  background-color: var(--color5) !important;
  background-image: none;
}
input[type=submit]:hover {
  background-color: var(--color8) !important;
}
input[type=submit]:active {
  background-color: var(--color9) !important;
}
div.t-tags span:hover {
  background: var(--color8) !important;
}
.a-like.med,
.f,
.fieldsets div,
.fieldsets div > p,
.gen,
.gensmall,
.med,
.small,
a.gen,
a.gensmall,
a.med,
a.small,
a.small.tr-dl,
p.tCenter a.gen {
  background: 0 0;
}
.folded,
.folded2 {
  background-color: var(--color4) !important;
}
.row2,
.row2 td,
.menu-sub table th, #fs--1 {
  background-color: var(--color11) !important;
}
#page_container,
.c-head,
.gen,
.gensmall,
.med,
.new .dot-sf,
.news_date,
.news_title,
.poster_info em,
.q,
.q-head,
.q-head span,
.row1,
.row1 td,
.sb2-block,
.site-nav,
.small,
.sp-body,
.sp-head span,
.topmenu,
body,
div.t-tags span,
input,
legend,
optgroup option,
select,
span.brackets-pair,
span.p-color,
textarea {
  color: var(--color2) !important;
}
#idx-sidebar2 h3,
#latest_news h3,
#sidebar1 h3,
.catTitle,
.cat_title a,
.forumline th,
.maintitle,
.pagetitle,
.poster_info p,
a.tLink,
a:hover .brackets-pair,
optgroup,
td.topicSep,
.topmenu option {
  color: var(--color3) !important;
}
#latest_news a,
.a-like,
.nick,
.nick a,
.nick-author,
.nick-author a,
a {
  color: var(--color6) !important;
}
#latest_news a:hover,
.a-like:hover,
.site-nav a:active,
.site-nav a:hover,
a:active,
a:focus,
a:hover {
  color: var(--color7) !important;
}
.dlComplete,
.seed,
.seedmed,
.seedsmall {
  color: #7cb342;
}
.dlDown,
.leech,
.leechmed,
.leechsmall {
  color: #ef5350 !important;
}
[style='color: brown;'] {
  color: #e57373 !important;
}
.menu-a,
input,
select {
  border-radius: 0;
}
.topmenu {
  border-radius: 0;
}
#fs-main,
.bordered td,
.bordered th,
.c-body,
.cat_title,
.forumline td,
.forumline th,
.forums td.row1,
.menu-a,
.news_date,
.post_btn_2,
.q,
.sb2-block,
.sp-body,
.sp-head,
.sp-wrap,
.topic .td1,
.topic .td2,
.topic .td3,
fieldset,
hr,
input,
select,
table.bordered,
table.borderless .bordered td,
table.borderless .bordered th,
table.forumline,
table.topic,
textarea,
.menu-sub table,
.signature::before {
  border-color: #262626 !important;
}
div.t-tags span,
div.t-tags span:hover {
  border-color: var(--color8) !important;
}
option {
  border-color: var(--color4) !important;
}
.bw_TRL {
  border-width: 0 !important;
}
input,
textarea {
  border: solid 1px;
}
.post-box {
	border: none!important;
	background: #151515!important;
}
.news_title {
  background: transparent url(data:image/png;base64,) no-repeat left top;
}
#idx-sidebar2 h3,
#sidebar1 h3 {
  background: transparent url(data:image/png;base64,) no-repeat left bottom;
}
#adriver-240x120,
#bn-bot-wrap,
#bn-idx-3,
#bn-idx-marathonbet,
#idx-sidebar2 iframe,
#logo,
.bn-idx,
table.w100 iframe,
td.bn-topic {
  display: none;
}
.topmenu {
  text-shadow: 0 0 15px #000;
  background-color: #000000bf;
  border-color: #555;
}
.topmenu #search-text,
.topmenu #search-text-guest,
form#login-form-quick[type=password],
form#login-form-quick[type=text] {
  padding: 0 12px;
}
.topmenu #search-menu {
  margin-right: 2px;
  padding: 0 5px;
}
.topmenu #search-submit .topmenu #cse-search-btn-top {
  width: 70px !important;
}
.topmenu input,
.topmenu select {
  height: 30px;
}
.cat_title {
  padding: 18px 18px 17px;
  font-size: 15px;
}
#main-nav {
  background: url(data:image/png;base64,) 29px 9px no-repeat;
}
a.tLink:hover,
a.topictitle:hover,
a.torTopic:hover {
  text-decoration: none !important;
}
input,
select,
textarea {
  height: 40px;
  padding: 10px;
  font-size: 14px;
  outline: 0 !important;
}
div.t-tags span {
  text-shadow: none;
}
.news_date {
  border-radius: 7px 0 0 0;
}
.topmenu {
  position: fixed;
  top: -12px;
  left: -10px;
  z-index: 99;
  margin: 12px 0 0;
  padding: 8px 16px;
  border: none;
  box-shadow: 0 0 5px #000;
}
#page_footer a img,
.nav-natz a img {
  opacity: .15;
}
#page_footer a:hover img,
.nav-natz a:hover img {
  opacity: 1;
}
#main-nav {
  position: fixed;
  top: 10px;
  left: -20px;
  z-index: 999;
}
#page_header {
  margin-top: 0;
  margin-bottom: 30px;
}
#logged-in-username {
  color: #ccc !important;
}
.ped-editor-buttons .buttons-row input[type=button] {
  text-shadow: none;
  background: 0 0;
  box-shadow: none;
}
#tr-submit-btn {
  width: 200px !important;
  height: 40px;
}
.attach .tCenter:not(.row3) a {
  color: #00b600 !important;
  font-size: 22px;
  line-height: 1.1;
}
.topmenu #search-text {
  width: 500px;
  height: 40px;
  padding: 15px;
  color: #ccc !important;
  font-size: 20px;
}
.topmenu #search-menu,
.topmenu input[type=button],
.topmenu input[type=submit] {
  width: 120px;
  height: 40px;
  font-size: 16px;
}
.topmenu #search-text {
  margin-right: 2px;
}
#title-search {
  color: #ccc !important;
}
.category table.forums {
  border-left: 1px solid #262626;
}
.cat_title,
.t-top-buttons-wrap.row3.med.bold.hide-for-print {
  border: 1px solid #262626;
}
#fs-main {
  width: 700px !important;
  height: 500px !important;
}
.nav.pad_6.row1 {
  background: #0000008a !important;
}
#pagination .nav,
.bottom_info .nav {
  position: fixed;
  right: 0;
  bottom: -5px;
  left: -10px;
  z-index: 99;
  padding: 3px 10px;
  font-size: 16px;
  text-shadow: 0 0 15px #000;
  background: #00000070;
  box-shadow: 0 0 5px #000;
}
#pagination .nav p,
.bottom_info .nav p {
  margin: 10px;
}
#pagination .nav p,
.bottom_info .nav p {
  margin: 10px;
}
#page_content {
  padding: 40px 10px 0;
}
.row7[style] {
  background: #111 !important;
}
.forums td,
.hl-tr {
  height: 50px;
}
.sp-body img:hover {
  position: relative;
  z-index: 9;
  transform: scale(2);
}
.top-menu-username-wrap {
  position: absolute;
  top: 20px;
  right: 280px;
}
.internal-promo-text-top {
  display: none;
}
.w100.vMiddle .small.bold {
  margin-top: 0 !important;
}
.t-note .note-html,
.t-note .note-text {
  background: 0;
  border: 1px solid #a5afb4;
}
.site-nav {
  min-width: 0;
  padding: 8px 0 6px 110px;
}
.site-nav .floatR {
  display: none;
}
.site-nav .floatL li:first-child {
  position: absolute;
  top: 8px;
  left: 25px;
}
.site-nav .floatL li:first-child b {
  color: transparent;
  font-size: 18px;
}
.menu-split a:hover {
  color: #ccc !important;
}
.menu-split .menu-root img,
.pad_2.hide-for-print img,
img.forum_icon,
img.icon1,
img[alt="–û—Ç–≤–µ—Ç–∏—Ç—å"],
img[alt="–ù–æ–≤–∞—è —Ç–µ–º–∞"],
img[alt="–°–∫–∞—á–∞—Ç—å .torrent"],
.log-out-icon,
img.topic_icon,
li.dir > div:before,
li.file,
input[type="checkbox"], input[type="radio"] {
  filter: invert(1);
}
#tor-size-humn,
.borderless.bCenter.pad_8,
.gen td,
.leech,
.seed {
  font-size: 16px;
}
.top-alert {
  margin-top: 70px;
}
.small.f-dl.dl-stub {
  font-size: 12px;
}
.vf-col-tor .seedmed {
  font-size: 12px;
}
.vf-col-replies .med {
  color: #ccc !important;
  font-size: 12px;
}
li.dir > div s {
  color: #bd4e17;
}
li.dir > div:hover,
.a-like:hover,
ul.a-like-items > li:hover {
  color: #ccc !important;
}
#tor-fl-wrap,
#tor-filelist,
.menu-sub table td,
.menu-sub table {
  background: #1d1d1d;
}
#tor-filelist {
  background: #1d1d1d;
  border: none;
}
.ttp-label.ttp-hot {
  background: #f50;
}
.ped-buttons-row {
	line-height: unset !important
}
.ped-buttons-row input[type="button"] {
	background: var(--color5);
}
.ped-buttons-row input[type="button"]:active {
	background: linear-gradient(#0d0d0d 0%, #0d0d0d 100%)
}
.ped-editor select {
	background: var(--color5);
}
.cat_title a:hover {
	background: #151515;
	color: #ccc !important;
}
.cat_title, .t-top-buttons-wrap.row3.med.bold.hide-for-print {
	border-bottom-color: transparent
}
.post_head {
	border-bottom-color: #222
}
.soc-buttons span {
	margin: 7px 5px
}
.input-buttons select, .input-buttons input[type="text"], .input-buttons input[type="button"], .input-buttons input[type="submit"] {
	height: unset;
}
@media (max-width: 1720px) {
  .topmenu #search-text {
    width: 250px;
  }
}

@media (max-width: 1450px) {
  #page_content {
    padding: 80px 10px 0;
  }
  .topmenu {
    padding: 20px 0 54px 0;
  }
  form#quick-search {
    position: absolute;
    top: 40px;
    left: 0;
    justify-content: center;
    width: 100%;
    padding: 10px;
  }
  .top-menu-username-wrap {
    top: 22px;
  }
}

@media (max-width: 770px) {
  #main-nav {
    top: 20px;
    width: 110%;
  }
  .site-nav {
    padding: 2px 0 3px 110px;
  }
  .topmenu {
    padding: 3px 0 71px 0;
  }
  .top-menu-username-wrap {
    top: 2px;
  }
}

@media screen and (max-width: 950px) {
  #search-submit {
    max-width: unset;
  }
}
}
