css:
@-moz-document domain("cybersport.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #99161b #1c1c1c !important;
}
/*STRONA-GLOWNA*/
.section{
    background: #1c1c1c;
}
.section.is-grey{
    background: #1c1c1c;
}
.section .content{
    background: #1c1c1c;
}
.post .title a,.sidebar-widget .title,.container h2,.match-data strong,.galleries article.gallery h4 a,.videos article.video a{
    color: #99161b;
}
.relation-title a,.post-meta,.post-content h4,.post-date,.match-data span,.match-data,.match-data a{
    color: #fff;
}
.post-navigation{
    background: transparent;
}
/*STRONA-GLOWNA*/
/*ARTYKUL*/
.post.is-single h1{
    color: #99161b;
}
.post-body p,.sidebar-author .author-media .author-details h5 a,.sidebar-author .author-media .author-details p a{
    color: #fff;
}
.sidebar-widget .title.is-grey{
    background: #99161b;
    color: #fff;
}
/*ARTYKUL*/
/*RELACJE*/
.post .title{
    color: #99161b;
}
.table-results tbody td{
    color: #fff;
}
table.table-results tbody tr:nth-child(2n){
    background: #99161b;
}
.table-results p strong,.table-results strong span{
    color: #99161b;
}
.relation tbody td{
    color: #fff;
}
/*RELACJE*/
.post-bottom{
    color: #fff;
}
/*KONTAKT*/
.title{
    color: #99161b;
}
.contact-person strong,.contact-person span{
    color: #fff;
}

/*KONTAKT*/
/*SEARCH*/
nav .search .search-form{
    background: #99161b;
}
nav .search .search-form .button{
    border: 2px solid #1c1c1c;
}
/*SEARCH*/
/*RANKING*/
.table-results tbody tr td{
    background: #1c1c1c !important;
}
.post-body ul li{
    color: #fff !important;
}
/*RANKING*/
.sidebar-author .author-media .author-details h5{
    color: #fff;
}
.footer-right .copyright:before{
    content: "Ciemny motyw by Maciejozaman ";
}
}
