css:
@-moz-document domain("coda.io") {
/* Our CSS hangs out here: 
https://cdn.coda.io/assets/browser.b726145b4bc4d169ebf6.css
*/

/*Core paragraph style */

body {
    --font-size-smaller: 12px;
    --font-size-small: 13px;
    --font-size-normal: 14px;
    --font-size-large: 15px;
    --font-size-title: 18px;
    --font-size-hero: 30px;
    --font-size-h0: 36px;
    --font-size-h1: 28px;
    --font-size-h2: 22px;
    --font-size-h3: 18px;
    --font-line-height-standard: 18px;
    --font-line-height-large: 22px;
    --font-line-height-hero: 36px;
    --font-line-height-h0: 100px;
    --font-line-height-h1: 100px;
    --font-line-height-h2: 40px;
    --font-line-height-h3: 30px;
    --font-weight-light: 300;
    --font-weight-regular: 400;
    --font-weight-medium: 500;
    --font-weight-semi-bold: 600;
    --font-weight-bold: 700;
    font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen-Sans, Ubuntu, Cantarell, 'Helvetica Neue', 		sans-serif;
    font-size: var(--font-size-normal);
    line-height: var(--font-line-height-standard);
    font-weight: var(--font-weight-regular);
    flex: 1 1 0;
    min-height: 0;
    min-width: 0;
    display: flex;
    flex-direction: column;
    background: white;
    border: 0;
    color: black;
    margin: 0;
    overflow-y: hidden;
    padding: 0;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    -webkit-text-decoration-skip: ink;
    text-decoration-skip: ink;
}


/*
__________________

Sections of the app styles below

__________________
*/



/* **CANVAS writing** */

/*Section Title*/
.canvas_header_view--title--676ddfH8 {
    min-height: 0;
    min-width: 0;
    font-size: 30px;
    line-height: 150%;
    color: #FFC354;
    font-family: sans-serif;
    text-transform: uppercase;
    font-weight: 300;
    

	}

/* Lines are the paragraph styles */
.kr-line {
    margin-top:20px;
	}

.kr-line.kr-h1,
.kr-line.kr-h2,
.kr-line.kr-h3
{
    font-family: 'Calibre-R';
}


.kr-line.kr-h1{
    margin-bottom: 30px;
    margin-top: 40px;
}

.kr-line.kr-h2{
    margin-bottom: 20px;
    margin-top: 30px;
}

.kr-line.kr-h3{
    margin-bottom: 10px;
    margin-top: 20px;
}

/*Section body*/
.canvas_section--section--30iWxVOS {
    
    --font-size-smaller: 20px;
    --font-size-small: 20px;
    --font-size-normal: 25px;
    --font-size-large: 25px;
    --font-size-title: 50px;
    --font-size-hero: 60px;
    --font-size-h0: 60px;
    --font-size-h1: 50px;
    --font-size-h2: 40px;
    --font-size-h3: 30px;
    --font-line-height-standard: 30px;
    
    /*large is the normal text line height */
    --font-line-height-large: 35px;
    --font-line-height-hero: 36px;
    --font-line-height-h0: 60px;
    --font-line-height-h1: 50px;
    --font-line-height-h2: 40px;
    --font-line-height-h3: 30px;
    --font-weight-light: 300;
    --font-weight-regular: 400;
    --font-weight-medium: 500;
    --font-weight-semi-bold: 600;
    --font-weight-bold: 700;
    font-family: PT Serif;
    font-size: var(--font-size-normal);
    line-height: var(--font-line-height-standard);
    font-weight: var(--font-weight-regular);
    flex: 1 1 0;
    min-height: 0;
    min-width: 0;
    display: flex;
    flex-direction: column;
    
    /*This changes the canvas background color*/
    background: #FFFDF9;
    border: 0;
    
    /*This doesn't change the font color */
    color: blue;
    margin-bottom: 0px;
    overflow-y: hidden;
    padding: 0;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    -webkit-text-decoration-skip: ink;
    text-decoration-skip: ink;
}

/*changes the color of the body text */
.canvas_ranges_editor--canvas--2SZABG_D{
    color: #212121;
}

/* List styles */

.kr-ulist {
    margin-top: 10px;
}

.block-level-1 {
     margin-top: 10px;
}

.block-level-2 {
     margin-top: 0px;
}


/* Code snippet aka the explanation box */
.kr-line.kr-code {
    background: #F7F6F3;
    border: 0px;
    opacity:.8;
    font-family: 'Calibre-R';
	font-style: italic;
    width:60%;
    margin-top: 10px;
    margin-bottom: 10px;
                                      
}





/*These two are for the recommendations in a new section*/
.canvas_suggestions--root--1KmP-Hk3{
    visibility: hidden; 
    
}

.placeholder_text_caret_decorator--shared--m_qZ_UY1 {
    visibility: hidden; 
}

/*Bottom of the canvas padding*/
body:not(.isMobile) .canvas_ranges_editor--footer--1FI-8uBZ {
    padding-bottom: 50%;
}


/* 
______________
TABLES (grids)
___________
*/

.grid--root--vC2eX3Ij {
    font-family: sans-serif;
    background-color: #FFFDF9;
    	}	

.row_cell--root--3ja1lIy0 {
    font-size: 15px; 
    line-height: 20px;
    background-color: #FFFDF9;
	}


.column_header_cell--root--2R_RE2AN:not(.column_header_cell--rightAlign--1LgJ21gJ) .column_header_cell--title--1mQ_5dEM:not(.column_header_cell--focused--1CyNtlcy) {
    font-size: 15px; 
    background-color: #FFFDF9;
    
}

.grid_title--title--2kGaCBGJ {
    color: gray;
    background-color: #FFFDF9;
    
}


.aggregate_row--container--3Rw5z1MI {
      visibility: hidden;  
    height:0px;
    width:0px;
    margin:0px;
}


/* 
___________
SECTION LIST
____________
*/
.section_list_item--root--1RhaukoO {
    display: flex;
    flex: 1 1 0;
    min-height: 40px;
    min-width: 0;
    flex-basis: inherit;
    align-items: center;
    font-size: 15px;
    font-weight: var(--font-weight-regular);
    position: relative;
    opacity: 1;
    color: grey;
	}

.add_section_button--addButtonContainer--3ZoezOov {
    visibility: hidden;  
	}

.simple_text_input_view--placeholder--1NfJDiTp {
    visibility: hidden;
}




/*
_______
TOP NAV
_________
*/


.title--textInput--17rnHTGB {
	visibility: hidden;
	}

.roster--roster--2FfR52fP{
	visibility: hidden; 
	}

.title--icon--2EhFmDq3 {
	visibility: hidden;
	}



.title_bar--root--KjDBqrRl {
    background: #FBF7F5;
    opacity: 0.6;
}

/*The red insert button */
.insert_object_menu--root--2UwW55fd {
    align-items: center;
    background-color: #F6E9DE;
    display: flex;
    justify-content: center;
}



/* 
_________
Right rail
__________
*/
.document--commentRail--2OUU9WkT {
    visibility:hidden;
      background: #FFFBEF;
}

.document--rightContentsContainer--1ukMDw6X{
    visibility:hidden;
    background: #FFFBEF;
    
}

.document--rightContentsContainer--1ukMDw6X.document--collapsed--z0_7RCYy {
    visibility:hidden;
      background: #FFFBEF;
    
}

/* Help button */
.help_button--button--4Ly1iuWp{
    visibility:hidden;
}


/* Better indented list coming from
https://userstyles.org/styles/161421/coda-io-better-indented-lists 
*/


/* For addition levels, +22 from previous */
  .kr-indented.block-level-2, .kr-olist.block-level-2, .kr-ulist.block-level-2
  {
    border-left-width: 60px;
  }

  .kr-indented.block-level-3, .kr-olist.block-level-3, .kr-ulist.block-level-3
  {
    border-left-width: 82px;
  }

  .kr-indented.block-level-4, .kr-olist.block-level-4, .kr-ulist.block-level-4
  {
    border-left-width: 104px;
  }

  .kr-indented.block-level-5, .kr-olist.block-level-5, .kr-ulist.block-level-5
  {
    border-left-width: 128px;
  }


/*
___________
Typeface
___________
*/

/*COLORS!*/

/*fonts*/

.kr-red-dark-fg {
    color: #F46A54;
    fill: #F46A54;
}

.kr-orange-dark-fg {
    color: #EA9562;
    fill: #EA9562;
}

.kr-yellow-dark-fg {
    color: #F6A524;
    fill: #F6A524;
}

.kr-green-dark-fg {
    color: #73C886;
    fill: #73C886;
}


.kr-blue-dark-fg {
    color: #0D868F;
    fill: #0D868F;
}

.kr-purple-dark-fg {
    color: #A170B8;
    fill: #A170B8;
}

.kr-pink-dark-fg {
    color: #DB67AC;
    fill: #DB67AC;
}


.kr-gray-dark-fg {
    color: #8E8E8E;
    fill: #8E8E8E;
}

/*highlights*/

.kr-red-light-bg {
    background-color: #FEA596;
}

.kr-orange-light-bg {
    background-color: #F6BE96;
} 


.kr-yellow-light-bg {
    background-color: #FFC969;
} 

.kr-green-light-bg {
    background-color: #94DDB6;
} 

.kr-blue-light-bg {
    background-color: #D6E8FA;
} 

.kr-purple-light-bg {
    background-color: #D8C3FF;
} 

.kr-pink-light-bg {
    background-color: #FEC0E5;
} 

.kr-gray-light-bg {
    background-color: #DED8DB;
} 
    
    

/* Calibre-R typeface */
/* https://developer.mozilla.org/en-US/docs/Web/CSS/@font-face */

@font-face {
  font-family: 'Calibre-R';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Bold.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Bold.woff') format('woff');
  font-weight: 700;
  font-style: normal;
}
@font-face {
  font-family: 'Calibre-R';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Semibold.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Semibold.woff') format('woff');
  font-weight: 600;
  font-style: normal;
}
@font-face {
  font-family: 'Calibre-R';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Medium.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Medium.woff') format('woff');
  font-weight: 500;
  font-style: normal;
}
@font-face {
  font-family: 'Calibre-R';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Regular.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Regular.woff') format('woff');
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: 'Calibre-R';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Light.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/Calibre-R-Light.woff') format('woff');
  font-weight: 300;
  font-style: normal;
}
@font-face {
  font-family: 'codaicons';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/codaicons.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/codaicons.woff') format('woff');
  font-weight: normal;
  font-style: normal;
}

@font-face {
  font-family: 'Calibre';
  src: url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/calibre-mediumitalic.woff2') format('woff2'), url('https://cdn.coda.io/assets/f9ceff65ec18/fonts/calibre-mediumitalic.woff') format('woff');
  font-weight: 500;
  font-style: italic;
}










}
