css:
@-moz-document domain(theonegenerator.com) {
    div#app-content {
        background-color: #353b48;
    }

    .card {
        background-color: #34495e;
        color: #ffffff;
    }

    button.btn {
        color: #ffffff;
    }

    .generator-output input {
        background-color: #415c76;
    }

    section.topbar-menu.topbar-desktop {
        border-left: none;
        background-color: #434b5b;
    }
    /*section.topbar-menu.topbar-desktop .container-fluid {
        background-color: #434b5b;
    }*/

    .breadcrumb {
        background-color: transparent;
    }
    .breadcrumb-item.active {
        color: #fff;
    }

    section.sidebar {
        background-color: #252932;
    }

    .sidebar-menu .menu-header:hover {
        background-color: #2e323e !important;
        color: #eee !important;
    }
    .sidebar-menu .menu-header.active {
        background-color: #2b303b !important;
        color: #ffffff !important;
    }

    .form-control {
        background-color: #415c76 !important;
        color: #eee !important;
    }
    .form-control:hover {
        border-color: #dbe3eb;
    }
    .form-control:focus {
        border-color: #ecf0f1 !important;
        background-color: #415c76 !important;
        color: #eee !important;
    }

    .generator-menu-snippet {
        background-color: #50596d !important;
        color: #ffffff;
    }

    .app-version {
        background-color: #2b303b;
    }

    .logo .the-one-logo {
        color: #ffffff !important;
    }

    .list-group .list-group-item {
        background-color: #2b303b;
        color: hsla(0, 100%, 100%, 0.8);
    }


    .list-group .list-group-item.list-group-item-action:hover {
        background-color: #3e4656;   
    }
    .list-group .list-group-item.list-group-item-secondary {
        background-color: #242932;   
    }
}
