/* ----- ----- ----- ----- ----- ----- ----- ----- ----- -----
   Card Labels
   ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- */
  .card-label-red
  {
    background-color: var(--LB1) !important;
  }

  .card-label-orange
  {
    background-color: var(--LB2) !important;
  }

  .card-label-yellow
