css:
@-moz-document url-prefix("https://catbox.moe"), domain("catbox.moe")
{

img {
    display: none !important;
}

table {

	width: 90%;
}

td > p {
	margin-bottom: 0px;
	text-align: center;
}

td > a {
	font-size: 18px;
}

td.thumbnail {
	text-align: center;
	width: 15%;
}

div.image {display: none !important}

video.previewimg {
    max-width: 100%;
}

body {

	background-image: url("http://static3.grsites.com/archive/textures/cloth/cloth010.jpg") !important;
	font-family: 'Courier New' !important;
    color: #fff !important;
    text-shadow: #000 2px 2px 2px !important;
     
}

.welcome {

	color: #000 !important;
	text-align: center;
	font-size: 64px;
	padding: 5px;
	
}

.welcomesub {

	padding: 15px;
	color: #fff !important;
	text-align: center;
	font-size: 22px;
}

.greeting {
	font-size: 24px; 
	margin-top: -25px;
}

.linkrow {

	font-size: 10px;
	margin-top: 5px;
    color: #fff !important;
}

.loginstatus {

	font-size: 14px; 
	margin-top: 5px; 
	margin-bottom: 20px;
	font-style: italic;
}

.hometab {

	width: 10%;
	height: 22px;
	padding: 2px;
	border: 2px dashed #000 !important;
	border-radius: 15px;
	text-align: center;
	position: absolute;
	background-image: url("https://files.catbox.moe/cxy672.gif") !important;
	font-size: 18px;
    
}

.hometab:nth-of-type(2) {

	top: 46px;

}

.footer {

	position: fixed;
	bottom: 0px;
	width: 100%;
	margin-top: 85px;
	z-index: 1;
	font-size: 18px; 
	text-align: center;
}

.urlUploads {

	text-align: center !important;
    font-weight: bold !important;
    background-image: url("https://files.catbox.moe/cxy672.gif") !important;
   	margin-left: 38% !important;
	margin-right: 38% !important;
    border-radius: 15px !important;
   	
}              

form.genericform {

	width: 70%;
	padding: 35px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 16px;
	overflow: hidden;
	
}

form.urlUploadForm {

	width: 45%;
	margin-left: auto;
	margin-right: auto;
	text-align: center;
	padding: 5px 5px 5px 5px;
}

form a {

	color: #000 !important;
	text-decoration: none;

}

input[type=text] { 

	width: 45%;
	height: 25px;
	font-size: 16px;

}

input[type=number] {
	background-color: #AA8 !important;
	border: 2px dashed #000 !important;
	width: 45%;
	height: 25px;
	font-size: 16px;
	font-family: "Courier New" !important;
}


input[type=text].stylized { 

	background-color: #AA8 !important;
	border: 2px dashed #000 !important;;
background-image: url("https://files.catbox.moe/cxy672.gif") !important;
}

input[type=text]:focus { 

	outline: 0;

}

textarea {

	background-color: #A0A0A0 !important;
	border: 2px dashed #000 !important;
	width: 45%;
	font-size: 14px;
	font-family: "Courier New" !important;

}

input[type=password] { 

	background-color: #E0E9C0 !important;
	border: 2px dashed #000 !important;
	width: 35%;
	height: 25px;
	font-size: 18px;
}

input[name=url] { 

	width: 50%;
	background-color: #E0E9C0 !important;
	border: 2px dashed #000 !important;
	text-align: center;
	color: black;

}

::-webkit-input-placeholder {
	text-align: center;
	color: #000000;
}

::-moz-placeholder {
	text-align: center;  
}

:-ms-input-placeholder {  
	text-align: center; 
}

input[type="checkbox"] {

	width: 20px;
	height: 20px;
	background: #fff !important;
    
}

input[type=button] {

	border: 2px dashed #000 !important;;
	padding: 5px;
	border-radius: 5px;
	width: 100%;
	text-align: center;
	background-color: #AA8 !important;
	background-image: url("https://files.catbox.moe/cxy672.gif") !important;
    font-size: 22px;
	cursor: pointer;
	
}

input[type=button]:hover {

	background-color: #996 !important;;
	transition: background-color 150ms linear;
}

input[type=submit] {

	border: 2px dashed #000 !important;
	padding: 5px;
	border-radius: 5px;
	width: 100%;
	text-align: center;
	background-color: #E0E9C0 !important;
	background-image: url("https://files.catbox.moe/cxy672.gif") !important;
	font-size: 22px;
	cursor: pointer;
	
}

input[type=submit]:hover {

    background-color: #AA8 !important;
  transition: background-color 150ms linear;
background-image: url("https://files.catbox.moe/cxy672.gif") !important;

}

select {

   	background-image: url("https://files.catbox.moe/cxy672.gif") !important;
	border: 3px dashed #000 !important;

}

a.homebutton {

	color: black;
	text-decoration: none;

}

a.homebutton:hover {

	color: black;
	text-decoration: underline;

}

a.linkbutton {

	color: #111000 !important;
	text-decoration: none;

}

a.linkbutton:hover {

	color: #111000 !important;
	text-decoration: underline;

}

a.linkbutton#showURLUp {
	cursor: pointer;
}

a.stats {

	color: #111000 !important;
	text-decoration: none;

}

a.stats:hover {

	color: #111000 !important;
	text-decoration: underline;

}

a.filelist {

	width: 20%;
    padding: 5px;
    border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 20px;
	margin-top: -5px;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-family: "Courier New" !important;
    font-size: 14px;
	display: inline-block;

}

a#disabled {
	color: gray;
	cursor: text;
	text-decoration: none;
}

p.albumlink {

	font-size: 12px;
	cursor: pointer;
	margin-left: auto;
	margin-right: auto;
	border: 1px dashed #000 !important;
	border-radius: 4px;
	padding-top: 2px;
	padding-bottom: 2px;
	background-color: #E0E9C0 !important;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap; 
	width: 70%;
}

span.urlResponse {

	font-size: 20px;
	cursor: pointer;
	margin-left: auto;
	margin-right: auto;
	padding-top: 2px;
	padding-bottom: 2px;
	display: inherit;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
}

button.urlUpSubmit {

	width: 25%;
	margin-top: 10px;
	display: block;
	margin: 5px auto 0 auto;
	border: 2px dashed #000 !important;
	padding: 5px;
	border-radius: 5px;
	text-align: center;
    background-image: url("https://files.catbox.moe/cxy672.gif")  !important;
    font-size: 22px;
	cursor: pointer;
}

button.urlUpSubmit:hover {

	background-color: #AA8 !important;
	transition: background-color 150ms linear;

}

.legalContainer {
	width: 75%;
	margin-left:auto; 
	margin-right: auto; 
	font-size: 16px; 
	font-family: Monospace
}

.legalSection {
	background-color: #888 !important; 
	padding: 10px 10px; 
	border-radius: 6px;
}

.legalRow {
	text-align: center;
	font-size: 24px;
	color: black;
	margin-bottom: 20px;
	margin-top: 15px;
}

span.legalRowText {
	margin-left: 20px;
	margin-right: 20px;
	background-color: #888 !important;
	border-radius: 18px;
	padding: 8px 20px;
	border: dashed 1px #000 !important;
	color: #000 !important;
}

span.legalRowText:hover {
	text-decoration: underline;
	cursor: pointer;
}

span.legalRowText.active {

	border-color: black;
	color: black;
	text-decoration: none;

}

.notenormal {
	width: 55%;
	padding: 15px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 35px;
	overflow: hidden;
}

.notesmall {

	width: 45%;
	padding: 5px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 22px;

}

.notetiny {

	width: 25%;
	padding: 5px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 16px;

}

.notetinywarning {

	width: 35%;
	padding: 5px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 16px;

}

table {

	width: 55%;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	border: 1px dashed #000 !important;
	border-collapse: collapse;
	text-align: center;
	font-size: 18px;
	margin: auto;
}

td {
	border: 1px dashed #000 !important;
	max-width: 3em;
	word-wrap:break-word;
}

ul {
	padding: 0;
}

ul li.home{

	width: 25%;
	padding: 25px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 65px;
	margin-top: -5px;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 26px;
	display: inline-block;

}

ul li.torrent {

	width: 20%;
	padding: 6px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 15px;
	margin-top: -5px;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 14px;
	display: inline-block;
	overflow: hidden;
}

ul#reorder-bounds {
	list-style-type: none;
}

ul li#reorderable {
	display: inline-block;
}

li div {
	border:dashed 1px black; 
}

dl {
	padding: 0;	
	width: 60%;
}

dl.faq {

	display:table; 
	margin-left: auto; 
	margin-right: auto; 
	margin-bottom: 75px;

}

dl dt.question {

	font-size: 20px;
	font-weight: 600;
	margin-top: 5px;

}

dl dd.answer {

	font-size: 18px;
	margin-top: 5px;

}

code {
	font-size: 16px;
}

div.image {
	position: fixed;
	right: 0px;
	bottom: -5px;
	z-index: -1;
}

div.seeders {

	color: #000 !important;
	width: 35%;
	padding: 15px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 15px;	
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 24px;
	display: inline-block;

}

div.leechers {

	color: #000 !important;
	width: 35%;
	padding: 15px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 15px;	
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 24px;
	display: inline-block;

}

div.completed {

	color: #000 !important;
	width: 35%;
	padding: 15px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	text-align: center;
	margin: 15px;	
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 24px;
	display: inline-block;

}

.grid-bounds {

	width: 85%;
	text-align: center;
	font-family: "Courier New" !important;
	margin: 0 auto;
}

.col-1-8 {
	width: 12.5%;
	min-width: 185px;
	display: inline-block;
	margin-top: 15px;
	padding: 5 15;
}

.col-1-8 > p {
	font-size: 12px;
	margin: 0 0;
}


img.previewimg, video.previewimg {

	max-width: 100%;
	max-height: 8em;
	margin-top: 10px;
	margin-bottom: 10px;


}

img.reorderable {

	max-width: 100%;
	max-height: 10em;

}
.placeHolder div {

	height: 100%;
	background-color:white;
	border:dashed 1px gray;

}

form.dz-clickable {

	width: 650px;
	border: 6px double #000 !important;
	border-radius: 4px;
	background-color: #fff !important;
	cursor: pointer;
	padding-bottom: 25px;
background-image: url("https://files.catbox.moe/cxy672.gif") !important;
	    

}

form.dz-clickable:hover {

	background-color: #AA8 !important;
	text-decoration: none;
	outline: none;
	transition: background-color 150ms linear;
	border: 3px dashed #000 !important;

}

div.dz-default {

	color: #000 !important;
	cursor: pointer;
	font-size: 24px;
	font-weight: 400;
	margin-top: 30px;

}

div.dz-preview:first-of-type {
	margin-top: 45px;
}


div.dz-preview {

	width: 85%;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	border: 2px dashed #000 !important;
	border-radius: 15px;
	padding-top: 20px;
	margin-top: 25px;
	box-shadow: 1px 1px 5px grey;
	overflow: hidden;
	cursor: default;

}

div.dz-error {
	border: 2px dashed #000 !important;
}

div.dz-error-message {

	font-size: 20px;
	margin: 5px;
	cursor: default;
}

div.dz-success {
	border: 2px dashed #000 !important;
}

div.dz-details {

	text-align: left;
	margin-left: 25px;
}

.dz-upload {
	display: block;
	height: 10px;
	width: 0%;
	max-width: 75%;
	margin-left: 20%;
	margin-top: -16px;
	border-radius: 15px;
	float: left;
}

div.dz-filename {
	margin-bottom: 5px;
}

div.responseText {

	font-size: 20px;
	margin-top: 5px;
	margin-bottom: 7px;
	cursor: pointer;
	width: 340px;
	margin-left: auto;
	margin-right: auto;
	border: 1px dashed #000 !important;
	border-radius: 4px;
	padding-top: 2px;
	padding-bottom: 2px;
	display: inherit;
	background-color: #a0a0a0 !important;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;		

}

div.responseText:hover {

	background-color: #222100 !important;
	transition: background-color 150ms linear;

}

div.dz-fallback input {

	width: 30%;
	display: block;

}

div.dz-fallback input:hover {

	width: 30%;
	display: block;

}

div.dz-message {

	font-size: 24px;

}

div.noscriptalert {

	color: #112200 !important;
	font-size: 18px;
	margin-left: auto;
	margin-right: auto;
	text-align: center;
}

div.contributorsContainer {

	width: 60%;
	margin: 0 auto;

}

div.contributor {

	padding: 5px;
	border: 2px dashed #000 !important;
	border-radius: 25px;
	background-image: url("https://files.catbox.moe/cxy672.gif");
	font-size: 18px;
	margin-top: 5px;

}

div.contributor > p > span {
	color: #000;
}

input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
    -webkit-appearance: none;
}

@media screen and (max-device-width: 640px) {

	form.dz-clickable {

		width: 450px;
		min-height: 250px;

	}

	form.urlUploadForm {
		width: 450px;
	}

	div.dz-message {

		font-size: 40px;
		font-weight: 400;
		margin-top: 110px;

	}

	div.urlUploads a {
		font-size: 32px;
	}

	.greeting {

		width: 85%;
		font-size: 38px;

	}

	.linkrow {
		
		margin-top: 55px;
		font-size: 30px;

	}

	.footer .innertext {

		font-size: 24px; 
		text-align: center;

	}

	.loginstatus { 

		font-size: 20px;
		margin-bottom: 20px;

	}

	form.genericform {

		width: 85%;
		margin-bottom: 290px;

	}

	dl.faq {

		font-size: 28px;
		width: 85%;

	}

	.hometab {

		width: 20%;
		height: auto;
	}

	.hometab:nth-of-type(2) {

		top: 54px;
		color: #020202;
	}
	
	a.homebutton{

		font-size: 24px;

	}

	.image {
		display: none;
	}	
	
	.notetiny {
		width: 70%;
	}

	.notesmall {
		width: 70%;
	}

	.notetiny a {
		font-size: 32px;
	}	
	
	table {

		max-width: 75%;
		
	}

	td {
		max-width: 15em;
	}

}



}
