#icerik-yazi {
    background-color: #333 !important;
    border: none !important;
}
.icerik-y-uzun, .icerik-yy-uzun, .yazi1 span, .yazar-bilgi, .post-navigasyon a {
    color: #939393;
}
.k-baslik, .yazi-alt, .f-baslik, .t-baslik, .aktif1, .k-kenar1, .k-kenar2, .wp-pagenavi a, .wp-pagenavi a:link, .wp-pagenavi a:visited, .wp-pagenavi a:hover, .wp-pagenavi a:active, .wp-pagenavi span.pages, .wp-pagenavi span.extend, .wp-pagenavi a.last, .tagcloud a:hover {
    background: #5b5b5b;
}
#dc_jqaccordion_widget-2-item, div.widget:nth-child(6), div.widget:nth-child(4) {
    display: none;
}
#menu-item-241947 > ul:nth-child(2) {
    background: #333;
}
ul.nav li ul.sub-menu li {
    background:#333;
}
ul.nav li:hover > a {
    color: #3498db !important;
    background: #333;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=fullprogramlarindir") {
body {
	color: #939393 !important;
	background-color: #333 !important;
	margin: revert;
}
.comment__footer .author, .comment__header .author, .publisher-anchor-color a {
	color: #3498db !important;
}
}
