css:
@-moz-document domain("app.roll20.net") {
#initiativewindow {
    padding: 1px 1px;
    overflow-x: auto
}
#initiativewindow ul li {
    padding: 1px;
}
#initiativewindow ul li {
    min-height: 15px
}
#initiativewindow ul li img {
    max-width: 20px;
    max-height: 18px;
}
#initiativewindow ul li span.initiative {
    font-size: 18px;
    padding: 1px;
    min-height: 12px
}
#initiativewindow ul li span.name {
    font-size: 14px;
    padding-top: 1px
}
#initiativewindow input {
    font-size: 12px;
    height: 12px;
}
.ui-dialog-titlebar {
    padding: 1px 5px;
    font-size: 8px;
}
.ui-dialog .ui-dialog-title {
    margin-top: 1px;
    margin-bottom: 1px;
    padding: 1px
}
.ui-dialog .ui-dialog-titlebar-close {
    top: 50%;
    width: 19px;
    margin: -10px 0 0 0;
    padding: 1px;
    height: 6px;
    font-size: 7px;
    line-height: 7.5px;
}
.ui-dialog .ui-dialog-titlebar-close span {
    display: block;
    margin: 1px;
    text-indent: 9999px
}
.ui-dialog .ui-dialog-buttonpane {
    border-width: 1px 0 0 0;
    margin: .05em 0 0 0;
    background-color: #f5f5f5;
    padding: 1px 10px 1px;
}
.ui-dialog .ui-dialog-buttonpane button {
    margin: .05em .04em .05em 0;
}
}
