css:
@-moz-document url-prefix("https://app.astraltabletop.com/widget/game") {
/*
    Astral, Chat, delayed dice menu
    Modifies: Dice menus in the in-game chat
    Shrinks the long formula into a "click" button,
    enables Actions to be menus of die rolls by skill or attribute
*/

/*
div.chat-message
  span.message-container
    span.action-box     has title
    table or div        user content
      span.enabled      click to roll
      span.roll
        span.value
*/

div.chat-message .message-container {
    font-family: verdana;
    font-size: 11px;
}
div.chat-message .message-container .action-box {
    font-size: 20px;
}

div.chat-message span.enabled {
    /* this makes a box */
    display: inline-block;
    width: 30px;
    height: 18px;
    overflow: hidden;

    /* this lines it up */
    position: relative;
    top: 4px;

    /* tiny print */
    font-size: 9px;
    font-style: normal;
    padding: 1px 1px;
    color: yellow;
}


}
