css:
@-moz-document domain("prohardver.hu"), domain("mobilarena.hu"), domain("itcafe.hu"), domain("gamepod.hu"), domain("logout.hu") {



 @media only screen and (min-width:992px)

 {
	#page-index-2 #left {
		width:25%;
	}
	#page-index-2 #center {
		width:70%;
		padding-left:5%
	}
	.content-list.content-list-compact img {
		display: none!important;
	}
	.content-list .content-image-comment {
		position: relative;
		font-size: 0.8rem;
	}
	.content-list.content-list-compact .media-heading {
		font-size: 1rem;
	}
	.content-list.content-list-megabox .megabox-box .content-image .megabox-msgs {
		font-size: 0.75rem;
	}
	.content-list.content-list-megabox .megabox-box.megabox-large .content-lead {
		font-size: 0.8rem;
	}
	.content-list.content-list-megabox .megabox-box.megabox-small .content-lead {
		font-size: 0.7rem;
	}
	.content-list .content-site-ph {
		font-size: 0.8rem;
	}
	.content-list.content-list-megabox.content-list-megabox-banner .megabox-banner-container h1 {
		font-size: 0.8rem!important;
	}
	.content-list.content-list-megabox.content-list-megabox-banner .megabox-banner-container {
		display: wrap;
	}
	.content-list.content-list-megabox.content-list-megabox-banner .megabox-banner-container p {
		font-size: 0.7rem!important;
	}
	.content-list.content-list-megabox .megabox-box .content-image h1 {
		font-size: medium;
	}
	.content-list.content-list-megabox.megabox-ph .megabox-box {
		height: 5em;
	}
	.content-list.content-list-megabox .megabox-box .content-image .megabox-caption {
		top: 0;
		font-size: 0.8rem;
		font-weight: bold;
		height: max-content;
	}
	.content-list .media {
		padding: .1rem .1rem;
		position: relative;
	}
}
main {
	background-image:none !important;
}
#page-index-2 {
	background-image:none !important;
}
#top > div.container.p-0.bg-transparent.overflow-hidden {
	display: none;
}
#left > div.content-list.content-list-megabox.content-list-megabox-banner > div.megabox-banner-container > div.megabox-banner {
	display: none;
}

}
