css:
@-moz-document domain("wbijam.pl") {
/* ------- STYL NOCNY ------ */
.dark-mode {
    background: #2A2A2A;
    color: #E2E2E2;
}

.dark-mode .lista {
    border: 1px solid #CDCDCD;
    background-color: #424242;
}

.dark-mode .lista a:link {
    color: #14BFA1;
}

.dark-mode .lista a:hover {
    color: #14BFA1;
}

.dark-mode .lista_hover:hover {
    background-color: #6C6C6C;
}

.dark-mode .naglowek {
    background-color: #424242;
    border-left: 5px solid #13BFA1;
    border-right: 5px solid #13BFA1;
    color: #E2E2E2;
}

.dark-mode .naglowek_fb {
    border-bottom: 1px solid #E2E2E2;
}

.dark-mode .naglowek_fb_lewa {
    color: #E2E2E2;
}

.dark-mode .tresc {
    background-color: #424242;
    border: 1px none #E2E2E2;
}

.dark-mode .tresc_bar_nowy {
    background-color: #424242;
    border: 1px solid #E2E2E2;
}

.dark-mode .tresc_bar {
    background-color: #424242;
    border: 1px solid #939393;
    margin-top: -1px;
    border-bottom: 1px none black;
    border-right: 1px none black;
}

.dark-mode a:link {
    text-decoration: underline;
    color: #14BFA1;
}

.dark-mode a:hover {
    text-decoration: none;
}

.dark-mode a:visited {
    color: #868686;
    text-decoration: underline;
}

.dark-mode a:visited:hover {
    text-decoration: none;
}

.dark-mode .mega_link {
    color: #14BFA1;
}

.dark-mode .odtwarzacz_link {
    color: #14BFA1;
}

.dark-mode .odtwarzaj_vk {
    color: #14BFA1;
}

.dark-mode #stopka_gora a:link {
    text-decoration: none;
    color: #14BFA1;
}

.dark-mode #stopka_gora a:hover {
    text-decoration: overline;
    color: #14BFA1;
}

.dark-mode #stopka_gora a:visited {
    text-decoration: none;
    color: #14BFA1;
}

.dark-mode #stopka_gora a:visited:hover {
    text-decoration: overline;
    color: #14BFA1;
}

.dark-mode #menu_prawa {
    background-color: #424242;
}

.dark-mode .pmenu li a {
    color: #14BFA1;
    text-decoration: none;
}

.dark-mode .pmenu li a:hover {
    color: #14BFA1;
    text-decoration: underline;
}

.dark-mode #menu_prawa_fb {
    background-color: #424242;
}

.dark-mode div.episode-calendar-wrapper.active {
    color: #202020;
}

.dark-mode .strony a {
    color: #484848;
    text-decoration: none;
}

.dark-mode .obecna a {
    color: #FFFFFF;
    text-decoration: none;
}

.dark-mode #tresc_dzielna_gildia {
    background-color: #424242;
    border: 1px solid #E2E2E2;
}

.dark-mode .dzielna_gildia_lista {
    background-color: #424242;
    color: #E2E2E2;
}

.dark-mode .pmenu .kolejnosc a {
    color: #E2E2E2;
}

.dark-mode .pmenu .kolejnosc a:hover {
    color: #E2E2E2;
}

/* ------- PRZYCISK DARK-MODE ------ */
#dark-mode-changer {
    margin: 0;
    margin-bottom: 3px;
    padding: 0;
    height: 30px;
    border: 1px solid #000;
    border-radius: 4px;
    cursor: pointer;
    overflow: hidden;
}

#dark-mode-changer.active {
    border-color: #FFF;
}

#dark-mode-changer li {
    list-style: none;
    height: 60px;
    text-align: center;
    text-transform: uppercase;
    transition: 0.5s;
}

#dark-mode-changer.active li {
    transform: translateY(-30px);
}

#dark-mode-changer li span {
    display: block;
    height: 30px;
    line-height: 30px;
    color: #262626;
    background: #FFF;
}

#dark-mode-changer li span:nth-child(1) {
    background: #262626;
    color: #FFF;
}

/* ------- OG�LNE ------ */
body {
    margin: 10px 0 0 0;
    font-size: 14px;
    font-family: 'Trebuchet MS', 'Arial', 'Verdana';
    color: #202020;
    background-image: url(https://i.imgur.com/Xq8CxuD.jpg);
}

ul {
    list-style-type: none;
    padding: 0px;
    margin: 0px;
}

#szkielet {
    margin: 0px auto;
}

a:link {
    color: #016DB7;
    text-decoration: none;
    text-shadow: 0 0 2px #919191;
}

a:hover {
    text-decoration: underline;
}

a:visited {
    text-decoration: line-through;
    color: #016DB7;
}

a:visited:hover {
    text-decoration: underline;
}

.red {
    color: #CD0A0A;
}

.green {
    color: #32782D;
}

.vertical_top {
    vertical-align: top;
}

.left {
    text-align: left;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.bold {
    font-weight: bold;
}

.italic {
    font-style: italic;
}

img {
    border: 0px;
}

/* ------- ODTWARZACZ ------ */
.mega_link {
    cursor: pointer;
    color: #0079CC;
}

.mega_link:hover {
    text-decoration: underline;
}

.odtwarzacz_link {
    cursor: pointer;
    color: #0079CC;
}

.odtwarzacz_link:hover {
    text-decoration: underline;
}

.odtwarzaj_vk {
    cursor: pointer;
    color: #0079CC;
}

.odtwarzaj_vk:hover {
    text-decoration: underline;
}

/* ------- ODLICZANIE I GALERIE NA PODSTRONACH ------ */
div#actual-time {
    display: none;
}

div#episode-gallery {
    display: none;
}

div#show-gallery img {
    vertical-align: middle;
}

div#show-gallery {
    font-size: 16px;
    margin: 10px auto;
    height: 30px;
    text-align: center;
    line-height: 30px;
    cursor: pointer;
}

div.episode-calendar-wrapper {
    font-size: 16px;
    margin: 10px auto;
    height: 30px;
    text-align: center;
    line-height: 30px;
}

div.episode-calendar-wrapper.active {
    background-color: #C2ECBE;
}

img#refresh-gallery {
    cursor: pointer;
}

/* ------- MININAWIGACJA ------ */
.nawigacja {
    height: 30px;
}

.nawigacja_lewa {
    text-align: left;
    float: left;
}

.nawigacja_srodek {
    text-align: center;
    float: left;
}

.nawigacja_prawa {
    text-align: right;
    float: left;
}

/* ------- REKLAMY ------ */
#adv-top {
    margin: 10px auto;
}

.taboola-container {
    margin-top: 10px;
}

/* --- MENU PRAWA --- */
#menu_prawa {
    color: #343434;
    font-size: medium;
    margin-left: 20px;
    float: right;
    background-color: rgba(228, 228, 228, 0.825);
    border-top: 1px solid #939393;
    border-bottom: 1px solid #939393;
    border-radius: 12px;
}

.pmenu_naglowek_a,
.pmenu_naglowek_b {
    height: 28px;
    text-align: center;
    font-size: large;
    color: #e6e6e6;
    padding-top: 5px;
    text-shadow: 0 0 2px #cacaca;
}

.pmenu_naglowek_a {
    background: none no-repeat rgba(38, 69, 140, .7);
}

.pmenu_naglowek_b {
    background: none no-repeat rgba(64, 38, 140, .7);
}

.pmenu {
    clear: both;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 10px;
    padding-bottom: 10px;
}

.pmenu li {
    display: block;
}

.pmenu li a {
    margin-left: 3px;
    color: #0079CC;
    text-decoration: none;
}

.pmenu li a:hover {
    color: #0079CC;
    text-decoration: underline;
}

.pmenu li.red a {
    color: #CD0A0A;
}

.description_header {
    margin: 10px 0 0 0;
}

.pmenu .kolejnosc a {
    color: #AB0000;
}

.pmenu .kolejnosc a:hover {
    color: #AB0000;
}

/* ----- NAG��WKI ----- */
.naglowek {
    height: auto;
    background: linear-gradient(90deg, rgba(117, 117, 117, 1) 0%, rgba(162, 162, 162, 1) 65%, rgba(194, 194, 194, 1) 100%);
    font-size: x-large;
    padding-left: 4px;
    padding-top: 4px;
    clear: both;
    border-left: 4px solid #083B78;
    border-right: 4px solid #083B78;
    color: #0D52A4;
    font-weight: bold;
    padding-bottom: 6px;
    margin: 0px;
    padding-right: 8px;
    border-top: 1px solid #939393;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
    text-align: left;
    text-shadow: 0 0 2px #606060;
}

.pod_naglowek {
    border-bottom: 1px solid #BBBBBB;
    padding-bottom: 8px;
    padding-top: 8px;
    font-size: x-large;
    clear: both;
    margin-bottom: 16px;
    font-weight: bold;
    margin-top: 0px;
    text-align: center;
    font-style: normal;
    background-color: rgba(74, 74, 74, 0.8);
    border: 3px dotted #3e3e3e;
    color: #66D0E7;
    text-shadow: 0 0 3px silver;
    font-family: Arial;
    border-left: 1px hidden black;
    border-right: 1px hidden black;
}

/* ----- ARTYKU�Y I NEWSY, TRE�� ----- */
#tresc_lewa {
    float: left;
    background-color: rgba(228, 228, 228, 0.825);
    border-top: 1px solid #939393;
    border-bottom: 1px solid #939393;
    border-radius: 12px;
    margin-left: -16px;
    padding: 8px;
    height: auto;
    font-size: medium;
    color: #343434;
}

.tresc {
    text-align: justify;
    clear: both;
    background-color: rgba(228, 228, 228, 0.825);
    border: 1px none #939393;
    padding: 10px 10px 36px 10px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    font-size: larger;
    border-bottom: 1px solid #939393;
    color: #343434;
}

.tresc_bar_nowy {
    border-bottom: 1px solid #828282;
    border-top: 1px none #828282;
    border-left: 1px none #828282;
    border-right: 1px none #828282;
    padding-top: 3px;
    padding-bottom: 3px;
    padding-left: 8px;
    padding-right: 8px;
    float: right;
    clear: both;
    margin-bottom: 20px;
    height: 26px;
    border-bottom-right-radius: 12px;
    border-bottom-left-radius: 12px;
    background-color: rgba(0, 0, 0, 0.3);
    font-size: medium;
    width: 97.6%;
}

.tresc_bar_nowy p {
    width: auto;
    float: left;
    font-style: italic;
    margin: 3px 0 0 0;
    color: #10438C;
    font-weight: normal;
}

.tresc_bar_nowy p.tresc_bar_fb {
    float: right;
}

.tresc_bar_nowy a {
    color: #484848;
    text-decoration: none;
}

.tresc_bar_nowy a:hover {
    color: #ABABAB;
}

.tresc_bar {
    background-color: rgba(0, 0, 0, 0.2);
    float: right;
    clear: both;
    border: 1px solid #939393;
    border-right: 0px solid black;
    border-bottom: 0px solid black;
    border-top-left-radius: 12px;
    width: auto;
    /*+placement: 0px -24px;*/
    position: relative;
    left: 0px;
    top: -24px;
    line-height: 1;
}

.tresc_bar p {
    float: right;
    font-style: italic;
    color: #10438C;
    font-weight: normal;
    width: auto;
}

.tresc_bar a {
    color: #484848;
    text-decoration: none;
}

.tresc_bar a:hover {
    color: #ABABAB;
}

.tresc_bar_fb_ramka {
    height: 20px;
    border: none;
    overflow: hidden;
}

.tresc_zdjecie {
    float: left;
    margin-right: 10px;
    margin-bottom: 2px;
}

/* ----- WYNIKI WYSZUKIWANIA PLIK�W ----- */
.wyniki_wyszukiwania {
    height: 26px;
    margin-bottom: 10px;
    margin-top: 15px;
}

.wyniki_wyszukiwania_pomoc {
    float: left;
    height: 26px;
    line-height: 26px;
}

.wyniki_wyszukiwania_fb {
    float: right;
}

.wyniki_wyszukiwania_fb_ramka {
    height: 20px;
    border: none;
    overflow: hidden;
}

.odcinek_oczekiwanie_informacja {
    clear: both;
    margin-bottom: 10px;
}

/* ----- PLUGIN FB ----- */
#fb_ramka_glowna {
    height: 28px;
    border: none;
    overflow: hidden;
    margin-bottom: 5px;
    margin-top: 3px;
}

.naglowek_fb {
    font-size: 11px;
    font-weight: bold;
    height: 26px;
    margin-bottom: 10px;
    margin-top: 0px;
    border-bottom: 1px solid #BBBBBB;
    clear: both;
    text-shadow: 0 0 3px #7d7d7d;
    font-family: Arial;
}

.naglowek_fb_lewa {
    font-size: x-large;
    float: left;
    height: 26px;
    line-height: 26px;
    color: #292929;
    font-weight: normal;
    padding-bottom: 0px;
    margin: 0px;
}

.naglowek_fb_prawa {
    float: right;
}

.naglowek_fb_ramka {
    height: 20px;
    border: none;
    overflow: hidden;
}

#menu_prawa_fb {
    background-color: rgba(228, 228, 228, 0.825);
}

#menu_prawa_fb_image {
    cursor: pointer;
    border-radius: 7px;
}

#menu_prawa_fb_plugin {
    margin-bottom: 5px;
    display: none;
}

/* ----- STRONICOWANIE ----- */
.strony {
    text-align: center;
    clear: both;
}

.strony a {
    border: 1px solid #BBBBBB;
    background-color: #FFFFFF;
    padding-left: 3px;
    padding-right: 3px;
    text-decoration: none;
    color: #484848;
    font-weight: bold;
}

.strony a:hover {
    border: 1px solid #484848;
}

.obecna a {
    background-color: #085778;
    border: 1px solid #085778;
    color: #FFFFFF;
}

/* ----- STOPKA ----- */
#stopka_srodek a:link {
    color: #0298FF;
    text-decoration: none;
}

#stopka_srodek a:hover {
    text-decoration: underline;
}

#stopka_srodek a:visited {
    color: #868686;
    text-decoration: none;
}

#stopka_srodek a:visited:hover {
    text-decoration: underline;
}

#stopka_srodek {
    background: rgba(60, 60, 60, 0.825);
    color: #C5C5C5;
    clear: both;
    text-align: center;
    padding: 25px;
}

#stopka_gora {
    clear: both;
    margin: 0px auto;
    padding-bottom: 60px;
    font-size: large;
}

#stopka_gora_lewy {
    float: left;
    text-align: left;
}

#stopka_gora_prawy {
    float: right;
    text-align: right;
}

#stopka_gora a:link {
    text-decoration: none;
    color: #3037B9;
    text-shadow: 0 0 2px #000000;
}

#stopka_gora a:hover {
    text-decoration: overline;
    color: #3037B9;
}

#stopka_gora a:visited {
    text-decoration: none;
    color: #3037B9;
}

#stopka_gora a:visited:hover {
    text-decoration: overline;
    color: #3037B9;
}

#stopka_dol {
    clear: both;
    padding: 20px;
    text-align: center;
    color: #545454;
    background-color: rgba(228, 228, 228, 0.600);
}

/* ------ LISTA ----- */
.lista {
    border: 1px solid #939393;
    border-radius: 8px;
    background-color: rgba(228, 228, 228, 0.825);
    border-spacing: 2px;
    clear: both;
    font-size: medium;
}

.lista td {
    border: 1px solid #b0b0b0;
    padding-top: 1px;
    padding-bottom: 1px;
    padding-left: 5px;
    border-radius: 5px;
}

.lista th {
    border: 0px;
    color: #818181;
}

.lista_hover:hover {
    background-color: #ECF0F3;
}

.lista_td {
    text-align: center;
}

.lista_td_calendar {
    text-align: center;
}

.lista a:link {
    text-decoration: none;
    color: #0079CC;
}

.lista a:hover {
    text-decoration: underline;
    color: #0079CC;
}

.lista a:visited {
    text-decoration: none;
    color: #868686;
}

.lista a:visited:hover {
    text-decoration: underline;
    color: #868686;
}

/* ------ DZIELNA GILDIA ----- */
.dzielna_gildia_lista {
    height: 34px;
    font-size: 16px;
    font-weight: bold;
    padding: 5px;
}

#tresc_dzielna_gildia {
    text-align: justify;
    clear: both;
    font-size: 13px;
    background-color: #FFFFFF;
    border: 1px solid #B9B9B9;
    border-bottom: 0px;
    border-top: 0px;
    padding-bottom: 15px;
    padding-top: 15px;
}

table.dzielna_gildia tr {
    height: 200px;
    text-align: center;
}

/* ------ PORTAL DODATKOWE ----- */
#tresc-portal {
    margin: 0 auto;
}

.top-portal {
    font-size: large;
    margin: 0 auto;
    margin-bottom: 15px;
    background-color: rgba(255, 255, 255, 0.598);
    /*+border-radius: 12px;*/
    -moz-border-radius: 12px;
    -webkit-border-radius: 12px;
    -khtml-border-radius: 12px;
    border-radius: 12px;
    border: 1px solid #939393;
    padding: 9px;
    border-left: 4px solid #083B78;
    border-right: 4px solid #083B78;
}
}
