css:
@-moz-document url(https://idos.idnes.cz/) {

* {
    box-shadow: none !important;
}

form *, .from-to * {
    border-color: #222222;
    border-width: 1px;
}
#content {
    background: #111 !important;
}
#logo {
    background: #0000!important;
}
.delay-bubble {
    color: #ee7a7a !important;
}
body {
    background-color: black !important;
}
label, .connection-head, .connection-expand, .prev-next-share *, #row-btns, .btn-blue *, .btn-orange, .btn-orange * {
    background-color: #3e3e3e !important;
    color: #dddddd;
    background-image: none !important;
    border-color: #3e3e3e;    
    text-shadow: none;
}

a {
    background-image: none !important;
    background: #00000000 !important;
}

h3 * {
    color: #0e95dd;
}
.expand * {
    color: grey;
}

input,
input * {
    background: black !important;
    color: #dddddd !important;
    border-color: black !important;
    border-radius: 0px !important;
}

.reset > label {
    background: #00000000 !important;
}

.connection-details,
.remarks-list,
.message-grey,
.box,
.basket-passengers,
.to-basket,
.to-basket * {
    background-color: #292929 !important;
    color: #dddddd;
    border-color: #292929 !important;
}

#header,
#main,
#content,
#bg-color {
    background-color: black;
}
.relative {
    background: black;
}
.connection .connection-expand {
    height: auto;
    padding: 5px 20px 5px 0;
}
.connection .connection-expand li {
    height: auto;
}
.connection .connection-expand li a {
    height: auto;
    padding-top: 0;
}
.connection .connection-head .connection-collapse a {
    margin: 12px 0 0 0;
    padding: 0;
}
.connection.ca-collapsed .connection-head {
    background-color: #e0f0f9;
}
.connection .connection-head .connection-collapse {
    margin: -10px 0 0 20px;
}
.connection .connection-head {
    padding: 2px 20px 2px 0;
    background-color: #e0f0f9;
}
.date-total label {
    padding: 0 20px !important;
}
.connection.detail-box .connection-details .line-item div > a .line-title {
    padding: 0;
}
.connection.detail-box .connection-details .delay-bubble {
    margin-bottom: 0;
}
.connection.detail-box .connection-details .reset.stations {
    margin-bottom: 2px;
}
.connection .connection-head .date-total {
    background-color: #e0f0f9;
}
.connection .connection-head .connection-collapse a {
    height: auto;
}
.delay-bubble {
    padding: 0 2px;
    margin-left: 0px;
}
.connection .connection-details .stations .item {
    padding: 0 0 0 80px;
}
.reset-share {
    margin-bottom: 0;
}
.headerWrapper {
    height: auto;
}
.btn-buy {
    height: auto;
}
.btn {
    padding: 1px;
}
.registration-inps .btn {
    padding: 6px;
}
.sign-in__social-button {
    background-size: 24px 24px;
}
.sign-in__h3-headline {
    margin: 0;
}
.forgot {
    margin-top: 5px;
}
.sign-in__hr--first {
    margin: 0.5em 0;
}
.sign-in__hr {
    margin: 0.5em 0;
}
p.submit a {
    padding: 5px 0;
}
.submit button {
    padding: 5px 0;
}
.share-button {
    background-position: 3px center;
    padding-left: 20px !important;
}
.share-button:hover {
    background-position: 3px center;
}
.share-button--fb {
    width: 44px;
    height: 45px;
    background-position: center center;
}
.share-button--fb:hover {
    background-position: center center;
}
.btn.btn-medium {
    height: auto;
}
.reset-share li.link {
    line-height: 2.5em;
}
.header-btn .btn {
    height: 2.5em;
}
.categories {
    margin: 0 -5px 10px;
}
#connection-filter {
    text-align: left;
    padding: 0 5px 0 0;
}
#header #connection-filter {
    text-align: right;
}
#connection-filter .from-to {
    margin-bottom: 10px;
}
#connection-filter .from-to .inp-combined:first-child {
    margin-bottom: 10px;
}
#connection-filter .ext-params-control {
    margin: 5px 0 0 0;
}
#connection-filter .date-time {
    height: 46px;
}
#connection-filter .submit {
    margin-top: 10px;
}
#connection-filter .other-params .direct-connection {
    margin: 5px 0;
}
p {
    margin: 0;
}
.box {
    margin: 10px 0 10px;
}
.box #header {
    margin-bottom: 5px;
}
h1 + .box {
    margin-top: 0;
}
#header.sticky {
    padding: 5px 0;
}
#header.simple {
    border-radius: 3px;
    padding: 3px 0;
}
#header.simple div.header-wrapper {
    margin: 10px 10px 0 0;
}
#header.simple #logo {
    margin: 0 0 0 10px;
    padding: 0;
}
.messages > li.message-red::before,
.messages > div.message-red::before {
    display: none;
}
.messages > li.message-red,
.messages > div.message-red {
    padding: 10px;
}
.messages > li,
.messages > div {
    padding: 5px;
}
.departures-table__cell {
    padding: 0px 5px;
}
#col-content.simple {
    width: auto;
}
h1,
#bg-color #header h1 {
    padding: 0;
}
#bg-color h1 {
    background: white;
    margin: 0;
    padding: 0.7em 0 0.7em 0.5em;
    text-align: left;
}
.box {
    margin: 0;
}
.connection-list .box {
    margin: 10px 0;
}
a#pagingNext {
    margin-bottom: 10px;
}
.passengers-list .inp-combined {
    width: calc(20% - 12px);
}
div.connection-head.relative.zjr-panel {
    padding-left: 10px;
}
.empty-basket {
    background-color: white;
    margin: 0;
}
#kosik-content.content-padding {
    padding: 0;
    box-shadow: 0 2px 10px rgba(21, 101, 150, 0.38);
}

@media print {

    #header,
    .connection-list,
    .zjr-table,
    .departures-table {
        font-size: 120%;
        background-color: #101010;
    }
    .zjr-table td {
        padding-top: 0;
        padding-bottom: 0;
        font-size: 120%;
    }
    table.zjr-table.times tr td {
        border-bottom: 1px solid gray;
    }
}
}
