css:
@-moz-document url(https://app.roll20.net/) {
.vaultchar {
    border: 1px solid #aaa;
    margin-top: 1em;
    background: #eee;
    border-radius: 5px;
    padding: 0.5em;
    height: 385px;
    text-overflow: clip;
}
.vaultchar p.char-name {
    font-size: 20px;
    background: #666;
    border-radius: 4px;
    padding: 0em 0.5em 0em;
    color: #fff
}
.char-info {
    height: 270px;
    text-overflow: clip;
    margin-top: 5px;
}
.char-images {
    height: 270px;
    margin-top: 5px;
}
.char-bio {
    text-overflow: clip;
    overflow: scroll;
}
.charmenu p {
    font-size: 12px;
}
.char-tools .btn:not(.btn-primary) {
    padding: 0em;
}
label.choose {
    line-height: 1.8;
    margin-bottom: 0px;
    padding: 0em;
}
.char-tools .btn {
    padding: 0em;
}
.char-avatar img,
.char-token img {
    overflow: scroll;
    max-height: 100px;
}
.char-avatar,
.char-token {
    font-size: 12px;
    padding: 0em;
    margin: .25em 1em 0em;
}
}
