/* your code goes here */


body {
    height:100vh;
    margin:0;
    overflow:hidden;
    color:#fff;
    background-color:#1e1e1e;
    font-family:TriviaSeznam, sans-serif;
    font-size:14px;
    line-height:22px
}
body>header {
    flex: 0 0 72px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    box-shadow: 0 2px 32px 0 rgba(0, 0, 0, 0.05), 0 1px 3px 0 rgba(0, 0, 0, 0.03);
    background-color: #121212;
    padding: 0 16px;
}
body>header nav ul li {
    position: relative;
    line-height: 24px;
    margin: 0 16px;
    color: #fff;
    cursor: pointer;
}
body>header nav ul .active {
    font-weight: bold;
    color: #FC6723;
}
.calendar-picker {
    list-style: none;
    padding: 16px;
    background-color: #1e1e1e;
    color: white;
}
#top h2 {
    font-size: 24px;
    line-height: 32px;
    margin-left: 16px;
    margin-right: 8px;
    color: #FC6723;
}
a {
    text-decoration: none;
    color: #FC6723;
    transition: all 300ms;
}
input {
    -webkit-writing-mode: horizontal-tb !important;
    text-rendering: auto;
    color: #fff;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    -webkit-appearance: textfield;
    background-color: white;
    -webkit-rtl-ordering: logical;
    cursor: text;
    margin: 0em;
    font: 400 13.3333px Arial;
    padding: 1px 0px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
.icon {
    width: 24px;
    height: 24px;
    color: #FFF;
    transition: all 300ms;
}
.popup.editor header {
    padding: 8px 16px;
    background-color: #121212;
}
.popup.editor section {
    padding: 16px;
    background-color: #1e1e1e;
    color: #fff;
}
.popup.editor input[type=text], .popup.editor select {
    height: 40px;
    background-color: #121212;
    color: white;
}
.popup.editor footer {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    padding: 16px;
    font-size: 12px;
    background-color: #1e1e1e;
}
[type=text], [type=number], textarea, .file-input {
    border-radius: 4px;
    border: 1px solid #d40f0f;
    padding: 0 7px;
    background-color: #fff;
    box-shadow: inset 0 2px 3px 0 rgba(0, 0, 0, 0.1), inset 0 2px 8px 0 rgba(0, 0, 0, 0.05);
}
#detail {
    flex: 0 0 340px;
    overflow-x: hidden;
    overflow-y: auto;
    border-left: 1px solid #000;
    background-color: #121212;
}
#top {
    display: flex;
    flex-direction: row;
    align-items: center;
    flex: 0 0 64px;
    border-bottom: 1px solid #000;
    padding: 0 16px;
}
#top .create button {
    width: 168px;
    background-color: #fc6723;
    color: #fff;
}
#top .view-nav button[type=button] {
    padding: 0 16px;
    background-color: #fc6723;
    color: #fff;
}
#editor>ul li:first-child {
    border-radius: 4px 4px 0 0;
    background-color: #FC6723;
    color: white;
}
#calendars li:hover, #calendars li.has-popup {
    background-color: rgba(0, 0, 0, 0.03);
}
#editor form section {
    background-color: #1e1e1e;
    flex-grow: 1;
    overflow: auto;
    padding: 16px 16px 16px 24px;
}
#editor form header {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 16px 24px;
    border-bottom: 1px solid #000;
    background-color: #1e1e1e;
}
#editor form footer {
    display: flex;
    flex-direction: row;
    align-items: center;
    border-top: 1px solid #000;
    padding: 24px;
    background-color: #1e1e1e;
}
select {
    border-radius: 4px;
    border: 1px solid #E0E0E0;
    padding: 0 40px 0 7px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background: no-repeat top 50% right 8px / 24px 24px #121212 url(data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.or…%200%200%201%201.06-1.06z%22%20fill%3D%22%23828282%22%20%2F%3E%3C%2Fsvg%3E);
    color: #fff;
}
[type=text], [type=number], textarea, .file-input {
    border-radius: 4px;
    border: 1px solid #fff;
    padding: 0 7px;
    background-color: #121212;
    box-shadow: inset 0 2px 3px 0 rgba(0, 0, 0, 0.1), inset 0 2px 8px 0 rgba(0, 0, 0, 0.05);
}
button.plain:not(:disabled):hover .icon {
    color: #FC6723;
}
.month-widget h3 {
    text-transform: lowercase;
    font-size: 16px;
    line-height: 24px;
    color: #FC6723;
}
#calendars h3 {
    font-size: 16px;
    line-height: 24px;
    color: #FC6723;
}
.window.modal header {
    padding: 48px 48px 16px 48px;
    background-color: #1e1e1e;
    color: #fff;
}
.window.modal footer {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 32px 48px 48px 48px;
    background-color: #121212;
}
.window header {
    border-bottom: 1px solid #000;
}
#day .grid, #week .grid, #day .events, #week .events, #day .day, #week .day {
    position: relative;
    background-color: #1e1e1e;
    color: #fff;
}
#day h3 .name, #week h3 .name, #schedule h3 .name {
    font-size: 11px;
    line-height: 16px;
    color: #ffffff;
    margin-left: 4px;
}
#day h3.today, #week h3.today, #schedule h3.today {
    border-top: 3px solid #FC6723;
    background-color: #121212;
    color: #fff;
}
#day .grid .hour, #week .grid .hour {
    left: 16px;
    color: #fff;
    font-size: 11px;
    line-height: 16px;
    width: 32px;
    text-align: right;
}
#day .scroll, #week .scroll, #month .scroll, #schedule .scroll {
    flex-grow: 1;
    overflow: auto;
    background-color: #121212;
}
#day .weather, #week .weather, #month .weather, #schedule .weather {
    margin-left: auto;
    font-size: 11px;
    line-height: 16px;
    font-weight: normal;
    color: #fff;
}
#day .grid .now.hour, #week .grid .now.hour {
    color: #FC6723;
    font-weight: bold;
    background-color: #121212;
}
#month .scroll td {
    height: 125px;
    border: 1px solid #000000;
    padding: 0;
    vertical-align: top;
    background-color: #1e1e1e;
}
#day h3, #week h3, #schedule h3 {
    border-top: 3px solid transparent;
    border-bottom: 3px solid transparent;
    height: 32px;
    font-weight: normal;
    display: flex;
    flex-direction: row;
    align-items: center;
    background-color: #121212;
}
#day h3 .number, #week h3 .number, #schedule h3 .number {
    font-size: 16px;
    line-height: 24px;
    color: white;
}
#week header .days {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding-left: 64px;
    background-color: #121212;
}
#week header .row:not(:empty) {
    position: relative;
    height: 16px;
    background-color: #FC6723;
}
#day header .events:not(:empty), #week header .events:not(:empty) {
    border-top: 1px solid #FC6723;
    padding-top: 4px;
    padding-bottom: 4px;
    color: #fff;
    background-color: #FC6723;
}
#sidebar {
    flex: 0 0 258px;
    display: flex;
    flex-direction: column;
    border-right: 1px solid #000000;
    transition: all 300ms;
}
#sidebar footer {
    border-top: 1px solid #000;
    flex-shrink: 0;
    display: flex;
    height: 56px;
    align-items: center;
    justify-content: center;
}
#week .scroll .days .day {
    flex-grow: 1;
    border-right: 1px solid #000;
}
select {
    border-radius: 4px;
    border: 1px solid #000;
    padding: 0 40px 0 7px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background: no-repeat top 50% right 8px / 24px 24px #fff url(data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.or…%200%200%201%201.06-1.06z%22%20fill%3D%22%23828282%22%20%2F%3E%3C%2Fsvg%3E);
}
#schedule.empty {
    background-color: #1e1e1e;
}
#month header {
    display: flex;
    flex-direction: row;
    align-items: center;
    flex-shrink: 0;
    height: 32px;
    background-color: #121212;
}
.month-widget header {
    display: flex;
    flex-direction: row;
    align-items: center;
    background-color: #121212;
}
#day, #week, #month, #year {
    background-color: #1e1e1e;
}
#polls.empty button {
    display: block;
    width: 168px;
    margin: 24px auto;
    font-size: 12px;
    background-color: #FC6723;
    color: white;
}
#month .scroll li.event .title {
    flex-grow: 1;
    color: #fff;
    overflow: hidden;
    text-overflow: ellipsis;
    margin-right: 8px;
}
#month .scroll td.today {
    position: relative;
    background-color: #121212;
    background-clip: padding-box;
}
#schedule .event .title, #schedule .event .location {
    color: #fff;
}
#schedule .more {
    display: block;
    width: 168px;
    margin: 24px auto;
    font-size: 12px;
    background-color: #FC6723;
    color: white;
}
.window.popup header {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 16px;
    background-color: #121212;
}
#editor>ul li {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 400px;
    padding: 16px;
    box-shadow: 0 0 1px 0 rgba(0, 0, 0, 0.2), 0 1px 4px 0 rgba(0, 0, 0, 0.2), 0 16px 24px -16px rgba(0, 0, 0, 0.3);
    background-color: #fff;
    background-color: #cc521b;
}
.window.events-day header+footer {
    border-top: none;
    background-color: #1e1e1e;
    color: #f7f7f7;
}
.window.events-day footer button {
    color: #fff;
}
#editor form input[type=text], #editor form select, #editor form textarea {
    min-width: 0;
    max-width: 400px;
    flex-grow: 1;
    background-color: #121212;
}
#settings>header, .view header, body.nav-active::after {
    z-index: 2;
    background-color: #121212;
}
#settings-view>div {
    padding: 40px 16px;
    background-color: #1e1e1e;
}
#settings-view {
    flex-grow: 1;
    overflow: auto;
    background: #1e1e1e;
}
#settings-sidebar nav {
    flex-grow: 1;
    overflow: auto;
    background-color: #1e1e1e;
}
#settings-nav {
    padding: 32px 16px 32px 24px;
    color: #fff;
}
#settings-sidebar footer button {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 100%;
    height: 56px;
    font-size: 16px;
    line-height: 24px;
    padding-right: 36px;
    color: #fff;
    background-color: #FC6723;
}
#settings-sidebar footer {
    border-top: 1px solid #000;
}
#settings-nav .item.active {
    font-weight: bold;
    color: #fff;
    background-color: #1e1e1e;
}
.settings-form label.inset input, .settings-form label.inset select {
    width: 100%;
    height: 48px;
    padding-top: 20px;
    background-color: #121212;
}
.settings-form .file-input:not(:focus) {
    box-shadow: none;
    border: 1px solid #000;
}
.settings-form input[type=text]:not(:focus) {
    box-shadow: none;
    border: 1px solid #000;
}


/* Anketa */

.poll header {
    display: flex;
    flex-direction: row;
    align-items: center;
    flex-wrap: wrap;
    padding: 16px;
    background-color: #121212;
}

.poll header .term-range {
    margin-right: 0;
    color: white;
}

.poll footer {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 16px;
    background-color: #121212;
}

.poll table td:not(.has-input) {
    padding: 13px 16px;
    background-color: #1e1e1e;
}

.poll table td {
    border: 1px solid #000;
    padding: 0;
}

.poll table td.ok {
    background-color: #00230c;
}

.poll table td.ko {
    background-color: #3a0000;
}

.poll table label {
    display: block;
    padding: 13px 16px;
    background-color: #121212;
}

  }
