css:
@-moz-document domain("superuser.com") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #0077CC #1c1c1c;
}
.site-header--container img{
    filter: invert(100%);
}
.nav-links--link{
    color: #fff !important;
}
.youarehere a{
    color: #0077CC !important;
}
#content{
    background: #1c1c1c;
    border: 1px solid #0077CC;
}
.grid h1{
    color: #0077CC;
}
#hot-network-questions h4 a{
    color: #0077CC !important;
}
.post-tag{
    background: #1c1c1c;
    border-color: #0077CC;
}
.s-btn__muted.s-btn__outlined{
    border-color: #0077CC;
}
.bg-black-050{
    background: #1c1c1c !important;
    border: 1px solid #0077CC;
}
.narrow .mini-counts{
    color: #0077CC;
}
.site-footer .-link,.site-footer--copyright p{
    color: #fff;
}
/*POST*/
#question-header .question-hyperlink{
    color: #0077CC;
}
.ws-nowrap{
    color: #fff;
}
.post-text{
    color: #fff;
}
.comment-copy{
    color: #fff;
}
.answers-subheader h2{
    color: #0077CC;
}
.new-contributor-indicator{
    background: #0077CC;
}
.new-contributor-indicator span{
    color: #fff;
}
.js-vote-count{
    color: #0077CC !important;
}
.js-add-answer-component h2{
    color: #0077CC;
}
.new-login-left h3,.new-login-right h3,.new-login-right label,.new-login-right p{
    color: #fff;
}
.privacy-policy-agreement{
    color: #fff;
}
.bottom-notice{
    color: #fff;
}
.s-sidebarwidget{
    border: none;
}
code{
    color: purple;
    background: transparent
}
pre{
    background: #1c1c1c;
    border: 1px solid #0077CC;
}
#tabs a{
    background: #1c1c1c;
}
#tabs a:hover{
    background: #0077CC;
    color: #1c1c1c;
}
#tabs a.youarehere{
    background: #0077CC;
}
#tabs a.youarehere:hover{
    background: #1c1c1c;
    color: #0077CC;
}
.tag-popup .-container{
    background: #1c1c1c;
}
.s-sidebarwidget__yellow{
    background: #1c1c1c;
    border-color: #0077CC;
}
.s-sidebarwidget::before{
    display: none;
}
.s-sidebarwidget--header .s-sidebarwidget__small-bold-text{
    border-color: #0077CC;
}
.s-sidebarwidget__yellow .s-sidebarwidget--header,.s-anchors.s-anchors__grayscale a:not(.s-link),.s-input{
    background: #1c1c1c;
    color: #fff !important;
}
.s-sidebarwidget--header, .s-sidebarwidget--content{
    border:1px solid #0077CC !important;
}
#wmd-bold-button > span{
    background-position: 0px -40px !important;
}
#wmd-italic-button > span{
    background-position: -20px -40px !important;
}
#wmd-spacer1-button > span{
    background-position: 0px -40px !important;
}
#wmd-link-button > span{
    background-position: -40px -40px !important;
}
#wmd-quote-button > span{
    background-position: -60px -40px !important;
}
#wmd-code-button > span{
    background-position: -80px -40px !important;
}
#wmd-image-button > span{
    background-position: -100px -40px !important;
}
#wmd-spacer2-button > span{
    background-position: 0px -40px !important;
}
#wmd-olist-button > span{
    background-position: -120px -40px !important;
}
#wmd-ulist-button > span{
    background-position: -140px -40px !important;
}
#wmd-heading-button > span{
    background-position: -160px -40px !important;
}
#wmd-hr-button > span{
    background-position: -180px -40px !important;
}
#wmd-spacer3-button > span{
    background-position: 0px -40px !important;
}
#wmd-undo-button > span{
    background-position: -200px -40px !important;
}
#wmd-redo-button > span{
    background-position: -220px -40px !important;
}
#wmd-help-button > span{
    background-position: -240px -40px !important;
}
blockquote{
    background: transparent;
}
.wmd-button-bar{
    border-color: #0077CC;
}
.wmd-button-row{
    border-bottom: 1px solid #0077CC;
}
.mdhelp{
    border-left: 1px solid #0077CC;
    border-right: 1px solid #0077CC;
}
.mdhelp-tabs{
    background: #1c1c1c !important;
}
.mdhelp-tabs li{
    color: #fff;
}
.wmd-help-button.active-help{
    background: transparent;
}
.grippie{
    background-color: #0077CC;
    border: 1px solid #0077CC;
}
.s-notice__warning{
    background: transparent;
    border-color: #0077CC;
}
.s-notice__warning{
    color: #fff;
}
.fc-dark{
    color: #fff !important;
}
.ps-relative textarea{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #0077CC;
}
.s-btn__muted.s-btn__outlined{
    color: #fff;
}
/*POST*/
/*TAGS*/
#mainbar-full h1{
    color: #0077CC;
}
#mainbar-full p{
    color: #fff;
}
.s-btn__muted.s-btn__outlined.is-selected{
    background: #0077CC;
    color: #fff;
    border-color: #0077CC;
}
.s-input.s-input__search{
    background: #1c1c1c;
    color: #fff;
    border-color: #0077CC;
}
.s-input.s-input__search::placeholder{
    color: #fff;
}
#content div h1{
    color: #0077CC;
}
#content div p{
    color: #fff;
}
#synonyms-table td,#synonyms-table td span{
    color: #fff;
}
.page-numbers{
    border-color: #0077CC;
}
/*TAGS*/
/*UNANSWERED*/
#mainbar > div > div{
    color: #fff;
}
.vote span,.status strong{
    color: #0077CC;
}
.excerpt{
    color: #fff;
}
#sidebar h4{
    color: #0077CC;
}
/*UNANSWERED*/
/*HEADER*/
.top-bar .-secondary .-item .-link.topbar-icon-on, .top-bar .-secondary .-item .-link.topbar-icon-on:hover{
    background: #0077CC;
}
.topbar-dialog{
    border-left-color: #0077CC;
    border-right-color: #0077CC;
    border-bottom-color: #0077CC;
}
.topbar-dialog .header{
    background: #0077CC;
}.topbar-dialog .header h3{
    color: #1c1c1c;
}
.topbar-dialog .modal-content{
    background: #1c1c1c;
    color: #fff;
    margin: 0;
}
.huge-button-container a{
    color: #fff !important;
}
.topbar-dialog a:not(.btn-secondary):not(.nav-links--link):hover{
    background: #0077CC;
    color: #1c1c1c;
}
.topbar-dialog.help-dialog .item-summary{
    color: #fff;
}
.topbar-dialog.siteSwitcher-dialog{
    scrollbar-color: #0077CC #1c1c1c;
}
.topbar-dialog ul{
    background: #1c1c1c;
}
.topbar-dialog ul li:hover{
    background: #0077CC !important;
}
.topbar-dialog ul li:hover a span{
    color: #1c1c1c;
}
.topbar-dialog ul li:hover .js-gps-track{
    color: #1c1c1c;
}
.site-filter-container input{
    background: #1c1c1c !important;
    color: #fff;
}
.site-filter-container input::placeholder{
    color: #fff;
}
/*HEADER*/
/*LOGIN*/
.grid--cell .wmx3{
    color: #fff;
}
.bg-white{
    background: #1c1c1c !important;
    border: 1px solid #0077CC;
}
.grid label{
    color: #fff;
}
/*LOGIN*/
/*FOOTER*/
.site-footer--copyright:before{
    content: "Dark Theme by Maciejozaman";
    color: #0077CC;
    font-size: 13px;
}
.site-footer--copyright p a{
    color: #0077CC;
}
.site-footer .-link._expandable::before{
    background: #0077CC;
    opacity: 1;
}
.site-footer .-link._expandable::after{
    border-color: #1c1c1c;
}


/*FOOTER*/
}
