css:
@-moz-document domain("youtube.com") {
ytd-rich-grid-renderer > div#contents.style-scope.ytd-rich-grid-renderer {
    padding-left:0px;
    max-width: calc(100% - 30px);
}

div#grid-header.style-scope.ytd-rich-grid-renderer {
    padding-left: 100px;
    max-width: calc(100% - 200px);
    margin-left: 0;
}

h3.style-scope.ytd-rich-grid-video-renderer {
    margin-top: 5px;
}

ytd-rich-item-renderer {
    margin-right: 4px;
    margin-left: 10;
    display: inline-block;
    width: 200px;
    margin-bottom: 14px;
}

a#video-title.yt-simple-endpoint.style-scope.ytd-rich-grid-video-renderer {
    font-size: 1.4rem;
}

div#metadata *{
    font-size: 1.3rem!important;
} 

a#avatar-link {
    display: none;
}
}
