 /* Set Up standard tag format */
.node-tag[title*="@"]
{
    font-weight: normal;
    color: white !important;
    background-color: #666!important;
    border-radius: 10px;
    text-decoration: none;
    padding: 1px 5px 1px 5px;
    font-size: 0.8em;
    font-weight: bolder;
    opacity: 0.4;
    font-size:70%;
    word-wrap: normal;
    white-space: nowrap;
    vertical-align: middle;
}
.node-tag[title*="#"]
{
    color: black !important;
    background-color: #ccc!important;
    border-radius: 10px;
    text-decoration: none;
    padding: 1px 5px 1px 5px;
    font-size: 0.8em;
    font-weight: bold;
    opacity: 0.4;
    font-size:70%;
    word-wrap: normal;
    white-space: nowrap;
    vertical-align: middle;
}

/* Set up colored tags */
.node-tag[title*="@read"]
{
    display: none;
}

.node-tag[title*="@time"]
{
    color: white !important;
    background-color: var(--pink) !important;
    opacity: 1;
}

.node-tag[title*="@est"]
{
    color: white !important;
    background-color: var(--purple) !important;
    opacity: 1;
}

.node-tag[title*="@waiting"]
{
    color: white !important;
    background-color: var(--blue) !important;
    opacity: 1;
}

.node-tag[title*="@inProgress"]
{
    color: white !important;
    background-color: var(--pink) !important;
    opacity: 1;
}

.node-tag[title*="@dnf"]
{
    color: white !important;
    opacity: .20;
}

.node-tag[title*="#ðŸ’¸"]
{
    color: black !important;
    background-color: var(--green) !important;
    opacity: 1;
}

.node-tag[title*="#â­"]
{
    color: black !important;
    background-color: var(--yellow) !important;
    opacity: 1;
}

.node-tag[title*="#ðŸ”¥"]
{
    color: black !important;
    background-color: var(--orange) !important;
    opacity: 1;
}

.node-tag[title*="#5/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: 1;
}

.node-tag[title*="#4/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: .80;
    filter: contrast(90%);
}

.node-tag[title*="#3/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: .60;
    filter: contrast(80%);
}

.node-tag[title*="#2/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: .40;
    filter: contrast(70%);
}

.node-tag[title*="#1/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: .20;
    filter: contrast(60%);
}

.node-tag[title*="#?/5"]
{
    color: white !important;
    background-color: black !important;
    opacity: .0;
    filter: contrast(60%);
}

