     max-width: 70%;

}

/* disable note space & icon */
.Node-openNote {
    display: none !important;
}

/* font */
.u-use-pref-font {
    /*
    font-family: 'Montserrat', sans-serif;
    letter-spacing: -.02em;
    */
}

/* font size */
.is-largeFont .Node-contentContainer {
    font-size: 21px;
}

/* always hide scrollbars but allow scrolling */
html {
    overflow: scroll;
    overflow-x: hidden;
}
::-webkit-scrollbar {
    width: 0px;  /* Remove scrollbar space */
    height: 0px;
    background: transparent;  /* Optional: just make scrollbar invisible */
}


/* layout tweaks */
.DocumentItem {
margin: 1px 0;
}

.FolderItem-header {
padding-left: 30px;
}

.FolderItem .DocumentItem .DocumentItem-header {
padding-left: 30px;
}

.DocumentItem-title,
.FolderItem-title {
margin-left: 0;
}

.DocumentItem-icon,
.FolderItem-icon {
display: none;
}


.AppHeader {
    background-color: var(--transparent);
    box-shadow: none;
}

.LeftPaneSplitter {
    display: none;
}
.is-desktop .Pane-header,
.is-desktop .popup-message-banner {
    background-color: rgba(0, 0, 0, 0);
}

.is-desktop .LeftPaneContainer-navItem {
    background: rgba(0, 0, 0, 0);
    color: rgba(0, 0, 0, 0);
}

.is-desktop .LeftPaneContainer-navItem.is-active, .is-desktop .LeftPaneContainer-navItem:hover{
    color: #4e4e4e
}

.is-desktop .LeftPaneSlidebarContainer {
    box-shadow: none;
}

.is-desktop .LeftPaneContainer-nav {
    background-color: rgba(0, 0, 0, 0);
    box-shadow: none;
    visibility: hidden;
}

.Pane-header {
    visibility: hidden;
}

.is-compactDensity.is-article-view .Node-self {
    padding-top: 5px;
    padding-bottom: 5px;
}

.is-desktop .main-container {
    margin-top: -32px;
}

.is-desktop .DocumentTools {
    margin-top: 32px;
}

.DocumentTools-overlay {
    background-color: rgba(0, 0, 0, 0);
    box-shadow: none;
}

.DocumentTools-icon{
}

.DocumentContainer {
    height: calc(100% + 32px) !important;
}

.AppHeader-icon {
}

.AppHeader-icon:hover {
    border: none;
    box-shadow: none;
}

.header-sync-state.mod-synced,
.header-sync-state.mod-saved,
.header-sync-state.mod-sync-now,
.header-sync-state.mod-syncing {
