css:
@-moz-document domain("trends.gab.com") {
:root {
	--bg-md: #18242b;
	--bg-md-2: #152025;
	--bg-lt: #213038;
	--bg-dk: #0f171b;
	--bg-primary: #10364a;
	--text-md: #949cb7;
	--text-lt: #cbd3f1;
	--text-dk: #6b7080;
	--text-primary: #2285c3;
}
body.hydra {
	color: var(--text-md) !important;
	background-color: var(--bg-dk) !important;
}
a,
.hydra .page-stats .stat-container .stat-value {
	color: var(--text-primary) !important;
}
a:hover {
	color: var(--text-primary) !important;
	text-decoration: underline !important;
}
.pagination .page-link,
.btn {
	background-image: none !important;
}
.page-link:hover {
	background-color: var(--bg-lt) !important;
}
.page-link,
.bg-dark .dropdown-menu,
input {
	background-color: var(--bg-md) !important;
}
.card,
.page-item.disabled .page-link,
.list-group-item {
	background-color: var(--bg-md-2) !important;
}
.bg-info,
.btn-info,
.bg-dark,
.page-item.active .page-link {
	background-color: var(--bg-primary) !important;
}
.page-link,
.page-link:hover,
.hydra .start-trend-container form.start-trend-form input[type=url].trend-url-input,
input,
.nav-link,
.dropdown-item,
.nav-link:hover,
.dropdown-item:hover,
.text-light,
.btn,
.btn:hover {
	color: var(--text-lt) !important;
}
.page-item.disabled .page-link,
.text-muted {
	color: var(--text-dk) !important;
}
.border,
.hydra .start-trend-container form.start-trend-form input[type=url].trend-url-input,
input,
.hydra .trend-column,
.column-item:hover,
.page-link {
	border-color: var(--bg-md) !important;
}
.hydra .start-trend-container form.start-trend-form input[type=url].trend-url-input,
input,
.list-group-item {
	border-color: var(--bg-lt) !important;
}
.hydra .page-stats .stat-container,
.hydra .poll-answer .answer-chart {
	border-color: var(--bg-md) !important;
}
.hydra .column-item {
	border-bottom: 5px solid var(--bg-md) !important;
}
.hydra .column-item {
	overflow: hidden;
	padding: 1rem;
	background-color: var(--bg-dk) !important;
}
.hydra .column-title {
	margin: 0 !important;
}
.hydra .column-link,
.hydra .rss-item-headline {
	font-weight: 400 !important;
}
.hydra img.column-image {
	margin: 0 1rem 1rem 0 !important;
	float: left !important;
	clear: both;
}
.column-item a {
	display: block !important;
}
.btn-warning,
.hydra .bitcoin-button {
	background-color: #442f0d !important;
	border-color: #442f0d !important;
}
.btn-success {
	background-color: #123c21 !important;
	border-color: #123c21 !important;
}
}
