css:
@-moz-document domain("hup.hu") {
html {
	font-size: 16px;
	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif;
}

.node--type-article[about^="/promo"] {
	display: none;
}

article.node .flag-bookmark-node.action-flag a,
article.node .flag-bookmark-node.action-unflag a {
	min-height: 1rem;
	min-width: 1rem;
	height: 1rem;
	width: 1rem;
	line-height: 1rem;
	background-size: 1rem;
}

blockquote {
	line-height: 1.3rem;
	font-size: 1rem;
	margin-left: 2rem;
	quotes: '\201E' '\201D';
}

blockquote::before {
	left: -2rem;
	content: open-quote;
	clip: unset;
	top: -1.5rem;
}

.region-sidebar-first,
.region-sidebar-second {
	display: flex;
	flex-direction: column;
}

.block {
	order: 10;
}

#block-hup-theme-account-menu {
	order: 1;
}

#block-views-block-blog-blog-block {
	order: 2;
}

#block-allasajanlatok,
#block-hirolvasocsatorna,
#block-huphirlevelblokk,
#block-hupkeresesblokk,
#block-huptweetjei,
#block-informacio,
#block-ittlakikahup,
#block-kovessminket,
#block-partnerunk,
#block-views-block-comments-recent-block-1,
#block-views-block-who-s-new-block-1,
#block-views-block-who-s-online-who-s-online-block {
	display: none;
}

.indented {
	margin-left: 15px;
}

.indented .indented .indented .indented .indented .indented {
	margin-left: 10px;
}

.indented .indented .indented .indented .indented .indented .indented .indented .indented .indented .indented .indented {
	margin-left: 5px;
}
}
