css:
@-moz-document domain("uesp.net") {
#content.mw-body
{
    font-size: 1rem;
}

#mw-content-text
{
    overflow-x: auto;
}

@media only screen and (max-width: 1549px) {
    #column-one > #p-cactions.portlet[role="navigation"]
    {
        left: initial;
        right: calc(50vw - 600px - 1vw);
        top: 23px
    }
}

@media only screen and (min-width: 1550px) {
    #content.mw-body
    {
        max-width: 1100px !important;
        margin: auto;
        margin-top: 40px;
    }

    #p-cactions
    {
        position: fixed;
        left: calc(50vw - 500px - 80px);
        top: 18px
    }
    
    .portlet
    , #p-logo
    {
        position: relative;
        left: calc(50vw - 500px - 250px);
        top: -156px;
    }
}
}
