css:
@-moz-document domain("teamquest.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #C9302C #1c1c1c;
}
.header-job{
    background: #1c1c1c;
    margin: 0;
}
.header-job .container h5, .header-job .container .htitle{
    color: #fff;
}
.navbar-brand > img{
    background: #fff;
}
.navbar-default{
    background: #1c1c1c;
}
.navbar-default .navbar-nav > li > a:hover{
    color: #fff;
}
.panel{
    background: transparent;
}
.dropdown-menu{
    background: #1c1c1c;
}
.dropdown-menu li a{
    color: #fff;
}
.topScroll .slogan{
    text-shadow: none;
    color: #C12E2A;
}
.topScroll .promo{
    background: #00000094;
}
.topScroll .header-slogan-position .promo-slogan{
    background: #00000094;
}
.topScroll .header-slogan-position h1{
    color: #C12E2A;
    text-shadow: none;
}
article.container,.article.container{
    background: transparent;
}
.article.container h1{
    color: #fff;
}
.text-justify{
    color: #fff;
}
.row{
    color: #fff;
}
article.container h3{
    color: #fff;
}
.margin-10-0 li{
    color: #fff;
}
.breadcrumbs{
    color: #fff;
}
.partners-list img{
    background: #fff;
}
.description{
    color: #fff;
}
.section-panel-box h2{
    color: #fff;
}
.joborders h2,.joborders h3{
    color: #fff;
}
.job-details-skills-btn a,.job-skills-btn a{
    color: #fff;
}
.workplaces__block ul li{
    color: #fff;
}
.text-center{
    color: #fff;
}
/*BLOG*/
.print_page__blog{
    color: #fff;
}
.blog-section.blog-view .subline__author{
    color: #fff;
}
.blog.workplaces{
    background: #1c1c1c;
}
/*BLOG*/
/*KONTAKT*/
article.container h1.top__title{
    color: #fff;
}
legend{
    color: #fff;
}
/*KONTAKT*/
/*FOOTER*/
footer .nav-footer li a{
    color: #fff;
}
footer .logo-alpha{
    color: #fff;
}
footer .logo-alpha:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #C12E2A;
}
/*FOOTER*/
}
