css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru")
{

	html, 
	body, 
	.code, 
	.tracker-item
	{
	    background: #f6f5f4;
	}

	.boxlet, 
	#whois_userpic, 
	.infoblock, 
	.page-number, 
	.messages .msg
	{
	    background: #ffffff;
	}


	a,
	#navPath a:visited, 
	#loginGreating a, 
	#bd header a:visited, 
	#bd a.navLink:visited, 
	#bd .nav a:visited, 
	#bd .nav li a:hover, 
	#bd .btn:hover, 
	.icon-reply-color
	{
	    color: #1c71d8;
	}
	#loginGreating a
	{
	    color: #1c71d8 !important;
	}

	html, 
	body, 
	#sitetitle, 
	#bd .forum table tbody td a,
	#bd .forum table tbody td a:visited,
	#bd .forum table thead, 
	#bd .forum table tfoot, 
	.message-table .tag, 
	#bd .nav li a, 
	#bd .btn-default, 
	.tracker-item .tracker-tags .tag,
	#bd .forum table thead a, #bd .forum table tfoot a,
	pre code
	{
	    color: #171a1b;
	}

	.message-table a.secondary,
	#bd .btn, 
	#bd .btn:hover
	{
	    color: #171a1b !important;
	}

	#bd h1,
	#bd h2,
	#bd h1 a,
	#bd header h1 a,
	#bd h2 a,
	#bd h1 a:visited,
	#bd header h1 a:visited,
	#bd h2 a:visited
	{
	    color: #555a5b;
	}

	#bd .forum table th,
	#bd .forum table tbody td,
	.tracker-item, .tracker-item:last-of-type,
	blockquote
	{
	    border-color: #bfb8b1;
	}

	#bd .forum table thead,
	#bd .forum table tfoot,
	.message-table .tag,
	#bd .nav li a,
	#bd .btn-default,
	.tracker-item .tracker-tags .tag
	{
	    background-color: #bfb8b1;
	}

	#bd .forum table tbody tr:hover,
	.tracker-item:hover,
	#bd .forum table tbody tr:hover a
	{
	    background-color: #3584e4 !important;
	    color: #f6f5f4 !important;
	}

	#bd .btn:hover {
	    box-shadow: 0 0 1px 1px #cdc7c2;
	}

	#bd .btn-selected {
	    
	    background-color: #e8e8e7;
	}

	.btn
	{
	    border-color: transparent !important;
	}

	#bd .btn-danger
	{
	    background-color: #e01b24;
	}

	#bd .btn-danger,
	#bd .btn-primary
	{
	color: #f6f5f4 !important;
	}

	.popover .wrap
	{
	    background: #666666;
	}

	.stars, 
	#bd .sign a, 
	#bd .sign a:visited, 
	.icon-user-color
	{
	    color: #26a269;
	}

	.cal-heatmap-container .q5 
	{
	    background-color: #26a269;
	    fill: #26a269;
	}

	#bd .tags a, 
	#bd .tags a:visited, 
	#bd a.tag, 
	#bd a.tag.tag:visited, 
	.icon-tag-color,
	.fav-buttons .selected [class^="icon-"]
	{
	    color: #e5a50a;
	}

	#hd .menu a, .icon-pin-color
	{
	    color: #865e3c;
	}

	.group-label 
	{
	    background-color: #865e3c;
	}


	.penguin_progress span
	{
	    background-color: #1c71d8 !important;
	}



	@media (prefers-color-scheme: dark) {
	    html, 
	    body, 
	    .code, 
	    .tracker-item
	    {
		background: #353535;
	    }

	    .boxlet, 
	    #whois_userpic, 
	    .infoblock, 
	    .page-number, 
	    .messages .msg
	    {
		background: #2d2d2d;
	    }
	    
	    a,
	    #navPath a:visited, 
	    #loginGreating a, 
	    #bd header a:visited, 
	    #bd a.navLink:visited, 
	    #bd .nav a:visited, 
	    #bd .nav li a:hover, 
	    #bd .btn:hover, 
	    .icon-reply-color
	    {
		color: #62a0ea;
	    }
	    #loginGreating a
	    {
		color: #62a0ea !important;
	    }
	    
	    html, 
	    body, 
	    #sitetitle, 
	    #bd .forum table tbody td a,
	    #bd .forum table tbody td a:visited,
	    #bd .forum table thead, 
	    #bd .forum table tfoot, 
	    .message-table .tag, 
	    #bd .nav li a, 
	    #bd .btn-default, 
	    .tracker-item .tracker-tags .tag,
	    #bd .forum table thead a, #bd .forum table tfoot a
	    {
		color: #eeeeec;
	    }

	    .message-table a.secondary,
	    #bd .btn, 
	    #bd .btn:hover
	    {
		color: #eeeeec !important;
	    }

	    #bd h1, 
	    #bd h2, 
	    #bd h1 a, 
	    #bd header h1 a, 
	    #bd h2 a, 
	    #bd h1 a:visited, 
	    #bd header h1 a:visited, 
	    #bd h2 a:visited
	    {
		color: #d7d7d7;
	    }

	    #bd .forum table th, 
	    #bd .forum table tbody td, 
	    .tracker-item, 
	    .tracker-item:last-of-type, 
	    blockquote
	    {
		border-color: #555;
	    }
	    #bd .forum table thead, 
	    #bd .forum table tfoot, 
	    .message-table .tag, 
	    #bd .nav li a, 
	    #bd .btn-default, 
	    .tracker-item .tracker-tags .tag
	    {
		background-color: #555;
	    }
	    
	    #bd .forum table tbody tr:hover,
	    .tracker-item:hover,
	    #bd .forum table tbody tr:hover a
	    {
		background-color: #15539e !important;
		color: #f6f5f4 !important;
	    }

	    #bd .btn:hover {
		box-shadow: 0 0 1px 1px #555;
	    }
	    
	    #bd .btn-selected {

		background-color: #2d2d2d;
	    }

	    #bd .btn-danger
	    {
		background-color: #e01b24;
	    }

	    .stars, 
	    #bd .sign a, 
	    #bd .sign a:visited, 
	    .icon-user-color
	    {
		color: #57e389;
	    }
	    .cal-heatmap-container .q5 
	    {
		background-color: #57e389;
		fill: #57e389;
	    }

	    #bd .tags a, 
	    #bd .tags a:visited, 
	    #bd a.tag, 
	    #bd a.tag.tag:visited, 
	    .icon-tag-color,
	    .fav-buttons .selected [class^="icon-"]
	    {
		color: #ffa348;
	    }
	    #hd .menu a, .icon-pin-color
	    {
		color: #ffbe6f;
	    }


	    .penguin_progress span
	    {
		background-color: #62a0ea !important;
	    }
	}
}
