css:
@-moz-document domain("livegrep.com") {
body {
  background-color: #282828;
  color: #aaa;
}

/**** Query ****/

/*** Left column ***/

#searcharea {
  background-color: #282828;
}

/* 'Query' */
.prefixed-input .prefix-label {
  color: #aaa;
}

/* Dash line */
.prefixed-input input[type="text"] {
  border-bottom-color: #333;
}

.prefixed-input input[type="text"]:valid {
  border-bottom-color: #333;
}

/* 'Special terms' */
.query-hint {
  color: #aaa;
}

.query-hint code {
  background-color: #333;
  border-color: #333;
}

/*** Right column ***/

.btn-default {
  background-color: #282828;
  border-color: #666;
  color: #aaa;
}

.dropdown-menu {
  background-color: #282828;
  border-color: #333;
}

.btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
  background-color: #282828;
  border-color: #333;
}

.btn-default:hover {
  background-color: #333;
  border-color: #666;
}

.btn-default:active:hover, .btn-default.active:hover, .open > .dropdown-toggle.btn-default:hover, .btn-default:active:focus, .btn-default.active:focus, .open > .dropdown-toggle.btn-default:focus, .btn-default:active.focus, .btn-default.active.focus, .open > .dropdown-toggle.btn-default.focus {
  background-color: #333;
  border-color: #666;
}


.bs-searchbox .form-control {
  background-color: #282828;
  border-color: #444;
}

.dropdown-menu > li > a {
  color: #aaa;
}

/**** Results ****/

/* 'Narrow to' */

.file-extensions button {
  background-color: #282828;
  border-color: #333;
}

/** Match **/

.matchstr {
  background-color: #222;
  color: #337ab7; /* from a */
}

/*** Row ***/

.result-path {
  color: #85939e; /* #3d464d; */
}

.result-path .repo, .result-path .version {
  color: #aaa;
}

.file-group {
  background-color: #282828;
  border-color: #333;
  border-left-color: #042a49; /* #A0D1FA; */
}

/** Code Box **/

.match {
  background-color: #222;
}

.match:hover {
  background-color: #282828;
}

.match .contents {
  color: #aaa;
}

/********************* livegrep.com/about *********************/

.textarea {
  border-color: #444;
}
}
