css:
@-moz-document domain("sporcle.com") {
body {background: Black;}

#bg-temp, #suggestions-collapse-bar, .leaderboard_row.leaderboard_stripe, .remodal-wrapper, .tabs, #notifications-dropdown li:hover, #userNav-dropdown h4, #userNav-content a:hover {background: #101010;}

#page-wrapper, #header-inner, .mh-wrapper, #notifications-dropdown, #userNav-dropdown, #streak-dropdown, #days-until-trophy {background:#242424;}

a, span, p, #page-jump, .mainNav.highlight li a, #notifications-dropdown, #userNav-content a {color: #dbdbdb!important;}

.banner-alert-item a, .banner-alert-item, .banner-alert-dismiss, #top-level-dropdown {color:black!important}

#notifications-dropdown a {color: white!important;text-decoration: underline;}

#notifications-dropdown a:hover {text-decoration: none!important;}

.unread {background: #373737!important;}

/*socialBox*/
#tabContent, #snark, #hide-snark-open-confirmation-modal {background: #242424!important; color: #dbdbdb}

#tabContent h4:hover, #tabContent li:hover, #tabList:hover h4  {background: #101010!important;}

/**/

.headerButtons, .on-fire, li a, .suggested-action-cta, .pageButton, .tab-link, #header-streak-count, .randomBadge, .conditionProgress, #nextButton a, #previousButton a, #pauseBox a, .data_header, .hint-num, #nextPage a, #previousPage a {color:#101010!important;}

.suggested-action.accomplished, .playlist-dropdown li:hover, #footerBox li:hover {background:#343434!important;
color: #dbdbdb;}

.dd-title, .current, .badge {color:grey;}

.action-header a, a.flame, .deks, .value, .question, .score-num, .recentlyUpdatedUpdator, .userToFollow a, .unplayed-game-cat a, h1, .headerBoxTitle, #alsoTry, .reckon-score, #snark, .reckon-title, .gameName a, #pl-meta, #sporcle-tv-desription, td, .dividerText, .percent.got_value, .desc, .page-subtitle, h3, #subHead, .plays, ol li a, .blurb, #condition-desc, #pre-badge-text, .groupDesc, .points, #filterBox a, #sortBy, #tab_groups, .number, #quiz-link, .cancel, .question_wrapper, label, h4, .presentationItem, #similar a, #similar, #gameLegend, #extraText, .hintText, .grid-group .hint-num, h5, .answerresults .got_value, .mh-excerpt {color:#dbdbdb!important;}

.gameinfo a, .featured-header, #suggestions-collapse-bar, #game-thumbnails a, .day, .greeting, .borderedList a, .recentlyUpdatedGame, .live5-winners, a.stateful, #tickets-title, .unplayed-game-name, h2, #sporcle-tv-title, .rhcHeader, a.tab, .quizTitle, .selected, .tab.on, ol li.active a, .gtitle, .timerem, .rightBoxTitle, .rightBoxTitle2, .showdown-red span, .buttonGreen span, h3 a, .mh-container-inner a, .AnswerOpen {color:white!important;}

.answers-box, #badgeInfoBox, .groupHeading, .post, .leaderboard_stripe .leaderboard_cell.rank, .leaderboard_stripe .rank, .leaderboard_header, .leaderboard_header .rank, .leaderboard_subtabs_container, #showdownBox, .remodal-is-opened, .answerboxcolumn, .main, #questionEditForm, .ui-draggable-handle, #imgNavBox, .group-name, #pickerBox, #randomBox, #filters ol {background:#101010!important;}

#tab-content, #subTitle, .rank, .buttonShape, #box1, #box2, #box3, #box4, #popBox .rhcHeader, .mh-row {background:#242424!important; color:white;}

#badgeInfo a, .gameName:visited {color: grey!important;}

.subcat-link, .grid-hint.selected, .grid-hint:hover, .gametable-col .d_extra {background: #2e2e2e!important;}

.hint-result span {color: #fff;
    text-shadow: 1px 0 0 #000, 0 -1px 0 #000, 0 1px 0 #000, -1px 0 0 #000;}


/*quiz stuff*/

#mapcanvas .d_value {background: white!important;}

.decoy .text, #reckonBox span {color:white!important; font-style: normal!important;}

#gameHeaderWrapper, .decoy, .current, .quizBox:hover, .ui-sortable-handle:hover, .mh-home {background:#242424!important;}

#questionbox, #answerbox, #reckonBox, #pl-title, .non-current, .you td, .got_value, .playlist-dropdown-header, .up_next, #leftNavHeader {background:#101010!important;}

.borderOff, .decoy {border:none;}

.skip-el, .text, .answer.right .text {color:#000!important;}

.nav-item, .quizBox, .d_name {background:#101010;}

.playlist-dropdown li, .d_value, #topWinners tr, .ui-dialog, .ui-widget-content, #imgbox, #gameBarBox.post, #currQuestionImageWrapper{background:#242424!important;}

.quizUrl:visited {color:grey!important;}

.d_value {color: green!important;}

.d_value.wrong {color: red!important;}

th {background: black!important; color:#dbdbdb!important;}


/*Crossword*/
/*.cell.empty {background: black!important;}*/


/*leftNav*/
#leftNavWrapper, #rightNavWrapper, #gameLists #leftNav, #rightNav {background:#101010;}

#leftNav a, #leftNavHeader, #rightNav a {color: #dbdbdb!important;}

li.on, #rightNav a:hover, #socialRow{background: #242424!important;}
/**/
.Topic .Question, .Topic .Answer, .leaderboard_header, #box1 .text, #box2 .text, #box3 .text, #box4 .text {color:#dbdbdb!important;}

/**/

.buttonOrange, .mainNav.highlight {background:#f90!important; color:White!important;}

.buttonOrange:hover, .buttonShape.showdown-red:hover {background:#ffae00!important;}

.buttonShape.showdown-red {background:#ff5400 !important;}

.buttonGreen {background:#84b623!important;}

.buttonGreen:hover {background:#83c504!important;}

.mh-site-logo, .ui-draggable {background: #575757;}

#instruction-list {background: #242424;}

/*user page*/
.label, #profile-content li a {color: #dbdbdb!important;}

#left-nav li {background: #101010; color: #dbdbdb!important; border: 1px solid white!important;}

#versus-tooltip {background: #101010!important;}

#left-nav li.on {background: #ff5400!important;}

#left-nav li:hover, #left-nav li.on:hover {background: #f90!important}

a.stateful:visited {color: grey!important}
/**/



}
